'use client';

import { useState, useEffect } from 'react';
import { useAuth } from '@clerk/nextjs';
import { useRouter } from 'next/navigation';
import { canAccessFeature, getAvailableFeatures, getUpgradeRequiredFeatures, FeatureKey, UserPlan } from '@/lib/server/canAccessFeature';

interface FeatureFlag {
  key: FeatureKey;
  name: string;
  description: string;
  requiredPlan: UserPlan;
  enabled: boolean;
}

const FEATURE_FLAGS: FeatureFlag[] = [
  {
    key: 'brand_assets_v1',
    name: 'Brand Assets v1',
    description: 'New branding and visual assets',
    requiredPlan: 'free',
    enabled: false,
  },
  {
    key: 'onboarding_tour',
    name: 'Onboarding Tour',
    description: 'Interactive tutorial for new users',
    requiredPlan: 'free',
    enabled: false,
  },
  {
    key: 'consent_optout',
    name: 'Consent Opt-out',
    description: 'Allow users to opt out of data collection',
    requiredPlan: 'free',
    enabled: false,
  },
  {
    key: 'projects_beta',
    name: 'Projects Beta',
    description: 'Project management features',
    requiredPlan: 'pro',
    enabled: false,
  },
  {
    key: 'finance_enabled',
    name: 'Finance Features',
    description: 'Bill management and budget tracking',
    requiredPlan: 'pro',
    enabled: false,
  },
  {
    key: 'notifications_minimal',
    name: 'Minimal Notifications',
    description: 'Basic push notification system',
    requiredPlan: 'pro',
    enabled: false,
  },
  {
    key: 'ask_box_enabled',
    name: 'Ask Box',
    description: 'AI-powered question answering',
    requiredPlan: 'pro',
    enabled: false,
  },
];

export default function FeatureFlagsPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  const [selectedPlan, setSelectedPlan] = useState<UserPlan>('free');
  const [flags, setFlags] = useState<FeatureFlag[]>(FEATURE_FLAGS);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    // Check if user is admin (you might want to add proper admin check)
    // For now, we'll allow any signed-in user to access this page
  }, [isLoaded, isSignedIn, router]);

  const handlePlanChange = (plan: UserPlan) => {
    setSelectedPlan(plan);
    setFlags(prev => prev.map(flag => ({
      ...flag,
      enabled: canAccessFeature(plan, flag.key),
    })));
  };

  const toggleFlag = (key: FeatureKey) => {
    setFlags(prev => prev.map(flag => 
      flag.key === key ? { ...flag, enabled: !flag.enabled } : flag
    ));
  };

  const saveFlags = async () => {
    setLoading(true);
    try {
      // In a real implementation, you'd save these to a database
      // For now, we'll just simulate a save
      await new Promise(resolve => setTimeout(resolve, 1000));
      alert('Feature flags saved successfully!');
    } catch (error) {
      console.error('Error saving flags:', error);
      alert('Failed to save feature flags');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoaded) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!isSignedIn) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white shadow rounded-lg p-6">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Feature Flags Dashboard
            </h1>
            <p className="text-gray-600">
              Manage feature flags and test different plan configurations
            </p>
          </div>

          {/* Plan Selector */}
          <div className="mb-8">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Test Plan</h2>
            <div className="flex space-x-4">
              {(['free', 'pro', 'pro_plus'] as UserPlan[]).map((plan) => (
                <button
                  key={plan}
                  onClick={() => handlePlanChange(plan)}
                  className={`px-4 py-2 rounded-md font-medium transition-colors ${
                    selectedPlan === plan
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {plan === 'pro_plus' ? 'Pro+' : plan === 'pro' ? 'Pro' : 'Free'}
                </button>
              ))}
            </div>
          </div>

          {/* Feature Flags */}
          <div className="mb-8">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Feature Flags</h2>
            <div className="space-y-4">
              {flags.map((flag) => (
                <div key={flag.key} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3">
                        <h3 className="text-lg font-medium text-gray-900">{flag.name}</h3>
                        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                          flag.requiredPlan === 'free' 
                            ? 'bg-green-100 text-green-800'
                            : flag.requiredPlan === 'pro'
                            ? 'bg-blue-100 text-blue-800'
                            : 'bg-purple-100 text-purple-800'
                        }`}>
                          {flag.requiredPlan === 'pro_plus' ? 'Pro+' : flag.requiredPlan === 'pro' ? 'Pro' : 'Free'}
                        </span>
                      </div>
                      <p className="text-sm text-gray-600 mt-1">{flag.description}</p>
                    </div>
                    <div className="flex items-center space-x-3">
                      <span className={`text-sm ${
                        flag.enabled ? 'text-green-600' : 'text-gray-400'
                      }`}>
                        {flag.enabled ? 'Enabled' : 'Disabled'}
                      </span>
                      <button
                        onClick={() => toggleFlag(flag.key)}
                        className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                          flag.enabled ? 'bg-blue-600' : 'bg-gray-200'
                        }`}
                      >
                        <span
                          className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                            flag.enabled ? 'translate-x-6' : 'translate-x-1'
                          }`}
                        />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Save Button */}
          <div className="flex justify-end">
            <button
              onClick={saveFlags}
              disabled={loading}
              className={`px-6 py-2 rounded-md font-medium transition-colors ${
                loading
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-blue-600 text-white hover:bg-blue-700'
              }`}
            >
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>

          {/* Back Button */}
          <div className="mt-8 text-center">
            <button 
              onClick={() => router.push('/dashboard')}
              className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
            >
              Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useAuth } from '@clerk/nextjs';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { SuggestionCard } from '@/components/ai/SuggestionCard';

interface ParsedItem {
  id: string;
  item_type: string;
  confidence_score: number;
  extracted_data: any;
  bill_amount?: number;
  bill_due_date?: string;
  bill_provider?: string;
  bill_category?: string;
  receipt_total?: number;
  receipt_date?: string;
  receipt_store?: string;
  receipt_items?: any[];
  event_title?: string;
  event_date?: string;
  event_location?: string;
  event_description?: string;
  user_confirmed: boolean;
  user_modified: boolean;
  created_at: string;
}

interface AISuggestion {
  id: string;
  suggestion_type: string;
  suggestion_data: any;
  ai_reasoning: string;
  user_feedback: 'accepted' | 'rejected' | 'modified' | 'pending' | 'completed' | 'corrected' | 'ignored';
  user_notes?: string;
  created_at: string;
  parsed_item?: {
    item_type: string;
    confidence_score: number;
    review_status: string;
    review_reason: string;
  };
}

interface EmailQueueEntry {
  id: string;
  email_subject: string;
  email_from: string;
  email_date: string;
  processing_status: 'pending' | 'processing' | 'completed' | 'failed';
  ai_analysis_result?: any;
  error_message?: string;
  created_at: string;
  processed_at?: string;
}

export default function AIEmailDashboard() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  
  const [emailQueue, setEmailQueue] = useState<EmailQueueEntry[]>([]);
  const [parsedItems, setParsedItems] = useState<ParsedItem[]>([]);
  const [pendingSuggestions, setPendingSuggestions] = useState<AISuggestion[]>([]);
  const [processedSuggestions, setProcessedSuggestions] = useState<AISuggestion[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'queue' | 'parsed' | 'suggestions'>('queue');

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    fetchData();
  }, [isLoaded, isSignedIn, router]);

  async function fetchData() {
    try {
      console.log('üîÑ Starting fetchData...');
      setLoading(true);
      
      // Fetch email queue
      const queueResponse = await fetch('/api/ai/email-queue');
      if (queueResponse.ok) {
        const queueData = await queueResponse.json();
        setEmailQueue(queueData.data || []);
      }

      // Fetch parsed items
      const itemsResponse = await fetch('/api/ai/parsed-items');
      if (itemsResponse.ok) {
        const itemsData = await itemsResponse.json();
        setParsedItems(itemsData.data || []);
      }

      // Fetch pending suggestions
      const pendingResponse = await fetch('/api/ai/suggestions?status=pending');
      if (pendingResponse.ok) {
        const pendingData = await pendingResponse.json();
        console.log('üîç Fetched pending suggestions:', pendingData.data);
        setPendingSuggestions(pendingData.data || []);
      }

      // Fetch processed suggestions
      const processedResponse = await fetch('/api/ai/suggestions?status=processed');
      if (processedResponse.ok) {
        const processedData = await processedResponse.json();
        console.log('üîç Fetched processed suggestions:', processedData.data);
        setProcessedSuggestions(processedData.data || []);
      }

      console.log('‚úÖ fetchData completed');

    } catch (error) {
      console.error('Error fetching AI email data:', error);
    } finally {
      setLoading(false);
    }
  }

  function getStatusColor(status: string) {
    switch (status) {
      case 'completed': return 'text-green-600 bg-green-100';
      case 'processing': return 'text-blue-600 bg-blue-100';
      case 'failed': return 'text-red-600 bg-red-100';
      case 'pending': return 'text-yellow-600 bg-yellow-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  }

  function getItemTypeIcon(itemType: string) {
    switch (itemType) {
      case 'bill': return 'üí∞';
      case 'receipt': return 'üßæ';
      case 'event': return 'üìÖ';
      case 'appointment': return 'üè•';
      case 'delivery': return 'üì¶';
      default: return 'üìß';
    }
  }



  function formatDate(dateString: string) {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  function formatCurrency(amount: number) {
    return new Intl.NumberFormat('en-AU', {
      style: 'currency',
      currency: 'AUD'
    }).format(amount);
  }

  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading AI Email Dashboard...</p>
        </div>
      </div>
    );
  }

  if (!isSignedIn) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <button
              onClick={() => router.back()}
              className="text-blue-600 hover:text-blue-700 flex items-center"
            >
              ‚Üê Back
            </button>
            
            <a
              href="/ai-learning-dashboard"
              className="inline-flex items-center px-4 py-2 bg-purple-600 text-white text-sm font-medium rounded-md hover:bg-purple-700 transition-colors"
            >
              üß† View AI Learning Insights
            </a>
          </div>
          
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            ü§ñ AI Email Processing Dashboard
          </h1>
          <p className="text-gray-600">
            Monitor AI-powered email parsing, extracted data, and smart suggestions
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <span className="text-2xl">üìß</span>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Emails</p>
                <p className="text-2xl font-bold text-gray-900">{emailQueue.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <span className="text-2xl">‚úÖ</span>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed</p>
                <p className="text-2xl font-bold text-gray-900">
                  {emailQueue.filter(e => e.processing_status === 'completed').length}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <span className="text-2xl">üîç</span>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Items Extracted</p>
                <p className="text-2xl font-bold text-gray-900">{parsedItems.length}</p>
              </div>
            </div>
          </div>

                     <div className="bg-white rounded-lg shadow p-6">
             <div className="flex items-center">
               <div className="p-2 bg-yellow-100 rounded-lg">
                 <span className="text-2xl">üí°</span>
               </div>
               <div className="ml-4">
                 <p className="text-sm font-medium text-gray-600">AI Suggestions</p>
                 <p className="text-2xl font-bold text-gray-900">{pendingSuggestions.length + processedSuggestions.length}</p>
                 <p className="text-xs text-gray-500">
                   {pendingSuggestions.length} pending
                 </p>
               </div>
             </div>
           </div>
        </div>

        {/* Tab Navigation */}
        <div className="bg-white rounded-lg shadow mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 px-6">
              <button
                onClick={() => setActiveTab('queue')}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'queue'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                Email Queue ({emailQueue.length})
              </button>
              <button
                onClick={() => setActiveTab('parsed')}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'parsed'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                Parsed Items ({parsedItems.length})
              </button>
                             <button
                 onClick={() => setActiveTab('suggestions')}
                 className={`py-4 px-1 border-b-2 font-medium text-sm ${
                   activeTab === 'suggestions'
                     ? 'border-blue-500 text-blue-600'
                     : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                 }`}
               >
                 AI Suggestions ({pendingSuggestions.length + processedSuggestions.length})
                 {pendingSuggestions.length > 0 && (
                   <span className="ml-2 bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-xs">
                     {pendingSuggestions.length} pending
                   </span>
                 )}
               </button>
            </nav>
          </div>

          {/* Tab Content */}
          <div className="p-6">
            {activeTab === 'queue' && (
              <div className="space-y-4">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Email Processing Queue</h3>
                {emailQueue.length === 0 ? (
                  <p className="text-gray-500 text-center py-8">No emails in queue</p>
                ) : (
                  emailQueue.map((email) => (
                    <div key={email.id} className="border rounded-lg p-4 hover:bg-gray-50">
                      <div className="flex items-center justify-between">
                        <div className="flex-1">
                          <h4 className="font-medium text-gray-900">{email.email_subject}</h4>
                          <p className="text-sm text-gray-600">From: {email.email_from}</p>
                          <p className="text-sm text-gray-500">{formatDate(email.email_date)}</p>
                        </div>
                        <div className="flex items-center space-x-3">
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(email.processing_status)}`}>
                            {email.processing_status}
                          </span>
                          {email.error_message && (
                            <span className="text-red-600 text-sm" title={email.error_message}>
                              ‚ö†Ô∏è Error
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}

            {activeTab === 'parsed' && (
              <div className="space-y-4">
                <h3 className="text-lg font-medium text-gray-900 mb-4">AI Extracted Items</h3>
                {parsedItems.length === 0 ? (
                  <p className="text-gray-500 text-center py-8">No items extracted yet</p>
                ) : (
                  parsedItems.map((item) => (
                    <div key={item.id} className="border rounded-lg p-4 hover:bg-gray-50">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center space-x-2 mb-2">
                            <span className="text-2xl">{getItemTypeIcon(item.item_type)}</span>
                            <h4 className="font-medium text-gray-900 capitalize">{item.item_type}</h4>
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                              item.confidence_score >= 0.8 ? 'bg-green-100 text-green-800' :
                              item.confidence_score >= 0.6 ? 'bg-yellow-100 text-yellow-800' :
                              'bg-red-100 text-red-800'
                            }`}>
                              {Math.round(item.confidence_score * 100)}% confidence
                            </span>
                          </div>
                          
                          {/* Display item-specific data */}
                          {item.item_type === 'bill' && (
                            <div className="space-y-1">
                              <p className="text-sm"><strong>Provider:</strong> {item.bill_provider}</p>
                              <p className="text-sm"><strong>Amount:</strong> {item.bill_amount ? formatCurrency(item.bill_amount) : 'N/A'}</p>
                              <p className="text-sm"><strong>Due Date:</strong> {item.bill_due_date || 'N/A'}</p>
                              <p className="text-sm"><strong>Category:</strong> {item.bill_category || 'N/A'}</p>
                            </div>
                          )}
                          
                          {item.item_type === 'receipt' && (
                            <div className="space-y-1">
                              <p className="text-sm"><strong>Store:</strong> {item.receipt_store}</p>
                              <p className="text-sm"><strong>Total:</strong> {item.receipt_total ? formatCurrency(item.receipt_total) : 'N/A'}</p>
                              <p className="text-sm"><strong>Date:</strong> {item.receipt_date || 'N/A'}</p>
                              {item.receipt_items && item.receipt_items.length > 0 && (
                                <p className="text-sm"><strong>Items:</strong> {item.receipt_items.length} items</p>
                              )}
                            </div>
                          )}
                          
                          {item.item_type === 'event' && (
                            <div className="space-y-1">
                              <p className="text-sm"><strong>Title:</strong> {item.event_title}</p>
                              <p className="text-sm"><strong>Date:</strong> {item.event_date || 'N/A'}</p>
                              <p className="text-sm"><strong>Location:</strong> {item.event_location || 'N/A'}</p>
                            </div>
                          )}
                          
                          <p className="text-xs text-gray-500 mt-2">
                            Extracted: {formatDate(item.created_at)}
                          </p>
                        </div>
                        
                        <div className="flex items-center space-x-2">
                          {item.user_confirmed && (
                            <span className="text-green-600 text-sm">‚úì Confirmed</span>
                          )}
                          {item.user_modified && (
                            <span className="text-blue-600 text-sm">‚úèÔ∏è Modified</span>
                          )}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}

                         {activeTab === 'suggestions' && (
               <div className="space-y-4">
                 <h3 className="text-lg font-medium text-gray-900 mb-4">AI Suggestions</h3>
                 {pendingSuggestions.length === 0 && processedSuggestions.length === 0 ? (
                   <p className="text-gray-500 text-center py-8">No AI suggestions yet</p>
                 ) : (
                   <div className="space-y-4">
                     {/* Pending Suggestions */}
                     {pendingSuggestions.length > 0 && (
                       <div>
                         <h4 className="text-md font-medium text-gray-700 mb-3">Pending Actions</h4>
                         <div className="grid gap-4">
                           {pendingSuggestions.map((suggestion) => (
                             <SuggestionCard
                               key={suggestion.id}
                               suggestion={suggestion}
                               onCorrectionSaved={fetchData}
                             />
                           ))}
                         </div>
                       </div>
                     )}

                     {/* Completed/Processed Suggestions */}
                     {processedSuggestions.length > 0 && (
                       <div>
                         <h4 className="text-md font-medium text-gray-700 mb-3">Processed Suggestions</h4>
                         <div className="grid gap-4">
                           {processedSuggestions.map((suggestion) => (
                             <SuggestionCard
                               key={suggestion.id}
                               suggestion={suggestion}
                               onCorrectionSaved={fetchData}
                             />
                           ))}
                         </div>
                       </div>
                     )}
                   </div>
                 )}
               </div>
             )}
          </div>
        </div>

        {/* Action Buttons */}
        <div className="text-center space-x-4">
          <button
            onClick={fetchData}
            className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
          >
            Refresh Data
          </button>
          
          <a
            href="/ai-email-dashboard/test-email"
            className="inline-block bg-green-600 text-white px-6 py-2 rounded-md hover:bg-green-700"
          >
            üß™ Test Email Processing
          </a>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useAuth } from '@clerk/nextjs';
import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';

export default function TestEmailPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  
  const [emailData, setEmailData] = useState({
    subject: '',
    body: '',
    from: '',
    date: new Date().toISOString().split('T')[0]
  });
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }
  }, [isLoaded, isSignedIn, router]);

  async function processTestEmail() {
    try {
      setLoading(true);
      setError(null);
      setResult(null);

      const response = await fetch('/api/ai/process-email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ emailData })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to process email');
      }

      setResult(data);

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error occurred');
    } finally {
      setLoading(false);
    }
  }

  function loadSampleEmail(type: 'bill' | 'receipt' | 'appointment') {
    switch (type) {
      case 'bill':
        setEmailData({
          subject: 'Your Origin Energy Bill - Due 15 Feb 2024',
          body: `Dear Customer,

Your Origin Energy bill is now available.

Account Number: 12345678
Bill Period: 15 Dec 2023 - 15 Jan 2024
Due Date: 15 Feb 2024
Amount Due: $125.50

This bill covers your electricity usage for the period shown above. Please ensure payment is received by the due date to avoid any late payment fees.

You can pay online at originenergy.com.au or call us on 13 24 61.

Thank you for choosing Origin Energy.

Best regards,
Origin Energy Team`,
          from: 'billing@originenergy.com.au',
          date: new Date().toISOString().split('T')[0]
        });
        break;

      case 'receipt':
        setEmailData({
          subject: 'Your Coles Online Order Receipt',
          body: `Thank you for your Coles Online order!

Order Number: CO123456789
Order Date: 15 Jan 2024
Delivery Date: 16 Jan 2024

Items:
- Milk (2L) x2 - $7.00
- Bread (Wholemeal) x1 - $4.20
- Eggs (12 pack) x1 - $6.50
- Bananas (1kg) x1 - $3.80
- Chicken Breast (500g) x1 - $8.50

Subtotal: $30.00
Delivery Fee: $2.00
Total: $32.00

Your order will be delivered between 2:00 PM - 6:00 PM on 16 Jan 2024.

Thank you for shopping with Coles!`,
          from: 'receipts@coles.com.au',
          date: new Date().toISOString().split('T')[0]
        });
        break;

      case 'appointment':
        setEmailData({
          subject: 'Dentist Appointment Confirmation - 20 Feb 2024',
          body: `Dear Patient,

Your dental appointment has been confirmed.

Appointment Details:
Date: 20 Feb 2024
Time: 2:00 PM
Duration: 45 minutes
Type: Regular Checkup & Clean

Location: 123 Dental Clinic
Address: 123 Main Street, Melbourne VIC 3000
Phone: (03) 9123 4567

Please arrive 10 minutes before your appointment time. If you need to reschedule or cancel, please call us at least 24 hours in advance.

What to bring:
- Medicare card
- Any relevant medical history
- List of current medications

We look forward to seeing you!

Best regards,
123 Dental Clinic Team`,
          from: 'appointments@123dental.com.au',
          date: new Date().toISOString().split('T')[0]
        });
        break;
    }
  }

  if (!isLoaded) {
    return <div>Loading...</div>;
  }

  if (!isSignedIn) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => router.back()}
            className="text-blue-600 hover:text-blue-700 mb-4 flex items-center"
          >
            ‚Üê Back to AI Dashboard
          </button>
          
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            üß™ Test AI Email Processing
          </h1>
          <p className="text-gray-600">
            Submit test emails to see how the AI processes and extracts information
          </p>
        </div>

        {/* Sample Email Buttons */}
        <div className="bg-white rounded-lg shadow p-6 mb-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Load Sample Emails</h3>
          <div className="flex space-x-4">
            <button
              onClick={() => loadSampleEmail('bill')}
              className="bg-blue-100 text-blue-700 px-4 py-2 rounded-md hover:bg-blue-200"
            >
              üí∞ Sample Bill
            </button>
            <button
              onClick={() => loadSampleEmail('receipt')}
              className="bg-green-100 text-green-700 px-4 py-2 rounded-md hover:bg-green-200"
            >
              üßæ Sample Receipt
            </button>
            <button
              onClick={() => loadSampleEmail('appointment')}
              className="bg-purple-100 text-purple-700 px-4 py-2 rounded-md hover:bg-purple-200"
            >
              üìÖ Sample Appointment
            </button>
          </div>
        </div>

        {/* Email Form */}
        <div className="bg-white rounded-lg shadow p-6 mb-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Email Data</h3>
          
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Subject
              </label>
              <input
                type="text"
                value={emailData.subject}
                onChange={(e) => setEmailData({ ...emailData, subject: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Email subject..."
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                From
              </label>
              <input
                type="email"
                value={emailData.from}
                onChange={(e) => setEmailData({ ...emailData, from: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="sender@example.com"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Date
              </label>
              <input
                type="date"
                value={emailData.date}
                onChange={(e) => setEmailData({ ...emailData, date: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Body
              </label>
              <textarea
                value={emailData.body}
                onChange={(e) => setEmailData({ ...emailData, body: e.target.value })}
                rows={8}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Email body content..."
              />
            </div>
          </div>

          <div className="mt-6">
            <button
              onClick={processTestEmail}
              disabled={loading || !emailData.subject || !emailData.body}
              className="bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
            >
              {loading ? 'Processing...' : 'üöÄ Process with AI'}
            </button>
          </div>
        </div>

        {/* Results */}
        {result && (
          <div className="bg-white rounded-lg shadow p-6 mb-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">‚úÖ Processing Results</h3>
            
            <div className="space-y-4">
              <div className="bg-green-50 border border-green-200 rounded-md p-4">
                <p className="text-green-800">
                  <strong>Success!</strong> Email processed in {result.data.processingTime}ms
                </p>
                <p className="text-green-700 text-sm mt-1">
                  Queue ID: {result.data.queueId}
                </p>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                  <h4 className="font-medium text-blue-900 mb-2">üìä Parsed Items</h4>
                  <p className="text-blue-800 text-2xl font-bold">{result.data.parsedItems}</p>
                  <p className="text-blue-700 text-sm">Items extracted from email</p>
                </div>

                <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
                  <h4 className="font-medium text-yellow-900 mb-2">üí° AI Suggestions</h4>
                  <p className="text-yellow-800 text-2xl font-bold">{result.data.suggestions}</p>
                  <p className="text-yellow-700 text-sm">Smart suggestions generated</p>
                </div>
              </div>

              <div className="mt-4">
                <a
                  href="/ai-email-dashboard"
                  className="text-blue-600 hover:text-blue-700 underline"
                >
                  View results in AI Dashboard ‚Üí
                </a>
              </div>
            </div>
          </div>
        )}

        {/* Error Display */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-md p-4">
            <h3 className="text-lg font-medium text-red-900 mb-2">‚ùå Error</h3>
            <p className="text-red-800">{error}</p>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useUserData } from '@/hooks/useUserData';
import { 
  Brain, 
  TrendingUp, 
  Target, 
  Lightbulb, 
  BarChart3, 
  CheckCircle,
  AlertCircle,
  Clock
} from 'lucide-react';

interface LearningInsights {
  total_corrections: number;
  patterns_identified: number;
  accuracy_trend: number;
  top_learning_areas: string[];
  suggested_improvements: string[];
  confidence_threshold: number;
  learning_goals: string[];
  last_updated: string;
}

export default function AILearningDashboard() {
  const { userData, isLoading: userDataLoading } = useUserData();
  const [insights, setInsights] = useState<LearningInsights | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (userData?.household_id) {
      fetchLearningInsights();
    }
  }, [userData?.household_id]);

  const fetchLearningInsights = async () => {
    if (!userData?.household_id) {
      setError('No household ID found. Please complete onboarding first.');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      const response = await fetch(`/api/ai/learning-insights?household_id=${userData.household_id}`);
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch learning insights');
      }
      
      const data = await response.json();
      setInsights(data.insights);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  if (userDataLoading || loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Brain className="h-12 w-12 animate-pulse mx-auto mb-4 text-blue-500" />
          <p className="text-lg text-gray-600">
            {userDataLoading ? 'Loading user data...' : 'Loading AI Learning Insights...'}
          </p>
        </div>
      </div>
    );
  }

  if (!userData?.household_id) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <AlertCircle className="h-12 w-12 text-yellow-500 mx-auto mb-4" />
          <p className="text-lg text-yellow-600 mb-4">Household Setup Required</p>
          <p className="text-gray-600 mb-4">
            You need to complete onboarding and join a household to view AI Learning Insights.
          </p>
          <Button onClick={() => window.location.href = '/onboarding'} variant="outline">
            Go to Onboarding
          </Button>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <AlertCircle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <p className="text-lg text-red-600 mb-4">Error loading insights</p>
          <p className="text-gray-600 mb-4">{error}</p>
          <Button onClick={fetchLearningInsights} variant="outline">
            Try Again
          </Button>
        </div>
      </div>
    );
  }

  if (!insights) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Brain className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <p className="text-lg text-gray-600">No learning insights available yet</p>
          <p className="text-gray-500">Start making corrections to see AI learning progress</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-4">
          <Brain className="h-8 w-8 text-blue-500" />
          <h1 className="text-3xl font-bold text-gray-900">AI Learning Dashboard</h1>
        </div>
        <p className="text-gray-600 text-lg">
          See how your AI assistant is learning and improving from your feedback
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Corrections</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{insights.total_corrections}</div>
            <p className="text-xs text-gray-500">User feedback provided</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Patterns Identified</CardTitle>
            <Target className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{insights.patterns_identified}</div>
            <p className="text-xs text-gray-500">AI learning patterns</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Accuracy Trend</CardTitle>
            <TrendingUp className="h-4 w-4 text-purple-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{insights.accuracy_trend}%</div>
            <p className="text-xs text-gray-500">Improvement over time</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Confidence Threshold</CardTitle>
            <BarChart3 className="h-4 w-4 text-orange-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{insights.confidence_threshold}%</div>
            <p className="text-xs text-gray-500">AI confidence level</p>
          </CardContent>
        </Card>
      </div>

      {/* Main Content Tabs */}
      <Tabs defaultValue="overview" className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="learning">Learning Areas</TabsTrigger>
          <TabsTrigger value="improvements">Improvements</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          {/* Learning Progress */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Target className="h-5 w-5" />
                Learning Progress
              </CardTitle>
              <CardDescription>
                How much the AI has learned from your feedback
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between text-sm">
                  <span>Pattern Recognition</span>
                  <span>{Math.min(100, (insights.patterns_identified / Math.max(insights.total_corrections, 1)) * 100)}%</span>
                </div>
                <Progress value={Math.min(100, (insights.patterns_identified / Math.max(insights.total_corrections, 1)) * 100)} />
                
                <div className="flex justify-between text-sm">
                  <span>Accuracy Improvement</span>
                  <span>{insights.accuracy_trend}%</span>
                </div>
                <Progress value={insights.accuracy_trend} />
              </div>
            </CardContent>
          </Card>

          {/* Recent Activity */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="h-5 w-5" />
                Recent Learning Activity
              </CardTitle>
              <CardDescription>
                Last updated: {new Date(insights.last_updated).toLocaleDateString()}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="text-sm text-gray-600">
                The AI system is actively learning from your corrections and improving its suggestions.
                {insights.total_corrections > 0 && (
                  <p className="mt-2">
                    Based on {insights.total_corrections} feedback items, the system has identified 
                    {insights.patterns_identified} learning patterns.
                  </p>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="learning" className="space-y-6">
          {/* Top Learning Areas */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Brain className="h-5 w-5" />
                Top Learning Areas
              </CardTitle>
              <CardDescription>
                What the AI is getting better at understanding
              </CardDescription>
            </CardHeader>
            <CardContent>
              {insights.top_learning_areas.length > 0 ? (
                <div className="space-y-3">
                  {insights.top_learning_areas.map((area, index) => (
                    <div key={index} className="flex items-center gap-3">
                      <Badge variant="secondary" className="w-20 justify-center">
                        #{index + 1}
                      </Badge>
                      <span className="text-sm">{area}</span>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-sm">
                  Learning areas will appear as you provide more feedback
                </p>
              )}
            </CardContent>
          </Card>

          {/* Learning Goals */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Target className="h-5 w-5" />
                Next Learning Goals
              </CardTitle>
              <CardDescription>
                What the AI is working on improving next
              </CardDescription>
            </CardHeader>
            <CardContent>
              {insights.learning_goals.length > 0 ? (
                <div className="space-y-3">
                  {insights.learning_goals.map((goal, index) => (
                    <div key={index} className="flex items-start gap-3">
                      <div className="w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0" />
                      <span className="text-sm">{goal}</span>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-sm">
                  Learning goals will be generated based on your feedback patterns
                </p>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="improvements" className="space-y-6">
          {/* Suggested Improvements */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Lightbulb className="h-5 w-5" />
                Suggested Improvements
              </CardTitle>
              <CardDescription>
                Actionable insights to help the AI work better for you
              </CardDescription>
            </CardHeader>
            <CardContent>
              {insights.suggested_improvements.length > 0 ? (
                <div className="space-y-4">
                  {insights.suggested_improvements.map((improvement, index) => (
                    <div key={index} className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                      <div className="flex items-start gap-3">
                        <Lightbulb className="h-5 w-5 text-blue-500 mt-0.5 flex-shrink-0" />
                        <p className="text-sm text-blue-800">{improvement}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-sm">
                  Improvement suggestions will appear as the AI learns more about your preferences
                </p>
              )}
            </CardContent>
          </Card>

          {/* Action Items */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CheckCircle className="h-5 w-5" />
                How to Help the AI Learn
              </CardTitle>
              <CardDescription>
                Simple actions you can take to improve AI suggestions
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3 text-sm">
                <div className="flex items-start gap-3">
                  <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                  <span>Always provide feedback when AI suggestions are incorrect</span>
                </div>
                <div className="flex items-start gap-3">
                  <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                  <span>Use the "Correct" button to explain what should have been suggested</span>
                </div>
                <div className="flex items-start gap-3">
                  <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                  <span>Add notes to corrections to help the AI understand your preferences</span>
                </div>
                <div className="flex items-start gap-3">
                  <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                  <span>Mark suggestions as "Done" when they're correct to reinforce good patterns</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Refresh Button */}
      <div className="mt-8 text-center">
        <Button onClick={fetchLearningInsights} variant="outline">
          Refresh Insights
        </Button>
      </div>
    </div>
  );
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL as string;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY as string;

// Create a Supabase client with service role key for server-side operations
const supabase = createClient(supabaseUrl, supabaseServiceKey);

export async function POST(request: NextRequest) {
  try {
    // 1. Authenticate the user via Clerk
    const { userId } = await auth();
    
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // 2. Get rewardId from request body
    const body = await request.json();
    const { rewardId } = body;

    if (!rewardId) {
      return NextResponse.json({ error: 'Reward ID is required' }, { status: 400 });
    }

    // 3. Verify the reward exists and get its costs
    const { data: reward, error: rewardError } = await supabase
      .from('rewards')
      .select('*')
      .eq('id', rewardId)
      .single();

    if (rewardError || !reward) {
      return NextResponse.json({ error: 'Reward not found' }, { status: 404 });
    }

    // 4. Fetch the user's current XP and coins
    const { data: user, error: userError } = await supabase
      .from('users')
      .select('xp, coins')
      .eq('clerk_id', userId)
      .single();

    if (userError || !user) {
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    // 5. Check if user has enough resources
    const currentXp = user.xp || 0;
    const currentCoins = user.coins || 0;
    const requiredXp = reward.cost_xp || 0;
    const requiredCoins = reward.cost_coins || 0;

    if (currentXp < requiredXp || currentCoins < requiredCoins) {
      return NextResponse.json({ 
        error: 'Insufficient XP or coins',
        details: {
          current: { xp: currentXp, coins: currentCoins },
          required: { xp: requiredXp, coins: requiredCoins }
        }
      }, { status: 400 });
    }

    // 6. Check if user has already claimed this reward
    const { data: existingClaim, error: claimCheckError } = await supabase
      .from('claimed_rewards')
      .select('*')
      .eq('user_id', userId)
      .eq('reward_id', rewardId)
      .single();

    if (claimCheckError && claimCheckError.code !== 'PGRST116') {
      // PGRST116 is "not found" error, which is expected if no claim exists
      return NextResponse.json({ error: 'Error checking existing claim' }, { status: 500 });
    }

    if (existingClaim) {
      return NextResponse.json({ error: 'Reward already claimed' }, { status: 400 });
    }

    // 7. Deduct resources from user and insert claim in a transaction
    const newXp = currentXp - requiredXp;
    const newCoins = currentCoins - requiredCoins;

    // Update user's resources
    const { error: updateError } = await supabase
      .from('users')
      .update({ 
        xp: newXp,
        coins: newCoins
      })
      .eq('clerk_id', userId);

    if (updateError) {
      console.error('Error updating user resources:', updateError);
      return NextResponse.json({ error: 'Failed to update user resources' }, { status: 500 });
    }

    // 8. Insert new claim into claimed_rewards table
    const { data: newClaim, error: insertError } = await supabase
      .from('claimed_rewards')
      .insert({
        user_id: userId,
        reward_id: rewardId
      })
      .select()
      .single();

    if (insertError) {
      console.error('Error inserting reward claim:', insertError);
      return NextResponse.json({ error: 'Failed to claim reward' }, { status: 500 });
    }

    console.log(`‚úÖ Reward ${rewardId} claimed successfully by user ${userId}. Deducted ${requiredXp} XP and ${requiredCoins} coins.`);

    // 9. Return success response
    return NextResponse.json({ 
      success: true, 
      data: {
        claim: newClaim,
        reward,
        resourcesDeducted: {
          xp: requiredXp,
          coins: requiredCoins
        },
        newBalance: {
          xp: newXp,
          coins: newCoins
        }
      }
    });

  } catch (error) {
    console.error('Exception in POST /api/claim-reward:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { getAuth } from '@clerk/nextjs/server';
import { z } from 'zod';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseKey);

const GetConflictsSchema = z.object({
  household_id: z.string().uuid(),
});

export async function GET(request: NextRequest) {
  try {
    const { userId } = await getAuth(request);
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const householdId = searchParams.get('household_id');

    if (!householdId) {
      return NextResponse.json({ error: 'household_id is required' }, { status: 400 });
    }

    // Verify user has access to this household
    const { data: membership, error: membershipError } = await supabase
      .from('household_members')
      .select('household_id')
      .eq('household_id', householdId)
      .eq('user_id', userId)
      .single();

    if (membershipError || !membership) {
      return NextResponse.json({ error: 'Household not found or access denied' }, { status: 404 });
    }

    // First, run conflict detection to ensure we have current conflicts
    const { error: detectionError } = await supabase
      .rpc('upsert_calendar_conflicts', { p_household_id: householdId });

    if (detectionError) {
      console.error('Error running conflict detection:', detectionError);
      // Continue anyway - maybe there are existing conflicts
    }

    // Get conflicts for the household with event details
    const { data: conflicts, error: conflictsError } = await supabase
      .from('calendar_conflicts')
      .select('id, household_id, event1_id, event2_id, conflict_type, severity, is_resolved, created_at, resolved_at, resolved_by')
      .eq('household_id', householdId)
      .eq('is_resolved', false)
      .order('created_at', { ascending: false });

    if (conflictsError) {
      console.error('Error fetching conflicts:', conflictsError);
      return NextResponse.json({ error: 'Failed to fetch conflicts' }, { status: 500 });
    }

    const eventIds = Array.from(new Set(
      (conflicts || []).flatMap(conflict => [conflict.event1_id, conflict.event2_id]).filter(Boolean)
    ));

    const eventMap = new Map<string, { id: string; title: string | null; start_at: string | null; end_at: string | null; attendee_user_id: string | null }>();

    if (eventIds.length > 0) {
      const { data: events, error: eventsError } = await supabase
        .from('events')
        .select('id, title, start_at, end_at, attendee_user_id')
        .in('id', eventIds);

      if (eventsError) {
        console.error('Error fetching conflict events:', eventsError);
        return NextResponse.json({ error: 'Failed to fetch conflicts' }, { status: 500 });
      }

      events?.forEach(event => {
        eventMap.set(event.id, {
          id: event.id,
          title: event.title ?? null,
          start_at: event.start_at ?? null,
          end_at: event.end_at ?? null,
          attendee_user_id: event.attendee_user_id ?? null,
        });
      });
    }

    // Get conflict statistics
    const { data: allConflicts, error: statsError } = await supabase
      .from('calendar_conflicts')
      .select('conflict_type, severity, is_resolved')
      .eq('household_id', householdId);

    if (statsError) {
      console.error('Error fetching conflict stats:', statsError);
      return NextResponse.json({ error: 'Failed to fetch conflict statistics' }, { status: 500 });
    }

    // Calculate statistics
    const totalConflicts = allConflicts?.length || 0;
    const unresolvedConflicts = allConflicts?.filter(c => !c.is_resolved).length || 0;
    const resolvedConflicts = allConflicts?.filter(c => c.is_resolved).length || 0;

    const conflictsByType = allConflicts?.reduce((acc, conflict) => {
      acc[conflict.conflict_type] = (acc[conflict.conflict_type] || 0) + 1;
      return acc;
    }, {} as Record<string, number>) || {};

    const conflictsBySeverity = allConflicts?.reduce((acc, conflict) => {
      acc[conflict.severity] = (acc[conflict.severity] || 0) + 1;
      return acc;
    }, {} as Record<string, number>) || {};

    const stats = {
      totalConflicts,
      unresolvedConflicts,
      resolvedConflicts,
      conflictsByType,
      conflictsBySeverity
    };

    // Transform conflicts data
    const transformedConflicts = conflicts?.map(conflict => {
      const event1 = eventMap.get(conflict.event1_id) || null;
      const event2 = eventMap.get(conflict.event2_id) || null;

      return {
      id: conflict.id,
      household_id: conflict.household_id,
      event1_id: conflict.event1_id,
      event2_id: conflict.event2_id,
      conflict_type: conflict.conflict_type,
      severity: conflict.severity,
      description: getConflictDescription(
        conflict.conflict_type,
        event1,
        event2
      ),
      detected_at: conflict.created_at,
      resolved_at: conflict.is_resolved ? conflict.updated_at : null,
      resolution_notes: null,
      event1: event1 ? {
        id: event1.id,
        title: event1.title || 'Unknown Event',
        start_at: event1.start_at,
        end_at: event1.end_at,
        attendee_user_id: event1.attendee_user_id
      } : null,
      event2: event2 ? {
        id: event2.id,
        title: event2.title || 'Unknown Event',
        start_at: event2.start_at,
        end_at: event2.end_at,
        attendee_user_id: event2.attendee_user_id
      } : null
    };
    }) || [];

    return NextResponse.json({
      success: true,
      data: {
        conflicts: transformedConflicts,
        stats
      }
    });
  } catch (error) {
    console.error('Error in GET /api/conflicts:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

function getConflictDescription(
  conflictType: string, 
  event1: any, 
  event2: any
): string {
  const formatTime = (timestamp: string) => {
    if (!timestamp) return '';
    return new Date(timestamp).toLocaleString();
  };

  switch (conflictType) {
    case 'time_overlap':
      return `"${event1?.title || 'Event 1'}" and "${event2?.title || 'Event 2'}" have overlapping times (${formatTime(event1?.start_at)} - ${formatTime(event1?.end_at)} vs ${formatTime(event2?.start_at)} - ${formatTime(event2?.end_at)})`;
    case 'same_title':
      return `"${event1?.title || 'Event 1'}" and "${event2?.title || 'Event 2'}" have identical titles`;
    case 'exact_duplicate':
      return `"${event1?.title || 'Event 1'}" and "${event2?.title || 'Event 2'}" appear to be exact duplicates (same title, start, and end times)`;
    default:
      return `Conflict detected between "${event1?.title || 'Event 1'}" and "${event2?.title || 'Event 2'}"`;
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { withAPISecurity } from '@/lib/security/apiProtection';
import { getDatabaseClient, getUserAndHouseholdData } from '@/lib/api/database';
import { createErrorResponse, createSuccessResponse, handleApiError } from '@/lib/api/errors';
import { DEFAULT_DIGEST_PREFERENCES } from '@/hooks/useDigestPreferences';

export async function GET(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      console.log('üöÄ GET: Fetching digest preferences for user:', user.id);

      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const supabase = getDatabaseClient();

      // Fetch existing preferences or return defaults
      const { data: preferences, error } = await supabase
        .from('digest_preferences')
        .select('*')
        .eq('user_id', user.id)
        .eq('household_id', household.id)
        .single();

      if (error && error.code !== 'PGRST116') { // PGRST116 = no rows returned
        console.error('Error fetching digest preferences:', error);
        return createErrorResponse('Failed to fetch digest preferences', 500, error.message);
      }

      // If no preferences exist, return defaults with user/household info
      if (!preferences) {
        const defaultPreferences = {
          ...DEFAULT_DIGEST_PREFERENCES,
          user_id: user.id,
          household_id: household.id,
          email_address: userData?.email || '',
        };
        
        return createSuccessResponse(defaultPreferences, 'Default digest preferences');
      }

      return createSuccessResponse(preferences, 'Digest preferences fetched successfully');

    } catch (error) {
      return handleApiError(error, { route: '/api/digest/preferences', method: 'GET', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api'
  });
}

export async function PUT(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      console.log('üöÄ PUT: Updating digest preferences for user:', user.id);

      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const body = await req.json();
      const supabase = getDatabaseClient();

      // Validate email if provided
      if (body.email_address && !isValidEmail(body.email_address)) {
        return createErrorResponse('Invalid email address', 400);
      }

      // Validate time format
      if (body.daily_digest_time && !isValidTime(body.daily_digest_time)) {
        return createErrorResponse('Invalid daily digest time format (use HH:MM)', 400);
      }

      if (body.weekly_digest_time && !isValidTime(body.weekly_digest_time)) {
        return createErrorResponse('Invalid weekly digest time format (use HH:MM)', 400);
      }

      // Check if preferences exist
      const { data: existingPreferences, error: fetchError } = await supabase
        .from('digest_preferences')
        .select('id')
        .eq('user_id', user.id)
        .eq('household_id', household.id)
        .single();

      const updateData = {
        ...body,
        user_id: user.id,
        household_id: household.id,
        updated_at: new Date().toISOString(),
      };

      let result;
      if (existingPreferences && !fetchError) {
        // Update existing preferences
        const { data, error } = await supabase
          .from('digest_preferences')
          .update(updateData)
          .eq('id', existingPreferences.id)
          .select()
          .single();

        if (error) {
          console.error('Error updating digest preferences:', error);
          return createErrorResponse('Failed to update digest preferences', 500, error.message);
        }

        result = data;
      } else {
        // Create new preferences
        const { data, error } = await supabase
          .from('digest_preferences')
          .insert({
            ...updateData,
            created_at: new Date().toISOString(),
          })
          .select()
          .single();

        if (error) {
          console.error('Error creating digest preferences:', error);
          return createErrorResponse('Failed to create digest preferences', 500, error.message);
        }

        result = data;
      }

      return createSuccessResponse(result, 'Digest preferences updated successfully');

    } catch (error) {
      return handleApiError(error, { route: '/api/digest/preferences', method: 'PUT', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api'
  });
}

// Helper functions
function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

function isValidTime(time: string): boolean {
  const timeRegex = /^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/;
  return timeRegex.test(time);
}

import { NextRequest, NextResponse } from 'next/server';
import { getAuth } from '@clerk/nextjs/server';
import { createClient } from '@supabase/supabase-js';
import { canAccessFeatureFromEntitlements } from '@/lib/server/canAccessFeature';
import { DigestDataService } from '@/lib/digestDataService';
import { EmailService } from '@/lib/emailService';
import { z } from 'zod';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseKey);

const SendDigestSchema = z.object({
  household_id: z.string().uuid(),
  type: z.enum(['daily', 'weekly']),
  user_id: z.string().optional(), // Optional - if not provided, send to all users in household
});

/**
 * Send digest email to household members
 */
export async function POST(request: NextRequest) {
  try {
    console.log('üîç Digest send API called');
    const { userId } = await getAuth(request);
    console.log('üîç Auth result:', { userId });
    
    if (!userId) {
      console.log('‚ùå No userId found, returning 401');
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const { household_id, type, user_id } = SendDigestSchema.parse(body);

    // Verify user has access to this household
    const { data: membership, error: membershipError } = await supabase
      .from('household_members')
      .select('household_id, role')
      .eq('household_id', household_id)
      .eq('user_id', userId)
      .single();
    
    if (membershipError || !membership) {
      return NextResponse.json({ error: 'Household not found or access denied' }, { status: 404 });
    }

    // Check entitlements for digest feature
    const { data: entitlements, error: entitlementsError } = await supabase
      .from('entitlements')
      .select('*')
      .eq('household_id', household_id)
      .single();

    if (entitlementsError || !entitlements) {
      return NextResponse.json({ error: 'Entitlements not found' }, { status: 404 });
    }

    if (!canAccessFeatureFromEntitlements(entitlements, 'digest_max_per_day')) {
      return NextResponse.json({ 
        error: 'Daily digest requires Pro plan',
        code: 'UPGRADE_REQUIRED'
      }, { status: 403 });
    }

    // Get household info
    const { data: household, error: householdError } = await supabase
      .from('households')
      .select('name')
      .eq('id', household_id)
      .single();

    if (householdError || !household) {
      return NextResponse.json({ error: 'Household not found' }, { status: 404 });
    }

    // Get users to send digest to
    let targetUsers;
    if (user_id) {
      // Send to specific user
      const { data: user, error: userError } = await supabase
        .from('users')
        .select('id, email')
        .eq('id', user_id)
        .single();

      if (userError || !user) {
        return NextResponse.json({ error: 'User not found' }, { status: 404 });
      }

      // Verify user is in the household
      const { data: membership, error: membershipError } = await supabase
        .from('household_members')
        .select('user_id')
        .eq('user_id', user_id)
        .eq('household_id', household_id)
        .single();

      if (membershipError || !membership) {
        return NextResponse.json({ error: 'User not in household' }, { status: 404 });
      }

      targetUsers = [user];
    } else {
      // Send to all household members
      const { data: members, error: membersError } = await supabase
        .from('household_members')
        .select('user_id')
        .eq('household_id', household_id);

      if (membersError || !members) {
        return NextResponse.json({ error: 'Failed to fetch household members' }, { status: 500 });
      }

      // Get user details for each member
      const userIds = members.map(member => member.user_id);
      const { data: users, error: usersError } = await supabase
        .from('users')
        .select('id, email')
        .in('id', userIds);

      if (usersError || !users) {
        return NextResponse.json({ error: 'Failed to fetch user details' }, { status: 500 });
      }

      targetUsers = users.filter(user => user && user.email);
    }

    if (targetUsers.length === 0) {
      return NextResponse.json({ error: 'No users found to send digest to' }, { status: 400 });
    }

    // Check daily digest quota
    if (type === 'daily') {
      const today = new Date().toISOString().split('T')[0];
      const { data: todayDigests } = await supabase
        .from('daily_digests')
        .select('id')
        .eq('household_id', household_id)
        .eq('digest_date', today);

      if (todayDigests && todayDigests.length >= entitlements.digest_max_per_day) {
        return NextResponse.json({ 
          error: 'Daily digest quota exceeded',
          code: 'QUOTA_EXCEEDED'
        }, { status: 429 });
      }
    }

    const results = [];
    let successCount = 0;
    let errorCount = 0;

    // Send digest to each user
    for (const user of targetUsers) {
      try {
        // Collect digest data
        const digestData = type === 'daily' 
          ? await DigestDataService.collectDailyDigestData(
              household_id,
              user.id,
              user.email,
              'User', // Fallback since name column doesn't exist
              household.name
            )
          : await DigestDataService.collectWeeklyDigestData(
              household_id,
              user.id,
              user.email,
              'User', // Fallback since name column doesn't exist
              household.name
            );

        // Send email
        const emailResult = type === 'daily'
          ? await EmailService.sendDailyDigest(digestData)
          : await EmailService.sendWeeklyDigest(digestData);

        if (emailResult.success) {
          successCount++;
          
          // Log successful digest (only for daily digests, and only once per household per day)
          if (type === 'daily') {
            const today = new Date().toISOString().split('T')[0];
            await supabase
              .from('daily_digests')
              .upsert({
                household_id,
                digest_date: today,
                sent_at: new Date().toISOString(),
                content: {
                  type: 'daily',
                  user_count: targetUsers.length,
                  message_id: emailResult.messageId
                },
                status: 'sent'
              }, {
                onConflict: 'household_id,digest_date'
              });
          }

          results.push({
            user_id: user.id,
            email: user.email,
            success: true,
            message_id: emailResult.messageId
          });
        } else {
          errorCount++;
          
          // Log failed digest
          if (type === 'daily') {
            const today = new Date().toISOString().split('T')[0];
            await supabase
              .from('daily_digests')
              .upsert({
                household_id,
                digest_date: today,
                sent_at: new Date().toISOString(),
                content: {
                  type: 'daily',
                  user_count: targetUsers.length,
                  error: emailResult.error
                },
                status: 'failed',
                error_message: emailResult.error
              }, {
                onConflict: 'household_id,digest_date'
              });
          }
          
          results.push({
            user_id: user.id,
            email: user.email,
            success: false,
            error: emailResult.error
          });
        }
      } catch (error) {
        errorCount++;
        console.error(`Error sending digest to user ${user.id}:`, error);
        results.push({
          user_id: user.id,
          email: user.email,
          success: false,
          error: error instanceof Error ? error.message : 'Unknown error'
        });
      }
    }

    // Log the digest sending for audit
    await supabase
      .from('audit_log')
      .insert({
        actor_id: userId,
        household_id,
        action: 'digest.send',
        target_table: 'daily_digests',
        meta: {
          digest_type: type,
          total_users: targetUsers.length,
          success_count: successCount,
          error_count: errorCount,
          results
        }
      });

    return NextResponse.json({
      success: true,
      message: `Digest sent to ${successCount} users`,
      stats: {
        total_users: targetUsers.length,
        success_count: successCount,
        error_count: errorCount
      },
      results
    });

  } catch (error) {
    console.error('Error sending digest:', error);
    if (error instanceof z.ZodError) {
      return NextResponse.json({ error: 'Invalid input', details: error.errors }, { status: 400 });
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { getDatabaseClient } from '@/lib/api/database';
import { z } from 'zod';

const updateEventSchema = z.object({
  title: z.string().min(1).optional(),
  description: z.string().optional(),
  startAt: z.string().datetime().optional(),
  endAt: z.string().datetime().optional(),
  timezone: z.string().optional(),
  isAllDay: z.boolean().optional(),
  rrule: z.string().optional(),
  exdates: z.array(z.string().datetime()).optional(),
  rdates: z.array(z.string().datetime()).optional(),
  location: z.string().optional(),
  calendarId: z.string().uuid().optional(),
  attendees: z.array(z.object({
    userId: z.string().optional(),
    email: z.string().email().optional(),
    status: z.enum(['accepted', 'declined', 'tentative', 'needsAction']).optional(),
    isOptional: z.boolean().optional()
  })).optional(),
  reminders: z.array(z.object({
    minutesBefore: z.number().min(0),
    method: z.enum(['push', 'email', 'sms'])
  })).optional()
});

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Await params for Next.js 15 compatibility
    const { id } = await params;

    const supabase = getDatabaseClient();
    
    // Get user's household
    const { data: userData, error: userError } = await supabase
      .from('users')
      .select('household_id')
      .eq('id', userId)
      .single();

    if (userError || !userData) {
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    // Get event with all related data
    const { data: event, error } = await supabase
      .from('events')
      .select(`
        *,
        calendar:calendars(name, color),
        attendees:event_attendees(
          id,
          user_id,
          email,
          status,
          is_optional
        ),
        reminders:event_reminders(
          minutes_before,
          method
        )
      `)
      .eq('id', id)
      .eq('household_id', userData.household_id)
      .single();

    if (error) {
      if (error.code === 'PGRST116') {
        return NextResponse.json({ error: 'Event not found' }, { status: 404 });
      }
      console.error('Error fetching event:', error);
      return NextResponse.json({ error: 'Failed to fetch event' }, { status: 500 });
    }

    return NextResponse.json({ event });

  } catch (error) {
    console.error('Error in GET /api/events/[id]:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

export async function PATCH(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Await params for Next.js 15 compatibility
    const { id } = await params;

    const body = await request.json();
    const validatedData = updateEventSchema.parse(body);

    const supabase = getDatabaseClient();
    
    // Get user's household
    const { data: userData, error: userError } = await supabase
      .from('users')
      .select('household_id')
      .eq('id', userId)
      .single();

    if (userError || !userData) {
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    // Check if event exists and user has permission
    const { data: existingEvent, error: checkError } = await supabase
      .from('events')
      .select('id, created_by')
      .eq('id', id)
      .eq('household_id', userData.household_id)
      .single();

    if (checkError || !existingEvent) {
      return NextResponse.json({ error: 'Event not found' }, { status: 404 });
    }

    // Check if user can modify (creator or household owner)
    const { data: userRole } = await supabase
      .from('household_members')
      .select('role')
      .eq('household_id', userData.household_id)
      .eq('user_id', userId)
      .single();

    const canModify = existingEvent.created_by === userId || userRole?.role === 'owner';
    
    if (!canModify) {
      return NextResponse.json({ error: 'Insufficient permissions' }, { status: 403 });
    }

    // Prepare update data
    const updateData: any = {
      updated_at: new Date().toISOString()
    };

    if (validatedData.title !== undefined) updateData.title = validatedData.title;
    if (validatedData.description !== undefined) updateData.description = validatedData.description;
    if (validatedData.startAt !== undefined) updateData.start_at = validatedData.startAt;
    if (validatedData.endAt !== undefined) updateData.end_at = validatedData.endAt;
    if (validatedData.timezone !== undefined) updateData.timezone = validatedData.timezone;
    if (validatedData.isAllDay !== undefined) updateData.is_all_day = validatedData.isAllDay;
    if (validatedData.rrule !== undefined) updateData.rrule = validatedData.rrule;
    if (validatedData.exdates !== undefined) updateData.exdates = validatedData.exdates;
    if (validatedData.rdates !== undefined) updateData.rdates = validatedData.rdates;
    if (validatedData.location !== undefined) updateData.location = validatedData.location;
    if (validatedData.calendarId !== undefined) updateData.calendar_id = validatedData.calendarId;

    // Update event
    const { data: event, error: eventError } = await supabase
      .from('events')
      .update(updateData)
      .eq('id', id)
      .select()
      .single();

    if (eventError) {
      console.error('Error updating event:', eventError);
      return NextResponse.json({ error: 'Failed to update event' }, { status: 500 });
    }

    // Update attendees if provided
    if (validatedData.attendees !== undefined) {
      // Delete existing attendees
      await supabase
        .from('event_attendees')
        .delete()
        .eq('event_id', id);

      // Insert new attendees
      if (validatedData.attendees.length > 0) {
        const attendeeData = validatedData.attendees.map(attendee => ({
          event_id: id,
          user_id: attendee.userId || null,
          email: attendee.email || null,
          status: attendee.status || 'needsAction',
          is_optional: attendee.isOptional || false
        }));

        const { error: attendeesError } = await supabase
          .from('event_attendees')
          .insert(attendeeData);

        if (attendeesError) {
          console.error('Error updating attendees:', attendeesError);
          // Don't fail the entire request for attendee errors
        }
      }
    }

    // Update reminders if provided
    if (validatedData.reminders !== undefined) {
      // Delete existing reminders
      await supabase
        .from('event_reminders')
        .delete()
        .eq('event_id', id);

      // Insert new reminders
      if (validatedData.reminders.length > 0) {
        const reminderData = validatedData.reminders.map(reminder => ({
          event_id: id,
          minutes_before: reminder.minutesBefore,
          method: reminder.method
        }));

        const { error: remindersError } = await supabase
          .from('event_reminders')
          .insert(reminderData);

        if (remindersError) {
          console.error('Error updating reminders:', remindersError);
          // Don't fail the entire request for reminder errors
        }
      }
    }

    return NextResponse.json({ 
      event,
      message: 'Event updated successfully' 
    });

  } catch (error) {
    console.error('Error in PATCH /api/events/[id]:', error);
    if (error instanceof z.ZodError) {
      return NextResponse.json({ error: 'Invalid event data', details: error.errors }, { status: 400 });
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

export async function DELETE(
