import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { getDatabaseClient } from '@/lib/api/database';
import { z } from 'zod';
import { generateEventOccurrences } from '@/lib/calendar/rruleUtils';
import { ConflictDetectionService } from '@/lib/conflictDetectionService';
import { canAccessFeatureFromEntitlements } from '@/lib/server/canAccessFeature';

// Validation schemas
const createEventSchema = z.object({
  title: z.string().min(1, 'Title is required'),
  description: z.string().optional(),
  startAt: z.string().datetime(),
  endAt: z.string().datetime(),
  timezone: z.string().default('Australia/Melbourne'),
  isAllDay: z.boolean().default(false),
  rrule: z.string().optional(),
  exdates: z.array(z.string().datetime()).optional().default([]),
  rdates: z.array(z.string().datetime()).optional().default([]),
  location: z.string().optional(),
  calendarId: z.string().uuid().optional(),
  attendees: z.array(z.object({
    userId: z.string().optional(),
    email: z.string().email().optional(),
    status: z.enum(['accepted', 'declined', 'tentative', 'needsAction']).default('needsAction'),
    isOptional: z.boolean().default(false)
  })).optional().default([]),
  reminders: z.array(z.object({
    minutesBefore: z.number().min(0),
    method: z.enum(['push', 'email', 'sms']).default('push')
  })).optional().default([])
});

const updateEventSchema = createEventSchema.partial();

const getEventsSchema = z.object({
  start: z.string().datetime(),
  end: z.string().datetime(),
  calendarId: z.string().uuid().optional()
});

export async function GET(request: NextRequest) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const start = searchParams.get('start');
    const end = searchParams.get('end');
    const calendarId = searchParams.get('calendarId');

    // Validate query parameters
    const validatedQuery = getEventsSchema.parse({
      start,
      end,
      calendarId: calendarId || undefined
    });

    const supabase = getDatabaseClient();
    
    // Get user's household
    const { data: userData, error: userError } = await supabase
      .from('users')
      .select('household_id')
      .eq('id', userId)
      .single();

    if (userError || !userData) {
      console.error('User not found:', userError);
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    console.log('User household_id:', userData.household_id);

    // Build query
    let query = supabase
      .from('events')
      .select(`
        *,
        calendar:calendars(name, color),
        attendees:event_attendees(
          id,
          user_id,
          email,
          status,
          is_optional
        ),
        reminders:event_reminders(
          minutes_before,
          method
        )
      `)
      .eq('household_id', userData.household_id)
      .gte('start_at', validatedQuery.start)
      .lte('start_at', validatedQuery.end)
      .order('start_at', { ascending: true });

    if (validatedQuery.calendarId) {
      query = query.eq('calendar_id', validatedQuery.calendarId);
    }

    console.log('Querying events for household:', userData.household_id);
    const { data: events, error } = await query;
    
    if (error) {
      console.error('Error fetching events:', error);
      return NextResponse.json({ error: 'Failed to fetch events', details: error.message }, { status: 500 });
    }
    
    console.log('Events found:', events?.length || 0);

    // Generate occurrences for recurring events
    const allOccurrences = [];
    const startDate = new Date(validatedQuery.start);
    const endDate = new Date(validatedQuery.end);

    for (const event of events || []) {
      if (event.rrule) {
        // Generate occurrences for recurring events
        const occurrences = generateEventOccurrences(
          {
            id: event.id,
            title: event.title,
            description: event.description,
            startAt: new Date(event.start_at),
            endAt: new Date(event.end_at),
            timezone: event.timezone,
            isAllDay: event.is_all_day,
            rrule: event.rrule,
            exdates: event.exdates?.map((d: string) => new Date(d)) || [],
            rdates: event.rdates?.map((d: string) => new Date(d)) || [],
            location: event.location
          },
          startDate,
          endDate
        );

        allOccurrences.push(...occurrences.map(occ => ({
          ...occ,
          calendar: event.calendar,
          attendees: event.attendees,
          reminders: event.reminders,
          isRecurring: true,
          originalEventId: event.id
        })));
      } else {
        // Single occurrence event
        allOccurrences.push({
          ...event,
          isRecurring: false
        });
      }
    }

    // Sort all occurrences by start time
    allOccurrences.sort((a, b) => 
      new Date(a.startAt || a.start_at).getTime() - new Date(b.startAt || b.start_at).getTime()
    );

    return NextResponse.json({ 
      events: allOccurrences,
      count: allOccurrences.length 
    });

  } catch (error) {
    console.error('Error in GET /api/events:', error);
    if (error instanceof z.ZodError) {
      return NextResponse.json({ error: 'Invalid query parameters', details: error.errors }, { status: 400 });
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const validatedData = createEventSchema.parse(body);

    const supabase = getDatabaseClient();
    
    // Get user's household
    const { data: userData, error: userError } = await supabase
      .from('users')
      .select('household_id')
      .eq('id', userId)
      .single();

    if (userError || !userData) {
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    // Create event
    const { data: event, error: eventError } = await supabase
      .from('events')
      .insert({
        household_id: userData.household_id,
        calendar_id: validatedData.calendarId || null,
        title: validatedData.title,
        description: validatedData.description,
        start_at: validatedData.startAt,
        end_at: validatedData.endAt,
        timezone: validatedData.timezone,
        is_all_day: validatedData.isAllDay,
        rrule: validatedData.rrule || null,
        exdates: validatedData.exdates || [],
        rdates: validatedData.rdates || [],
        location: validatedData.location,
        created_by: userId
      })
      .select()
      .single();

    if (eventError) {
      console.error('Error creating event:', eventError);
      return NextResponse.json({ error: 'Failed to create event' }, { status: 500 });
    }

    // Add attendees
    if (validatedData.attendees && validatedData.attendees.length > 0) {
      const attendeeData = validatedData.attendees.map(attendee => ({
        event_id: event.id,
        user_id: attendee.userId || null,
        email: attendee.email || null,
        status: attendee.status,
        is_optional: attendee.isOptional
      }));

      const { error: attendeesError } = await supabase
        .from('event_attendees')
        .insert(attendeeData);

      if (attendeesError) {
        console.error('Error adding attendees:', attendeesError);
        // Don't fail the entire request for attendee errors
      }
    }

    // Add reminders
    if (validatedData.reminders && validatedData.reminders.length > 0) {
      const reminderData = validatedData.reminders.map(reminder => ({
        event_id: event.id,
        minutes_before: reminder.minutesBefore,
        method: reminder.method
      }));

      const { error: remindersError } = await supabase
        .from('event_reminders')
        .insert(reminderData);

      if (remindersError) {
        console.error('Error adding reminders:', remindersError);
        // Don't fail the entire request for reminder errors
      }
    }

    // Check for conflicts if Pro plan
    let conflictResult = null;
    try {
      const { data: entitlements, error: entitlementsError } = await supabase
        .from('entitlements')
        .select('*')
        .eq('household_id', userData.household_id)
        .single();

      if (!entitlementsError && entitlements && canAccessFeatureFromEntitlements(entitlements, 'conflict_detection')) {
        conflictResult = await ConflictDetectionService.detectConflictsForEvent(
          event.id,
          userData.household_id,
          {
            id: event.id,
            title: event.title,
            start_at: event.start_at,
            end_at: event.end_at,
            is_all_day: event.is_all_day,
            household_id: userData.household_id
          }
        );
      }
    } catch (conflictError) {
      console.error('Error detecting conflicts:', conflictError);
      // Don't fail the entire request for conflict detection errors
    }

    return NextResponse.json({ 
      event,
      message: 'Event created successfully',
      conflicts: conflictResult
    }, { status: 201 });

  } catch (error) {
    console.error('Error in POST /api/events:', error);
    if (error instanceof z.ZodError) {
      return NextResponse.json({ error: 'Invalid event data', details: error.errors }, { status: 400 });
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { withAPISecurity } from "@/lib/security/apiProtection";
import { withFeatureFlags, createFeatureFlagContext } from "@/lib/middleware/featureFlags";
import { UserPlan } from "@/lib/server/canAccessFeature";

/**
 * GET /api/feature-flags
 * Returns feature flag status for the current user
 */
export async function GET(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    if (!user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Get user's plan from the request context or default to free
    const userPlan = (req.headers.get('x-user-plan') as UserPlan) || 'free';
    
    // Create feature flag context
    const context = createFeatureFlagContext(userPlan);
    
    return NextResponse.json({
      success: true,
      data: {
        userPlan: context.userPlan,
        availableFeatures: context.availableFeatures,
        featureFlags: context.featureFlags,
        timestamp: new Date().toISOString()
      }
    });
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api'
  });
}

/**
 * POST /api/feature-flags/test
 * Test feature flag access for a specific feature
 */
export async function POST(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    if (!user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    try {
      const body = await req.json();
      const { feature, userPlan = 'free' } = body;

      if (!feature) {
        return NextResponse.json({ 
          error: "Feature name is required" 
        }, { status: 400 });
      }

      // Create feature flag context
      const context = createFeatureFlagContext(userPlan as UserPlan);
      
      // Check if feature is available
      const isEnabled = context.featureFlags[feature] || false;
      
      return NextResponse.json({
        success: true,
        data: {
          feature,
          enabled: isEnabled,
          userPlan: context.userPlan,
          availableFeatures: context.availableFeatures,
          timestamp: new Date().toISOString()
        }
      });
    } catch (error) {
      console.error('Feature flag test error:', error);
      return NextResponse.json({ 
        error: "Invalid request body" 
      }, { status: 400 });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api'
  });
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { exchangeCodeForTokens, GoogleCalendarService } from '@/lib/googleCalendar';
import { z } from 'zod';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseKey);

const CallbackSchema = z.object({
  code: z.string(),
  state: z.string(),
});

/**
 * Handle Google Calendar OAuth callback
 */
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const { code, state } = CallbackSchema.parse({
      code: searchParams.get('code'),
      state: searchParams.get('state'),
    });

    // Decode state to get household_id and user_id
    const stateData = JSON.parse(Buffer.from(state, 'base64').toString());
    const { household_id, user_id } = stateData;

    // Exchange code for tokens
    const tokens = await exchangeCodeForTokens(code);

    if (!tokens.access_token) {
      throw new Error('No access token received');
    }

    // Initialize Google Calendar service
    const googleCalendar = new GoogleCalendarService(tokens.access_token);

    // Get user's calendars
    const calendars = await googleCalendar.getCalendarList();

    // Store tokens and calendar info in database
    const { error: insertError } = await supabase
      .from('google_calendar_imports')
      .upsert({
        household_id,
        google_calendar_id: 'primary', // We'll store primary calendar first
        access_token: tokens.access_token,
        refresh_token: tokens.refresh_token,
        token_expires_at: tokens.expiry_date ? new Date(tokens.expiry_date).toISOString() : null,
        last_import_at: new Date().toISOString(),
        last_successful_import_at: new Date().toISOString(),
        import_status: 'active',
        calendars: calendars.map(cal => ({
          id: cal.id,
          summary: cal.summary,
          accessRole: cal.accessRole,
          selected: cal.selected || false
        })),
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      }, {
        onConflict: 'household_id,google_calendar_id'
      });

    if (insertError) {
      console.error('Error storing Google Calendar tokens:', insertError);
      return NextResponse.json({ error: 'Failed to store authentication' }, { status: 500 });
    }

    // Redirect back to the app with success
    const redirectUrl = `${process.env.NEXT_PUBLIC_APP_URL}/calendar/sync?success=true&calendars=${calendars.length}`;
    
    return NextResponse.redirect(redirectUrl);

  } catch (error) {
    console.error('Error in Google Calendar callback:', error);
    
    // Redirect back to the app with error
    const redirectUrl = `${process.env.NEXT_PUBLIC_APP_URL}/calendar/sync?error=authentication_failed`;
    return NextResponse.redirect(redirectUrl);
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { GoogleCalendarService } from '@/lib/googleCalendar';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseKey);

/**
 * Cron job for automatic Google Calendar imports
 * This should be called by a cron service every hour
 */
export async function POST(request: NextRequest) {
  try {
    // Verify this is a legitimate cron request
    const authHeader = request.headers.get('authorization');
    const cronSecret = process.env.CRON_SECRET;
    
    if (!cronSecret || authHeader !== `Bearer ${cronSecret}`) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    console.log('üîÑ Starting Google Calendar cron import...');

    // Get all active Google Calendar imports
    const { data: importSettings, error: importError } = await supabase
      .from('google_calendar_imports')
      .select('*')
      .eq('import_status', 'active');

    if (importError) {
      console.error('Error fetching import settings:', importError);
      return NextResponse.json({ error: 'Failed to fetch import settings' }, { status: 500 });
    }

    if (!importSettings || importSettings.length === 0) {
      console.log('‚ÑπÔ∏è No active Google Calendar imports found');
      return NextResponse.json({ 
        success: true, 
        message: 'No active imports to process',
        processed: 0 
      });
    }

    let totalProcessed = 0;
    let totalImported = 0;
    const errors: string[] = [];

    // Process each household's Google Calendar import
    for (const importSetting of importSettings) {
      try {
        console.log(`üìÖ Processing import for household ${importSetting.household_id}`);

        // Check if token is expired
        if (importSetting.token_expires_at && new Date(importSetting.token_expires_at) < new Date()) {
          console.log(`‚ö†Ô∏è Token expired for household ${importSetting.household_id}, skipping`);
          continue;
        }

        if (!importSetting.access_token) {
          console.log(`‚ö†Ô∏è No access token for household ${importSetting.household_id}, skipping`);
          continue;
        }

        // Initialize Google Calendar service
        const googleCalendar = new GoogleCalendarService(importSetting.access_token);

        // Get selected calendars
        const calendars = importSetting.calendars || [];
        const selectedCalendars = calendars
          .filter((cal: any) => cal.selected !== false)
          .map((cal: any) => cal.id);

        if (selectedCalendars.length === 0) {
          console.log(`‚ÑπÔ∏è No calendars selected for household ${importSetting.household_id}`);
          continue;
        }

        // Set time range for import (last 24 hours to catch any missed events)
        const timeMin = new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString();
        const timeMax = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(); // Next 7 days

        // Import events from selected calendars
        const importResults = await googleCalendar.getEventsFromCalendars(selectedCalendars, {
          timeMin,
          timeMax,
          maxResults: 50 // Limit for cron job
        });

        let householdImported = 0;
        let householdSkipped = 0;

        // Process each calendar's events
        for (const { calendarId, events } of importResults) {
          for (const googleEvent of events) {
            try {
              // Convert Google event to internal format
              const internalEvent = GoogleCalendarService.convertToInternalEvent(googleEvent, calendarId);

              // Check if event already exists (by external_id)
              const { data: existingEvent } = await supabase
                .from('events')
                .select('id')
                .eq('external_id', googleEvent.id)
                .eq('external_source', 'google_calendar')
                .eq('household_id', importSetting.household_id)
                .single();

              if (existingEvent) {
                householdSkipped++;
                continue;
              }

              // Insert new event
              const { error: insertError } = await supabase
                .from('events')
                .insert({
                  household_id: importSetting.household_id,
                  ...internalEvent,
                  created_by: 'system', // System import
                  source: 'google_import_cron'
                });

              if (insertError) {
                console.error(`Error inserting event for household ${importSetting.household_id}:`, insertError);
                continue;
              }

              householdImported++;

            } catch (error) {
              console.error(`Error processing event for household ${importSetting.household_id}:`, error);
            }
          }
        }

        // Update import status
        await supabase
          .from('google_calendar_imports')
          .update({
            last_import_at: new Date().toISOString(),
            last_successful_import_at: new Date().toISOString(),
            updated_at: new Date().toISOString()
          })
          .eq('household_id', importSetting.household_id);

        // Log the import for audit
        await supabase
          .from('audit_log')
          .insert({
            actor_id: 'system',
            household_id: importSetting.household_id,
            action: 'google_calendar.cron_import',
            target_table: 'events',
            meta: {
              imported_events: householdImported,
              skipped_events: householdSkipped,
              calendars_processed: selectedCalendars.length,
              time_range: { timeMin, timeMax }
            }
          });

        totalProcessed++;
        totalImported += householdImported;

        console.log(`‚úÖ Household ${importSetting.household_id}: ${householdImported} imported, ${householdSkipped} skipped`);

      } catch (error) {
        console.error(`Error processing household ${importSetting.household_id}:`, error);
        errors.push(`Household ${importSetting.household_id}: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    }

    console.log(`üéâ Cron import completed: ${totalProcessed} households processed, ${totalImported} events imported`);

    return NextResponse.json({
      success: true,
      message: 'Cron import completed',
      stats: {
        households_processed: totalProcessed,
        events_imported: totalImported,
        errors: errors.length
      },
      errors: errors.length > 0 ? errors : undefined
    });

  } catch (error) {
    console.error('Error in Google Calendar cron job:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { auth } from '@clerk/nextjs/server';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

export async function GET(_request: NextRequest) {
  try {
    const { userId } = await auth();
    
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    console.log(`üèÜ Leaderboard request for user: ${userId}`);

    // First, get the user's household_id from household_members table
    const { data: userHousehold, error: householdError } = await supabase
      .from('household_members')
      .select('household_id')
      .eq('user_id', userId)
      .single();

    if (householdError) {
      console.error('‚ùå Error fetching user household:', householdError);
      return NextResponse.json({ error: 'Failed to fetch user household' }, { status: 500 });
    }

    if (!userHousehold) {
      console.error('‚ùå User not found in household_members');
      return NextResponse.json({ error: 'User not found in household' }, { status: 404 });
    }

    const householdId = userHousehold.household_id;
    console.log(`üè† User household_id: ${householdId}`);

    // Get all users in the same household with their XP
    // First get the household member IDs
    const { data: memberIds, error: membersError } = await supabase
      .from('household_members')
      .select('user_id')
      .eq('household_id', householdId);

    if (membersError) {
      console.error('‚ùå Error fetching household members:', membersError);
      return NextResponse.json({ error: 'Failed to fetch household members' }, { status: 500 });
    }

    if (!memberIds || memberIds.length === 0) {
      console.log('üì≠ No household members found');
      return NextResponse.json({ 
        success: true, 
        leaderboard: [],
        householdId 
      });
    }

    // Then get the user data for all members
    const userIds = memberIds.map(m => m.user_id);
    const { data: users, error: usersError } = await supabase
      .from('users')
      .select('id, xp, email')
      .in('id', userIds)
      .order('xp', { ascending: false })
      .limit(10);

    if (usersError) {
      console.error('‚ùå Error fetching users:', usersError);
      return NextResponse.json({ error: 'Failed to fetch leaderboard data' }, { status: 500 });
    }

    // Transform the data to match the expected format
    const leaderboard = users?.map(user => ({
      id: user.id,
      xp: user.xp || 0,
      username: user.email || `User ${user.id.slice(-4)}`
    })) || [];

    console.log(`üìä Leaderboard data:`, leaderboard);

    return NextResponse.json({ 
      success: true, 
      leaderboard,
      householdId 
    });

  } catch (error) {
    console.error('‚ùå Unexpected error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest } from 'next/server';
import { withAPISecurity } from '@/lib/security/apiProtection';
import { getUserAndHouseholdData } from '@/lib/api/database';
import { createErrorResponse, createSuccessResponse, handleApiError } from '@/lib/api/errors';
import { performanceMonitor } from '@/lib/monitoring/PerformanceMonitor';

export async function GET(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);

      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const { searchParams } = new URL(req.url);
      const type = searchParams.get('type') || 'overview';
      const timeWindow = searchParams.get('timeWindow')
        ? parseInt(searchParams.get('timeWindow') as string, 10)
        : 3600000;

      let data: any = {};

      switch (type) {
        case 'overview':
          data = {
            aiMetrics: performanceMonitor.getAIPerformanceMetrics(timeWindow),
            websocketMetrics: performanceMonitor.getWebSocketMetrics(timeWindow),
            systemMetrics: performanceMonitor.getSystemMetrics(),
            totalMetrics: performanceMonitor.getMetricsCount(),
            isEnabled: performanceMonitor.isMonitoringEnabled(),
          };
          break;
        case 'ai':
          data = performanceMonitor.getAIPerformanceMetrics(timeWindow);
          break;
        case 'websocket':
          data = performanceMonitor.getWebSocketMetrics(timeWindow);
          break;
        case 'system':
          data = performanceMonitor.getSystemMetrics();
          break;
        case 'raw':
          data = performanceMonitor.getMetrics(timeWindow);
          break;
        default:
          return createErrorResponse('Invalid type parameter', 400);
      }

      return createSuccessResponse(data, 'Performance monitoring data retrieved successfully');
    } catch (error) {
      return handleApiError(error, {
        route: '/api/monitoring/performance',
        method: 'GET',
        userId: user.id,
      });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api',
  });
}

export async function POST(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);

      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const body = await req.json();
      const { action, metric } = body;

      switch (action) {
        case 'clear_metrics':
          performanceMonitor.clearMetrics();
          return createSuccessResponse({}, 'Performance metrics cleared successfully');
        case 'enable_monitoring':
          performanceMonitor.enable();
          return createSuccessResponse({}, 'Performance monitoring enabled');
        case 'disable_monitoring':
          performanceMonitor.disable();
          return createSuccessResponse({}, 'Performance monitoring disabled');
        case 'record_metric':
          if (!metric) {
            return createErrorResponse('Metric data is required', 400);
          }

          performanceMonitor.recordMetric({
            ...metric,
            userId: user.id,
            householdId: household.id,
          });

          return createSuccessResponse({}, 'Metric recorded successfully');
        default:
          return createErrorResponse('Unknown action', 400);
      }
    } catch (error) {
      return handleApiError(error, {
        route: '/api/monitoring/performance',
        method: 'POST',
        userId: user.id,
      });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api',
  });
}

import { NextRequest, NextResponse } from 'next/server';
import { withAPISecurity } from '@/lib/security/apiProtection';
import { getDatabaseClient, getUserAndHouseholdData, createAuditLog } from '@/lib/api/database';
import { createErrorResponse, createSuccessResponse, handleApiError } from '@/lib/api/errors';
import { notificationSendSchema } from '@/lib/validation/schemas';
import webpush from 'web-push';

// Configure web-push
webpush.setVapidDetails(
  process.env.VAPID_SUBJECT!,
  process.env.NEXT_PUBLIC_VAPID_PUBLIC_KEY!,
  process.env.VAPID_PRIVATE_KEY!
);

export async function POST(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      console.log('üöÄ POST: Sending notification for user:', user.id);

      // Parse and validate request body using Zod schema
      let validatedData;
      try {
        const body = await req.json();
        const tempSchema = notificationSendSchema.omit({ household_id: true });
        validatedData = tempSchema.parse(body);
      } catch (validationError: any) {
        return createErrorResponse('Invalid input', 400, validationError.errors);
      }

      const { title, body: notificationBody, user_id: targetUserId } = validatedData;

      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const householdId = household.id;

      // Determine who to send notifications to
      let targetUserIds = [user.id];
      
      if (targetUserId) {
        targetUserIds = [targetUserId];
      } else if (householdId) {
        // Get all users in the household
        const supabase = getDatabaseClient();
        const { data: householdMembers, error } = await supabase
          .from('household_members')
          .select('user_id')
          .eq('household_id', householdId);

        if (error) {
          return createErrorResponse('Failed to fetch household members', 500, error.message);
        }

        targetUserIds = householdMembers.map(member => member.user_id);
      }

      // Get push subscriptions for target users
      const supabase = getDatabaseClient();
      const { data: subscriptions, error: subscriptionsError } = await supabase
        .from('push_subscriptions')
        .select('*')
        .in('user_id', targetUserIds);

      if (subscriptionsError) {
        return createErrorResponse('Failed to fetch subscriptions', 500, subscriptionsError.message);
      }

      if (!subscriptions || subscriptions.length === 0) {
        return createSuccessResponse({
          success: false,
          message: 'No active subscriptions found'
        }, 'No subscriptions found');
      }

      // Prepare notification payload
      const notificationPayload = {
        title,
        body: notificationBody,
        icon: '/icons/icon-192x192.png', // Default icon
        badge: '/icons/icon-72x72.png', // Default badge
        tag: 'home-management', // Default tag
        url: '/', // Default URL
        timestamp: Date.now(),
        requireInteraction: false,
        silent: false,
      };

      // Send notifications
      const sendPromises = subscriptions.map(async (subscription) => {
        try {
          const pushSubscription = {
            endpoint: subscription.endpoint,
            keys: {
              p256dh: subscription.p256dh_key,
              auth: subscription.auth_key,
            },
          };

          await webpush.sendNotification(
            pushSubscription,
            JSON.stringify(notificationPayload)
          );

          console.log(`‚úÖ Notification sent to user: ${subscription.user_id}`);
          return { success: true, userId: subscription.user_id };
        } catch (error) {
          console.error(`‚ùå Failed to send notification to user: ${subscription.user_id}`, error);
          
          // If subscription is invalid, remove it from database
          if (error instanceof Error && error.message.includes('410')) {
            await supabase
              .from('push_subscriptions')
              .delete()
              .eq('user_id', subscription.user_id);
          }
          
          return { success: false, userId: subscription.user_id, error };
        }
      });

      const results = await Promise.all(sendPromises);
      const successCount = results.filter(r => r.success).length;
      const failureCount = results.length - successCount;

      console.log(`üìä Notification results: ${successCount} sent, ${failureCount} failed`);

      // Create audit log
      await createAuditLog({
        user_id: user.id,
        household_id: household.id,
        action: 'notification_sent',
        details: { 
          title, 
          body: notificationBody, 
          target_users: targetUserIds,
          results: { total: results.length, successful: successCount, failed: failureCount }
        }
      });

      return createSuccessResponse({
        message: `Notifications sent: ${successCount} successful, ${failureCount} failed`,
        results: {
          total: results.length,
          successful: successCount,
          failed: failureCount,
        }
      });
    } catch (error) {
      console.error('Unexpected error:', error);
      return handleApiError(error);
    }
  }, {
    requireAuth: true,
    requireCSRF: true,
    rateLimitConfig: 'api'
  });
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { z } from 'zod';
import { sb, ServerError, createErrorResponse } from '@/lib/server/supabaseAdmin';

const NotificationSettingsSchema = z.object({
  settings: z.object({
    choreReminders: z.boolean(),
    mealPlanningReminders: z.boolean(),
    shoppingListUpdates: z.boolean(),
    achievementNotifications: z.boolean(),
    householdUpdates: z.boolean(),
  })
});

// GET - Fetch user's notification settings
export async function GET(_request: NextRequest) {
  try {
    const { userId } = await auth();
    
    if (!userId) {
      console.log('‚ùå No userId found in auth()');
      throw new ServerError('Unauthorized', 401);
    }
    
    console.log('‚úÖ Fetching notification settings for user:', userId);

    const { data, error } = await sb()
      .from('notification_settings')
      .select('*')
      .eq('user_id', userId)
      .single();

    if (error && error.code !== 'PGRST116') { // PGRST116 = no rows returned
      console.error('Error fetching notification settings:', error);
      throw new ServerError('Failed to fetch settings', 500);
    }

    // Return default settings if none exist
    const defaultSettings = {
      choreReminders: true,
      mealPlanningReminders: true,
      shoppingListUpdates: true,
      achievementNotifications: true,
      householdUpdates: true,
    };

    return NextResponse.json({
      success: true,
      settings: data?.settings || defaultSettings
    });

  } catch (error) {
    if (error instanceof ServerError) {
      return createErrorResponse(error);
    }
    console.error('Unexpected error:', error);
    return createErrorResponse(new ServerError('Internal server error', 500));
  }
}

// POST - Update user's notification settings
export async function POST(request: NextRequest) {
  try {
    const { userId } = await auth();
    
    if (!userId) {
      throw new ServerError('Unauthorized', 401);
    }

    const body = await request.json();
    const validatedData = NotificationSettingsSchema.parse(body);
    const { settings } = validatedData;

    // Upsert the settings
    const { error } = await sb()
      .from('notification_settings')
      .upsert({
        user_id: userId,
        settings,
        updated_at: new Date().toISOString(),
      }, {
        onConflict: 'user_id'
      });

    if (error) {
      console.error('Error saving notification settings:', error);
      throw new ServerError('Failed to save settings', 500);
    }

    console.log(`‚úÖ Updated notification settings for user: ${userId}`);

    return NextResponse.json({
      success: true,
      message: 'Settings saved successfully',
      settings
    });

  } catch (error) {
    if (error instanceof ServerError) {
      return createErrorResponse(error);
    }
    console.error('Unexpected error:', error);
    return createErrorResponse(new ServerError('Internal server error', 500));
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { z } from 'zod';
import { sb, ServerError, createErrorResponse } from '@/lib/server/supabaseAdmin';

const SubscribeSchema = z.object({
  endpoint: z.string().url(),
  keys: z.object({
    p256dh: z.string(),
    auth: z.string(),
  }),
});

export async function POST(request: NextRequest) {
  try {
    const { userId } = await auth();
    
    if (!userId) {
      throw new ServerError('Unauthorized', 401);
    }

    const body = await request.json();
    const validatedData = SubscribeSchema.parse(body);
    const { endpoint, keys } = validatedData;

    // Check if subscription already exists
    const { data: existing } = await sb()
      .from('push_subscriptions')
      .select('id')
      .eq('user_id', userId)
      .eq('endpoint', endpoint)
      .single();

    if (existing) {
      // Update existing subscription
      const { error: updateError } = await sb()
        .from('push_subscriptions')
        .update({
          keys,
          updated_at: new Date().toISOString(),
        })
        .eq('id', existing.id);

      if (updateError) {
        console.error('Error updating subscription:', updateError);
        throw new ServerError('Failed to update subscription', 500);
      }

      console.log(`‚úÖ Updated push subscription for user: ${userId}`);
    } else {
      // Create new subscription
      const { error: insertError } = await sb()
        .from('push_subscriptions')
        .insert({
          user_id: userId,
          endpoint,
          keys,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        });

      if (insertError) {
        console.error('Error creating subscription:', insertError);
        throw new ServerError('Failed to create subscription', 500);
      }

      console.log(`‚úÖ Created push subscription for user: ${userId}`);
    }

    return NextResponse.json({
      success: true,
      message: 'Subscription saved successfully',
    });

  } catch (error) {
    if (error instanceof ServerError) {
      return createErrorResponse(error);
    }
    console.error('Unexpected error:', error);
    return createErrorResponse(new ServerError('Internal server error', 500));
  }
}

