import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { getAuth } from '@clerk/nextjs/server';
import { z } from 'zod';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseKey);

const GetQuietHoursSchema = z.object({
  household_id: z.string().uuid(),
});

const SetQuietHoursSchema = z.object({
  household_id: z.string().uuid(),
  enabled: z.boolean(),
  start_time: z.string().regex(/^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/),
  end_time: z.string().regex(/^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/),
  days_of_week: z.array(z.number().min(0).max(6)).min(1),
});

export async function GET(request: NextRequest) {
  try {
    const { userId } = await getAuth(request);
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const householdId = searchParams.get('household_id');
    
    if (!householdId) {
      return NextResponse.json({ error: 'household_id is required' }, { status: 400 });
    }

    const { household_id } = GetQuietHoursSchema.parse({ household_id: householdId });

    // Verify user has access to this household
    const { data: membership, error: membershipError } = await supabase
      .from('household_members')
      .select('household_id')
      .eq('household_id', household_id)
      .eq('user_id', userId)
      .single();
    
    if (membershipError || !membership) {
      return NextResponse.json({ error: 'Household not found or access denied' }, { status: 404 });
    }

    // Get quiet hours settings
    const { data: quietHours, error: quietHoursError } = await supabase
      .from('quiet_hours')
      .select('*')
      .eq('household_id', household_id)
      .eq('is_active', true)
      .single();

    if (quietHoursError && quietHoursError.code !== 'PGRST116') {
      console.error('Error fetching quiet hours:', quietHoursError);
      return NextResponse.json({ error: 'Failed to fetch quiet hours' }, { status: 500 });
    }

    // Calculate current status
    const now = new Date();
    const currentDay = now.getDay();
    const currentTime = now.toTimeString().slice(0, 5); // HH:MM format

    let isQuietHours = false;
    let nextChange = null;

    if (quietHours && quietHours.is_active) {
      const { start_time, end_time, days_of_week } = quietHours;
      const isDayActive = days_of_week.includes(currentDay);
      
      if (isDayActive) {
        // Check if we're in the time range
        if (start_time <= end_time) {
          // Same day range (e.g., 22:00 to 07:00)
          isQuietHours = currentTime >= start_time || currentTime <= end_time;
        } else {
          // Overnight range (e.g., 22:00 to 07:00)
          isQuietHours = currentTime >= start_time || currentTime <= end_time;
        }
      }

      // Calculate next change
      const tomorrow = new Date(now);
      tomorrow.setDate(tomorrow.getDate() + 1);
      
      if (isQuietHours) {
        // Currently in quiet hours, next change is when they end
        const endTimeToday = new Date(now);
        const [endHour, endMinute] = end_time.split(':').map(Number);
        endTimeToday.setHours(endHour, endMinute, 0, 0);
        
        if (endTimeToday <= now) {
          endTimeToday.setDate(endTimeToday.getDate() + 1);
        }
        
        nextChange = endTimeToday.toISOString();
      } else {
        // Currently not in quiet hours, next change is when they start
        const startTimeToday = new Date(now);
        const [startHour, startMinute] = start_time.split(':').map(Number);
        startTimeToday.setHours(startHour, startMinute, 0, 0);
        
        if (startTimeToday <= now) {
          startTimeToday.setDate(startTimeToday.getDate() + 1);
        }
        
        nextChange = startTimeToday.toISOString();
      }
    }

    const settings = quietHours ? {
      enabled: quietHours.is_active,
      start_time: quietHours.start_time,
      end_time: quietHours.end_time,
      days_of_week: quietHours.days_of_week
    } : {
      enabled: false,
      start_time: '22:00',
      end_time: '07:00',
      days_of_week: [0, 1, 2, 3, 4, 5, 6]
    };

    const status = {
      is_quiet_hours: isQuietHours,
      settings,
      next_change: nextChange,
      formatted: {
        next_change: nextChange ? new Date(nextChange).toLocaleString() : null,
        days_of_week: quietHours ? 
          quietHours.days_of_week.map(d => ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][d]).join(', ') : 
          null,
        time_range: quietHours ? 
          `${quietHours.start_time} - ${quietHours.end_time}` : 
          null
      }
    };

    return NextResponse.json({
      success: true,
      data: {
        settings,
        status
      }
    });
  } catch (error) {
    console.error('Error in GET /api/quiet-hours:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId } = await getAuth(request);
    console.log('üîê Quiet hours POST auth result:', { userId });
    if (!userId) {
      console.log('‚ùå No user ID from auth in quiet hours POST');
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const { household_id, enabled, start_time, end_time, days_of_week } = SetQuietHoursSchema.parse(body);

    // Verify user has access to this household
    const { data: membership, error: membershipError } = await supabase
      .from('household_members')
      .select('household_id')
      .eq('household_id', household_id)
      .eq('user_id', userId)
      .single();
    
    if (membershipError || !membership) {
      return NextResponse.json({ error: 'Household not found or access denied' }, { status: 404 });
    }

    if (enabled) {
      // Upsert quiet hours settings
      const { data, error } = await supabase
        .from('quiet_hours')
        .upsert({
          household_id,
          start_time,
          end_time,
          days_of_week,
          is_active: true,
          updated_at: new Date().toISOString()
        }, {
          onConflict: 'household_id'
        })
        .select()
        .single();

      if (error) {
        console.error('Error saving quiet hours:', error);
        return NextResponse.json({ error: 'Failed to save quiet hours settings' }, { status: 500 });
      }

      return NextResponse.json({
        success: true,
        data,
        message: 'Quiet hours settings saved successfully'
      });
    } else {
      // Disable quiet hours
      const { error } = await supabase
        .from('quiet_hours')
        .update({ is_active: false })
        .eq('household_id', household_id);

      if (error) {
        console.error('Error disabling quiet hours:', error);
        return NextResponse.json({ error: 'Failed to disable quiet hours' }, { status: 500 });
      }

      return NextResponse.json({
        success: true,
        message: 'Quiet hours disabled successfully'
      });
    }
  } catch (error) {
    console.error('Error in POST /api/quiet-hours:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { withAPISecurity } from '@/lib/security/apiProtection';
import { getDatabaseClient, getUserAndHouseholdData, createAuditLog } from '@/lib/api/database';
import { createErrorResponse, createSuccessResponse, handleApiError } from '@/lib/api/errors';
import { createRecipeSchema } from '@/lib/validation/schemas';

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  return withAPISecurity(request, async (req, user) => {
    try {
      const { id: recipeId } = await params;

      if (!recipeId) {
        return createErrorResponse('Recipe ID is required', 400);
      }

      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const supabase = getDatabaseClient();

      // First, verify the recipe belongs to the user's household
      const { data: existingRecipe, error: fetchError } = await supabase
        .from('recipes')
        .select('id, household_id, title')
        .eq('id', recipeId)
        .eq('household_id', household.id)
        .single();

      if (fetchError || !existingRecipe) {
        return createErrorResponse('Recipe not found or access denied', 404);
      }

      // Delete the recipe
      const { error: deleteError } = await supabase
        .from('recipes')
        .delete()
        .eq('id', recipeId)
        .eq('household_id', household.id);

      if (deleteError) {
        console.error('Error deleting recipe:', deleteError);
        return createErrorResponse('Failed to delete recipe', 500, deleteError.message);
      }

      // Add audit log entry
      await createAuditLog({
        action: 'recipe.deleted',
        targetTable: 'recipes',
        targetId: recipeId,
        userId: user.id,
        metadata: { 
          recipe_title: existingRecipe.title,
          household_id: household.id
        }
      });

      return createSuccessResponse({}, 'Recipe deleted successfully');

    } catch (error) {
      return handleApiError(error, { route: '/api/recipes/[id]', method: 'DELETE', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: true,
    rateLimitConfig: 'api'
  });
}

export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  return withAPISecurity(request, async (req, user) => {
    try {
      const { id: recipeId } = await params;

      if (!recipeId) {
        return createErrorResponse('Recipe ID is required', 400);
      }

      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      // Parse and validate request body using Zod schema
      let validatedData;
      try {
        const body = await req.json();
        console.log('Request body:', body);
        
        const tempSchema = createRecipeSchema.omit({ household_id: true });
        validatedData = tempSchema.parse(body);
      } catch (validationError: any) {
        console.log('Validation failed:', validationError.errors);
        return createErrorResponse('Invalid input', 400, validationError.errors);
      }

      const supabase = getDatabaseClient();

      // First, verify the recipe belongs to the user's household
      const { data: existingRecipe, error: fetchError } = await supabase
        .from('recipes')
        .select('id, household_id, title')
        .eq('id', recipeId)
        .eq('household_id', household.id)
        .single();

      if (fetchError || !existingRecipe) {
        return createErrorResponse('Recipe not found or access denied', 404);
      }

      // Parse ingredients from text to structured format
      const parsedIngredients = parseIngredients(validatedData.ingredients);
      
      // Parse instructions from text to array
      const parsedInstructions = parseInstructions(validatedData.instructions);

      // Update the recipe in the database with validated data
      const { data: recipe, error: updateError } = await supabase
        .from('recipes')
        .update({
          title: validatedData.title,
          description: validatedData.description || '',
          ingredients: parsedIngredients, // JSONB array
          instructions: parsedInstructions, // TEXT array
          prep_time: validatedData.prep_time,
          cook_time: validatedData.cook_time,
          servings: validatedData.servings,
          tags: validatedData.tags || [],
          updated_at: new Date().toISOString()
        })
        .eq('id', recipeId)
        .eq('household_id', household.id)
        .select('*')
        .single();

      if (updateError) {
        console.error('Database update error:', updateError);
        return createErrorResponse('Failed to update recipe in database', 500, updateError.message);
      }

      // Add audit log entry
      await createAuditLog({
        action: 'recipe.updated',
        targetTable: 'recipes',
        targetId: recipeId,
        userId: user.id,
        metadata: { 
          recipe_title: validatedData.title,
          household_id: household.id,
          ingredients_count: parsedIngredients.length
        }
      });

      console.log('Recipe updated successfully:', recipe);

      return createSuccessResponse({ 
        recipe: {
          id: recipe.id,
          name: recipe.title, // Map back to 'name' for frontend compatibility
          description: recipe.description,
          prep_time: recipe.prep_time,
          cook_time: recipe.cook_time,
          servings: recipe.servings,
          ingredients: recipe.ingredients,
          instructions: recipe.instructions,
          created_at: recipe.created_at,
          household_id: recipe.household_id,
          created_by: recipe.created_by,
          updated_at: recipe.updated_at,
          image_url: recipe.image_url,
          tags: recipe.tags || [],
          is_favorite: false, // Default value
          difficulty: 'medium' // Default value
        }
      }, 'Recipe updated successfully');

    } catch (error) {
      return handleApiError(error, { route: '/api/recipes/[id]', method: 'PUT', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: true,
    rateLimitConfig: 'api'
  });
}

// Helper function to parse ingredients text into structured format
function parseIngredients(ingredientsText: string): any[] {
  if (!ingredientsText) return [];
  
  // Split by newlines or commas and clean up
  const lines = ingredientsText.split(/[\n,]+/).map(line => line.trim()).filter(Boolean);
  
  return lines.map((line, index) => {
    // Try to parse quantity and unit from the line
    const match = line.match(/^(\d+(?:\.\d+)?)?\s*([a-zA-Z]+)?\s*(.+)$/);
    
    if (match) {
      const [, amount, unit, name] = match;
      return {
        id: `temp-${index}`,
        recipe_id: '', // Will be set by database
        name: (name || line).trim(), // Fallback to full line if name is undefined
        amount: amount ? parseFloat(amount) : 1,
        unit: unit ? unit.trim() : '',
        notes: ''
      };
    }
    
    // If no quantity/unit pattern, treat as just a name
    return {
      id: `temp-${index}`,
      recipe_id: '', // Will be set by database
      name: line,
      amount: 1,
      unit: '',
      notes: ''
    };
  });
}

// Helper function to parse instructions text into array format
function parseInstructions(instructionsText: string): string[] {
  if (!instructionsText) return [];
  
  // Split by newlines and clean up
  return instructionsText
    .split('\n')
    .map(line => line.trim())
    .filter(Boolean)
    .map((line, _index) => {
      // Remove step numbers if they exist (e.g., "1. ", "Step 1: ")
      const cleaned = line.replace(/^(\d+\.?\s*|Step\s*\d+:\s*)/i, '').trim();
      return cleaned || line; // If cleaning results in empty string, use original
    });
}

