import { NextRequest, NextResponse } from 'next/server';
import { currentUser } from '@clerk/nextjs/server';
import { createServerComponentClient } from '@supabase/auth-helpers-nextjs';
import { cookies } from 'next/headers';
import { logger, createRequestLogger } from '@/lib/logging/logger';

export async function GET(req: NextRequest) {
  const requestId = logger.generateRequestId();
  const log = createRequestLogger(requestId);
  
  try {
    log.apiCall('GET', '/api/security/metrics');
    
    // Get authenticated user
    const clerkUser = await currentUser();
    if (!clerkUser) {
      log.apiError('GET', '/api/security/metrics', new Error('Unauthorized'), { status: 401 });
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get Supabase client
    const supabase = createServerComponentClient({ cookies });
    
    // Check if user is admin (you can customize this logic)
    const { data: userRole } = await supabase
      .from('users')
      .select('role')
      .eq('id', clerkUser.id)
      .single();

    if (userRole?.role !== 'admin') {
      log.apiError('GET', '/api/security/metrics', new Error('Forbidden'), { status: 403 });
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    // Fetch security metrics from the security_dashboard view
    const { data: metrics, error } = await supabase
      .from('security_dashboard')
      .select('*');

    if (error) {
      log.error('Failed to fetch security metrics', error, { userId: clerkUser.id });
      return NextResponse.json({ error: 'Failed to fetch security metrics' }, { status: 500 });
    }

    log.apiSuccess('GET', '/api/security/metrics', { userId: clerkUser.id, metricsCount: metrics?.length || 0 });
    
    return NextResponse.json(metrics || []);
    
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    log.apiError('GET', '/api/security/metrics', new Error(errorMessage), { userId: clerkUser?.id });
    
    return NextResponse.json(
      { error: 'Internal server error' }, 
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { getAuth } from "@clerk/nextjs/server";
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseKey);

export async function POST(request: NextRequest) {
  console.log('Set role API called');
  
  const { userId } = await getAuth(request);

  if (!userId) {
    console.log('No userId found, unauthorized');
    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
  }

  try {
    // Parse request body
    const body = await request.json();
    const { clerkId, role } = body;

    // Validate request body
    if (!clerkId || !role) {
      return NextResponse.json({ 
        error: "Missing required fields: clerkId and role" 
      }, { status: 400 });
    }

    if (!['owner', 'member'].includes(role)) {
      return NextResponse.json({ 
        error: "Invalid role. Must be 'owner' or 'member'" 
      }, { status: 400 });
    }

    console.log(`User ${userId} attempting to set role of ${clerkId} to ${role}`);

    // Check if current user is an owner in household_members
    const { data: currentUser, error: currentUserError } = await supabase
      .from('household_members')
      .select('role')
      .eq('user_id', userId)
      .single();

    if (currentUserError) {
      console.error('Error fetching current user:', currentUserError);
      return NextResponse.json({ 
        error: "Failed to verify current user permissions" 
      }, { status: 500 });
    }

    if (!currentUser) {
      return NextResponse.json({ 
        error: "Current user not found in household" 
      }, { status: 404 });
    }

    if (currentUser.role !== 'owner') {
      return NextResponse.json({ 
        error: "Insufficient permissions. Only owners can set user roles" 
      }, { status: 403 });
    }

    // Update the target user's role in household_members
    const { data: updatedUser, error: updateError } = await supabase
      .from('household_members')
      .update({ role })
      .eq('user_id', clerkId)
      .select()
      .single();

    if (updateError) {
      console.error('Error updating user role in household_members:', updateError);
      return NextResponse.json({ 
        error: "Failed to update user role in household" 
      }, { status: 500 });
    }

    if (!updatedUser) {
      return NextResponse.json({ 
        error: "Target user not found in household" 
      }, { status: 404 });
    }

    // Also update the role in the users table
    const { data: _updatedUserInUsers, error: usersUpdateError } = await supabase
      .from('users')
      .update({ role })
      .eq('clerk_id', clerkId)
      .select()
      .single();

    if (usersUpdateError) {
      console.error('Error updating user role in users table:', usersUpdateError);
      // Don't fail the request, just log the error since household_members was updated successfully
      console.warn('Warning: Failed to sync role to users table, but household_members was updated');
    }

    console.log(`Successfully updated user ${clerkId} role to ${role}`);
    return NextResponse.json({ 
      success: true,
      message: `User role updated successfully to ${role}`,
      user: {
        userId: updatedUser.user_id,
        role: updatedUser.role
      }
    });

  } catch (error) {
    console.error('Exception in set role API:', error);
    return NextResponse.json({ 
      error: error instanceof Error ? error.message : 'Unknown error occurred' 
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { withAPISecurity } from '@/lib/security/apiProtection';
import { getDatabaseClient, getUserAndHouseholdData, createAuditLog } from '@/lib/api/database';
import { createErrorResponse, createSuccessResponse, handleApiError } from '@/lib/api/errors';
import { toggleShoppingItemSchema } from '@/lib/validation/schemas';
import { logger } from '@/lib/logging/logger';

export async function POST(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      // Parse and validate input using Zod schema
      let validatedData;
      try {
        const body = await req.json();
        
        // Validate the input using our schema
        validatedData = toggleShoppingItemSchema.parse(body);
      } catch (validationError: any) {
        logger.warn('Shopping item toggle validation failed', {
          userId: user.id,
          errors: validationError.errors,
        });
        return createErrorResponse('Invalid input', 400, validationError.errors);
      }

      const { id: itemId, is_complete } = validatedData;

      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      // First get the current item to check its completion status and household
      const supabase = getDatabaseClient();
      const { data: currentItem, error: fetchError } = await supabase
        .from('shopping_items')
        .select(`
          is_complete,
          shopping_lists!inner(household_id)
        `)
        .eq('id', itemId)
        .single();

      if (fetchError) {
        logger.error('Error fetching shopping item before toggle', fetchError, {
          userId: user.id,
          itemId,
        });
        return createErrorResponse('Failed to fetch shopping item', 500, fetchError.message);
      }

      // Check if item was previously incomplete (to award rewards only once)
      const wasIncomplete = !currentItem.is_complete;
      // Fix: Access household_id correctly from the joined shopping_lists
      const itemHouseholdId = (currentItem.shopping_lists as any)?.household_id;

      if (!itemHouseholdId) {
        logger.error('Shopping item missing household_id', new Error('household_id missing'), {
          userId: user.id,
          itemId,
        });
        return createErrorResponse('Shopping item not associated with a household', 400);
      }

      // Verify the item belongs to the user's household
      if (itemHouseholdId !== household.id) {
        return createErrorResponse('Access denied to shopping item', 403);
      }

      if (is_complete && wasIncomplete) {
        // Complete the item and award rewards
        // First, get the current user's XP and coins
        const { data: userData, error: userError } = await supabase
          .from('users')
          .select('xp, coins')
          .eq('id', user.id);

        if (userError) {
          logger.error('Error fetching user rewards before toggle', userError, {
            userId: user.id,
            itemId,
          });
          return createErrorResponse('Failed to fetch user data', 500, userError.message);
        }

        if (!userData || userData.length === 0) {
          logger.warn('No user data found during shopping item toggle', {
            userId: user.id,
            itemId,
          });
          return createErrorResponse('User not found', 404);
        }

        const userDataRecord = userData[0];

        // Update the shopping item completion status
        const { data: updatedItem, error: itemError } = await supabase
          .from('shopping_items')
          .update({
            is_complete: true,
            completed_by: user.id,
            completed_at: new Date().toISOString()
          })
          .eq('id', itemId)
          .select()
          .single();

        if (itemError) {
          logger.error('Error updating shopping item completion', itemError, {
            userId: user.id,
            itemId,
          });
          return createErrorResponse('Failed to update shopping item', 500, itemError.message);
        }

        // Update user XP and coins
        const currentXp = userDataRecord?.xp ?? 0;
        const currentCoins = userDataRecord?.coins ?? 0;
        const newXp = currentXp + 10;
        const newCoins = currentCoins + 1;

        const { error: updateError } = await supabase
          .from('users')
          .update({
            xp: newXp,
            coins: newCoins
          })
          .eq('id', user.id);

        if (updateError) {
          logger.error('Error updating user rewards after shopping completion', updateError, {
            userId: user.id,
            itemId,
            newXp,
            newCoins,
          });
          return createErrorResponse('Failed to update user rewards', 500, updateError.message);
        }

        // Add audit log entry for completion
        await createAuditLog({
          action: 'shopping_item.completed',
          targetTable: 'shopping_items',
          targetId: itemId,
          userId: user.id,
          metadata: { 
            household_id: household.id,
            xp_awarded: 10,
            coins_awarded: 1,
            previous_status: false
          }
        });

        logger.info('Shopping item completed with rewards', {
          userId: user.id,
          householdId: household.id,
          itemId,
          xpAwarded: 10,
          coinsAwarded: 1,
        });
        return createSuccessResponse({ 
          item: updatedItem,
          rewards: { xp: 10, coins: 1 }
        }, 'Shopping item completed and rewards awarded');
      } else {
        // Toggle the item to the requested state
        const { data: updatedItem, error: itemError } = await supabase
          .from('shopping_items')
          .update({
            is_complete: is_complete,
            completed_by: is_complete ? user.id : null,
            completed_at: is_complete ? new Date().toISOString() : null
          })
          .eq('id', itemId)
          .select()
          .single();

        if (itemError) {
          logger.error('Error updating shopping item toggle state', itemError, {
            userId: user.id,
            itemId,
            newState: is_complete,
          });
          return createErrorResponse('Failed to update shopping item', 500, itemError.message);
        }

        // Add audit log entry for status change
        await createAuditLog({
          action: is_complete ? 'shopping_item.completed' : 'shopping_item.uncompleted',
          targetTable: 'shopping_items',
          targetId: itemId,
          userId: user.id,
          metadata: { 
            household_id: household.id,
            previous_status: !is_complete,
            new_status: is_complete
          }
        });

        logger.info('Shopping item toggled', {
          userId: user.id,
          householdId: household.id,
          itemId,
          newState: is_complete,
        });
        return createSuccessResponse({ item: updatedItem }, 'Shopping item toggled successfully');
      }

    } catch (error) {
      logger.error('Shopping item toggle failed', error as Error, {
        userId: user.id,
        route: '/api/shopping-items/toggle',
      });
      return handleApiError(error, { route: '/api/shopping-items/toggle', method: 'POST', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: true,
    rateLimitConfig: 'shopping'
  });
}

import { NextRequest, NextResponse } from 'next/server';
import { stripe } from '@/lib/stripe';
import { headers } from 'next/headers';
import { createClient } from '@/lib/supabaseClient';

const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET!;

export async function POST(req: NextRequest) {
  const body = await req.text();
  const signature = headers().get('stripe-signature')!;

  let event;

  try {
    event = stripe.webhooks.constructEvent(body, signature, webhookSecret);
  } catch (err) {
    console.error('Webhook signature verification failed:', err);
    return NextResponse.json({ error: 'Invalid signature' }, { status: 400 });
  }

  const supabase = createClient();

  try {
    switch (event.type) {
      case 'checkout.session.completed': {
        const session = event.data.object;
        const customerId = session.customer as string;
        const subscriptionId = session.subscription as string;
        const priceId = session.line_items?.data[0]?.price?.id;

        if (!priceId) {
          console.error('No price ID found in checkout session');
          break;
        }

        // Get the plan from the price ID
        const plan = priceId === process.env.STRIPE_PRO_PRICE_ID ? 'pro' : 
                    priceId === process.env.STRIPE_PRO_PLUS_PRICE_ID ? 'pro_plus' : 'free';

        // Update household plan in database
        const { error } = await supabase
          .from('households')
          .update({ 
            plan,
            stripe_customer_id: customerId,
            stripe_subscription_id: subscriptionId,
            updated_at: new Date().toISOString()
          })
          .eq('stripe_customer_id', customerId);

        if (error) {
          console.error('Error updating household plan:', error);
        } else {
          console.log(`Successfully updated household to ${plan} plan`);
        }
        break;
      }

      case 'customer.subscription.updated': {
        const subscription = event.data.object;
        const customerId = subscription.customer as string;
        const priceId = subscription.items.data[0]?.price?.id;

        if (!priceId) {
          console.error('No price ID found in subscription update');
          break;
        }

        const plan = priceId === process.env.STRIPE_PRO_PRICE_ID ? 'pro' : 
                    priceId === process.env.STRIPE_PRO_PLUS_PRICE_ID ? 'pro_plus' : 'free';

        // Update household plan
        const { error } = await supabase
          .from('households')
          .update({ 
            plan,
            stripe_subscription_id: subscription.id,
            updated_at: new Date().toISOString()
          })
          .eq('stripe_customer_id', customerId);

        if (error) {
          console.error('Error updating subscription:', error);
        }
        break;
      }

      case 'customer.subscription.deleted': {
        const subscription = event.data.object;
        const customerId = subscription.customer as string;

        // Downgrade to free plan
        const { error } = await supabase
          .from('households')
          .update({ 
            plan: 'free',
            stripe_subscription_id: null,
            updated_at: new Date().toISOString()
          })
          .eq('stripe_customer_id', customerId);

        if (error) {
          console.error('Error downgrading subscription:', error);
        } else {
          console.log('Successfully downgraded household to free plan');
        }
        break;
      }

      case 'invoice.payment_failed': {
        const invoice = event.data.object;
        const customerId = invoice.customer as string;

        // You might want to send an email notification here
        console.log(`Payment failed for customer ${customerId}`);
        break;
      }

      default:
        console.log(`Unhandled event type: ${event.type}`);
    }

    return NextResponse.json({ received: true });
  } catch (error) {
    console.error('Error processing webhook:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

