type UsersRow = Database['public']['Tables']['users']['Row'];
type ChoresRow = Database['public']['Tables']['chores']['Row'];
type RewardRedemptionRow = Database['public']['Tables']['reward_redemptions']['Row'];
type ShoppingListRow = Database['public']['Tables']['shopping_lists']['Row'];
type MealPlanRow = Database['public']['Tables']['meal_plans']['Row'];
type MealPlanIngredientRow = Database['public']['Tables']['meal_plan_ingredients']['Row'] & {
  meal_plans?: MealPlanRow | null;
};
type RecipeRow = Database['public']['Tables']['recipes']['Row'];
type BillRow = Database['public']['Tables']['bills']['Row'];
type ShoppingItemRow = Database['public']['Tables']['shopping_items']['Row'];

interface UserProfileExport {
  id: string;
  email: string | null;
  name: string | null;
  xp: number | null;
  created_at: string | null;
}

interface UserChoreExport {
  id: string;
  title: string;
  description: string | null;
  status: string | null;
  assigned_to: string | null;
  created_at: string | null;
  completed_at: string | null;
}

interface UserDataExport {
  profile?: UserProfileExport;
  chores?: UserChoreExport[];
  rewards?: RewardRedemptionRow[];
}

interface HouseholdDataExport {
  info?: {
    id: string;
    name: string | null;
    game_mode: string | null;
    created_at: string | null;
  };
  shopping_lists?: ShoppingListRow[];
  meal_plans?: MealPlanRow[];
  recipes?: RecipeRow[];
  bills?: BillRow[];
}

interface MissingIngredient {
  ingredient_name: string;
  meal_name: string;
  meal_type: string;
  quantity_needed: string;
}

type AuditTrailEntry = Record<string, unknown>;

interface ExportPayload {
  export_date: string;
  user_id: string;
  user_data: UserDataExport;
  household_data: HouseholdDataExport;
  audit_trail: AuditTrailEntry[];
  export_format: 'json';
  privacy_notice: string;
}

export async function GET(req: NextRequest) {
  return withAPISecurity(req, async (request, user) => {
    const requestId = logger.generateRequestId();
    const log = createRequestLogger(requestId);
    
    try {
      log.apiCall('GET', '/api/user-data/export');
      
      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        log.apiError('GET', '/api/user-data/export', new Error('No household found'), { status: 404 });
        return createErrorResponse('No household found', 404);
      }

      const supabase = getDatabaseClient();
      log.info('Starting data export', { userId: user.id, householdId: household.id });

      // Export user's personal data
      const userDataExport = await exportUserData(supabase, user.id);
      
      // Export household data (if user is a member)
      const householdData = await exportHouseholdData(supabase, household.id);
      
      // Export audit trail
      const auditData = await exportAuditData(supabase, user.id);

      const exportData: ExportPayload = {
        export_date: new Date().toISOString(),
        user_id: user.id,
        user_data: userDataExport,
        household_data: householdData,
        audit_trail: auditData,
        export_format: 'json',
        privacy_notice: 'This export contains all your personal data stored in the application.'
      };

      log.apiSuccess('GET', '/api/user-data/export', { 
        userId: user.id, 
        householdId: household.id,
        exportSize: JSON.stringify(exportData).length 
      });

      // Add audit log entry
      await createAuditLog({
        action: 'user_data.exported',
        targetTable: 'users',
        targetId: user.id,
        userId: user.id,
        metadata: { 
          household_id: household.id,
          export_size: JSON.stringify(exportData).length
        }
      });

      // Return as downloadable JSON file
      return new NextResponse(JSON.stringify(exportData, null, 2), {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
          'Content-Disposition': `attachment; filename="user-data-export-${new Date().toISOString().split('T')[0]}.json"`
        }
      });

    } catch (error) {
      return handleApiError(error, { route: '/api/user-data/export', method: 'GET', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api'
  });
}

// Export user's personal data
async function exportUserData(
  supabase: SupabaseClientType,
  userId: string,
): Promise<UserDataExport> {
  const userData: UserDataExport = {};

  // User profile
  const { data: userProfile } = await supabase
    .from('users')
    .select('*')
    .eq('id', userId)
    .single();
  
  if (userProfile) {
    userData.profile = {
      id: userProfile.id,
      email: userProfile.email,
      name: userProfile.name,
      xp: userProfile.xp,
      created_at: userProfile.created_at
    };
  }

  // User's chores
  const { data: userChores } = await supabase
    .from('chores')
    .select('*')
    .eq('assigned_to', userId);
  
  if (userChores) {
    userData.chores = userChores.map((chore) => ({
      id: chore.id,
      title: chore.title,
      description: chore.description,
      status: chore.status,
      assigned_to: chore.assigned_to,
      created_at: chore.created_at,
      completed_at: chore.completed_at
    }));
  }

  // User's rewards and redemptions
  const { data: userRewards } = await supabase
    .from('reward_redemptions')
    .select('*')
    .eq('user_id', userId);
  
  if (userRewards) {
    userData.rewards = userRewards;
  }

  return userData;
}

// Export household data
async function exportHouseholdData(
  supabase: SupabaseClientType,
  householdId: string,
): Promise<HouseholdDataExport> {
  const householdData: HouseholdDataExport = {};

  // Household info
  const { data: household } = await supabase
    .from('households')
    .select('*')
    .eq('id', householdId)
    .single();
  
  if (household) {
    householdData.info = {
      id: household.id,
      name: household.name,
      game_mode: household.game_mode,
      created_at: household.created_at
    };
  }

  // Shopping lists
  const { data: shoppingLists } = await supabase
    .from('shopping_lists')
    .select('*')
    .eq('household_id', householdId);
  
  if (shoppingLists) {
    householdData.shopping_lists = shoppingLists;
  }

  // Meal plans
  const { data: mealPlans } = await supabase
    .from('meal_plans')
    .select('*')
    .eq('household_id', householdId);
  
  if (mealPlans) {
    householdData.meal_plans = mealPlans;
  }

  // Recipes
  const { data: recipes } = await supabase
    .from('recipes')
    .select('*')
    .eq('household_id', householdId);
  
  if (recipes) {
    householdData.recipes = recipes;
  }

  // Bills
  const { data: bills } = await supabase
    .from('bills')
    .select('*')
    .eq('household_id', householdId);
  
  if (bills) {
    householdData.bills = bills;
  }

  return householdData;
}

// Export audit trail
async function exportAuditData(supabase: SupabaseClientType, userId: string): Promise<AuditTrailEntry[]> {
  const { data: auditTrail } = await supabase
    .rpc('get_user_audit_trail', { p_user_id: userId, p_limit: 1000 });
  
  return auditTrail || [];
}

// WebSocket API Route for Real-time AI Processing
// This can be easily removed if the WebSocket implementation doesn't work

import type { NextRequest } from 'next/server';
import { withAPISecurity } from '@/lib/security/apiProtection';
import { getUserAndHouseholdData } from '@/lib/api/database';
import { createErrorResponse, createSuccessResponse, handleApiError } from '@/lib/api/errors';
import { webSocketManager } from '@/lib/websocket/WebSocketServer';

export async function GET(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      // Check if WebSocket is initialized
      if (!webSocketManager.isInitialized()) {
        return createErrorResponse('WebSocket server not initialized', 503);
      }

      // Get WebSocket status
      const status = {
        initialized: webSocketManager.isInitialized(),
        connectedUsers: webSocketManager.getConnectedUsersCount(),
        householdRooms: webSocketManager.getHouseholdRoomsCount(),
        websocketUrl: `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/api/websocket`
      };

      return createSuccessResponse(status, 'WebSocket status retrieved successfully');

    } catch (error) {
      return handleApiError(error, { route: '/api/websocket', method: 'GET', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: false,
    rateLimitConfig: 'api'
  });
}

export async function POST(request: NextRequest) {
  return withAPISecurity(request, async (req, user) => {
    try {
      // Get user and household data
      const { user: userData, household, error: userError } = await getUserAndHouseholdData(user.id);
      
      if (userError || !household) {
        return createErrorResponse('User not found or no household', 404);
      }

      const body = await req.json();
      const { action } = body as { action?: string };

      switch (action) {
        case 'test_connection':
          return createSuccessResponse({
            connected: webSocketManager.isInitialized(),
            message: 'WebSocket connection test completed'
          }, 'Connection test completed');

        case 'get_status':
          return createSuccessResponse({
            initialized: webSocketManager.isInitialized(),
            connectedUsers: webSocketManager.getConnectedUsersCount(),
            householdRooms: webSocketManager.getHouseholdRoomsCount()
          }, 'WebSocket status retrieved');

        default:
          return createErrorResponse('Unknown action', 400);
      }

    } catch (error) {
      return handleApiError(error, { route: '/api/websocket', method: 'POST', userId: user.id });
    }
  }, {
    requireAuth: true,
    requireCSRF: true,
    rateLimitConfig: 'api'
  });
}

'use client';

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useAuth } from '@clerk/nextjs';
import { useRouter } from 'next/navigation';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import {
  FileText,
  Image,
  Receipt,
  Search,
  Store,
  DollarSign,
  CheckCircle,
  AlertCircle,
  Clock,
} from 'lucide-react';
import { FileUpload } from '@/components/attachments/FileUpload';
import { ReceiptItemsDisplay } from '@/components/attachments/ReceiptItemsDisplay';
import { format } from 'date-fns';
import { toast } from 'sonner';

interface ReceiptItem {
  id: string;
  item_name: string;
  item_price: number;
  item_category?: string;
  confidence_score: number;
}

interface Attachment {
  id: string;
  file_name: string;
  file_type: string;
  file_size: number;
  ocr_status: string;
  ocr_confidence?: number;
  receipt_total?: number;
  receipt_date?: string;
  receipt_store?: string;
  receipt_items?: ReceiptItem[];
  created_at: string;
}

export default function AttachmentsPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  const [attachments, setAttachments] = useState<Attachment[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAttachment, setSelectedAttachment] = useState<string | null>(null);
  const [filterStatus, setFilterStatus] = useState<string>('all');

  const fetchAttachments = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/attachments');
      const data = await response.json();

      if (data.success) {
        setAttachments(data.attachments || []);
      } else {
        toast.error('Failed to load attachments');
      }
    } catch (error) {
      console.error('‚ùå Error fetching attachments:', error);
      toast.error('Failed to load attachments');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    void fetchAttachments();
  }, [fetchAttachments, isLoaded, isSignedIn, router]);

  const handleUploadComplete = useCallback((attachment: Attachment) => {
    void fetchAttachments();
    setSelectedAttachment(attachment.id);
  }, [fetchAttachments]);

  const handleDeleteAttachment = useCallback(async (attachmentId: string) => {
    if (!confirm('Are you sure you want to delete this attachment?')) {
      return;
    }

    try {
      const response = await fetch('/api/attachments', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ attachment_id: attachmentId })
      });

      const data = await response.json();
      
      if (data.success) {
        toast.success('Attachment deleted successfully');
        setAttachments((prev) => prev.filter((a) => a.id !== attachmentId));
        if (selectedAttachment === attachmentId) {
          setSelectedAttachment(null);
        }
      } else {
        toast.error(data.error || 'Failed to delete attachment');
      }
    } catch (error) {
      console.error('‚ùå Error deleting attachment:', error);
      toast.error('Failed to delete attachment');
    }
  }, [selectedAttachment]);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'processing':
        return 'bg-blue-100 text-blue-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4" />;
      case 'processing':
        return <Clock className="h-4 w-4" />;
      case 'failed':
        return <AlertCircle className="h-4 w-4" />;
      default:
        return <Clock className="h-4 w-4" />;
    }
  };

  const getFileIcon = (fileType: string) => {
    if (fileType.startsWith('image/')) {
      return <Image className="h-4 w-4" />;
    }
    return <FileText className="h-4 w-4" />;
  };

  const filteredAttachments = useMemo(() => {
    return attachments.filter((attachment) => {
      const matchesSearch =
        attachment.file_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        attachment.receipt_store?.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesFilter = filterStatus === 'all' || attachment.ocr_status === filterStatus;

      return matchesSearch && matchesFilter;
    });
  }, [attachments, filterStatus, searchTerm]);

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  if (!isLoaded || loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex items-center justify-center min-h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600" />
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-4">
          <Receipt className="h-8 w-8 text-blue-500" />
          <h1 className="text-3xl font-bold text-gray-900">Receipts & Documents</h1>
          <Badge variant="secondary" className="bg-blue-100 text-blue-800">
            OCR-Powered
          </Badge>
        </div>
        <p className="text-gray-600 text-lg">
          Upload receipts and documents for automatic text extraction and shopping list integration
        </p>
      </div>

      <Tabs defaultValue="upload" className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="upload">Upload</TabsTrigger>
          <TabsTrigger value="receipts">Receipts ({attachments.filter(a => a.ocr_status === 'completed').length})</TabsTrigger>
          <TabsTrigger value="processing">Processing ({attachments.filter(a => a.ocr_status === 'processing' || a.ocr_status === 'pending').length})</TabsTrigger>
        </TabsList>

        <TabsContent value="upload" className="space-y-6">
          <FileUpload 
            onUploadComplete={handleUploadComplete}
            onUploadError={(error) => toast.error(error)}
          />
        </TabsContent>

        <TabsContent value="receipts" className="space-y-6">
          {/* Search and Filter */}
          <div className="flex gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 transform text-gray-400 h-4 w-4" />
                <Input
                  placeholder="Search receipts by name or store..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="all">All Status</option>
              <option value="completed">Completed</option>
              <option value="processing">Processing</option>
              <option value="failed">Failed</option>
            </select>
          </div>

          {/* Attachments List */}
          <div className="grid gap-4">
            {filteredAttachments.map((attachment) => (
              <Card 
                key={attachment.id} 
                className={`cursor-pointer transition-colors ${
                  selectedAttachment === attachment.id ? 'ring-2 ring-blue-500' : 'hover:bg-gray-50'
                }`}
                onClick={() => setSelectedAttachment(attachment.id)}
              >
                <CardContent className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3 flex-1 min-w-0">
                      {getFileIcon(attachment.file_type)}
                      <div className="flex-1 min-w-0">
                        <h3 className="font-medium text-gray-900 truncate">
                          {attachment.file_name}
                        </h3>
                        <div className="flex items-center gap-2 mt-1">
                          <Badge className={getStatusColor(attachment.ocr_status)}>
                            {getStatusIcon(attachment.ocr_status)}
                            <span className="ml-1 capitalize">{attachment.ocr_status}</span>
                          </Badge>
                          <span className="text-sm text-gray-500">
                            {formatFileSize(attachment.file_size)}
                          </span>
                          <span className="text-sm text-gray-500">
                            {format(new Date(attachment.created_at), 'MMM dd, yyyy')}
                          </span>
                        </div>
                        
                        {attachment.ocr_status === 'completed' && (
                          <div className="flex items-center gap-2 mt-2">
                            {attachment.receipt_store && (
                            <Badge variant="outline" className="flex items-center gap-1">
                                <Store className="h-3 w-3" />
                                {attachment.receipt_store}
                              </Badge>
                            )}
                            {attachment.receipt_total && (
                              <Badge variant="outline" className="flex items-center gap-1">
                                <DollarSign className="h-3 w-3" />
                                ${attachment.receipt_total.toFixed(2)}
                              </Badge>
                            )}
                            {attachment.receipt_items && attachment.receipt_items.length > 0 && (
                              <Badge variant="outline">
                                {attachment.receipt_items.length} items
                              </Badge>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                    
                    <div className="flex items-center gap-2">
                      {attachment.ocr_status === 'completed' && (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={(e) => {
                            e.stopPropagation();
                            setSelectedAttachment(attachment.id);
                          }}
                        >
                          View Items
                        </Button>
                      )}
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteAttachment(attachment.id);
                        }}
                        className="text-red-600 hover:text-red-700"
                      >
                        Delete
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {filteredAttachments.length === 0 && (
            <Card>
              <CardContent className="p-8 text-center">
                <Receipt className="h-12 w-12 mx-auto mb-4 text-gray-400" />
                <p className="text-gray-600">
                  {searchTerm || filterStatus !== 'all' 
                    ? 'No receipts match your search criteria'
                    : 'No receipts uploaded yet. Upload your first receipt to get started!'
                  }
                </p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="processing" className="space-y-6">
          <div className="grid gap-4">
            {attachments
              .filter(a => a.ocr_status === 'processing' || a.ocr_status === 'pending')
              .map((attachment) => (
                <Card key={attachment.id}>
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      {getFileIcon(attachment.file_type)}
                      <div className="flex-1">
                        <h3 className="font-medium text-gray-900">
                          {attachment.file_name}
                        </h3>
                        <div className="flex items-center gap-2 mt-1">
                          <Badge className={getStatusColor(attachment.ocr_status)}>
                            {getStatusIcon(attachment.ocr_status)}
                            <span className="ml-1 capitalize">{attachment.ocr_status}</span>
                          </Badge>
                          <span className="text-sm text-gray-500">
                            Processing OCR...
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
          </div>

          {attachments.filter(a => a.ocr_status === 'processing' || a.ocr_status === 'pending').length === 0 && (
            <Card>
              <CardContent className="p-8 text-center">
                <Clock className="h-12 w-12 mx-auto mb-4 text-gray-400" />
                <p className="text-gray-600">No files currently being processed</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>
      </Tabs>

      {/* Receipt Items Display */}
      {selectedAttachment && (
        <div className="mt-8">
          <ReceiptItemsDisplay 
            attachmentId={selectedAttachment}
            className="border-t pt-8"
          />
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect, useCallback } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { useUser } from '@clerk/nextjs';
import { toast } from 'sonner';
import { useUserData } from '@/hooks/useUserData';

interface Bill {
  id: string;
  title: string;
  description?: string;
  amount: number;
  currency: string;
  due_date: string;
  issued_date: string;
  paid_date?: string;
  status: 'pending' | 'paid' | 'overdue' | 'cancelled';
  category?: string;
  priority: 'low' | 'medium' | 'high' | 'urgent';
  source: string;
  created_at: string;
}

export default function EditBillPage() {
  const params = useParams();
  const router = useRouter();
  const { user } = useUser();
  const { userData } = useUserData();
  const [bill, setBill] = useState<Bill | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  
  // Form state
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    amount: '',
    due_date: '',
    category: 'General',
    priority: 'medium' as const
  });

  useEffect(() => {
    if (params.id && user) {
      void fetchBill();
    }
  }, [fetchBill, params.id, user]);

  const fetchBill = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/bills/${params.id}`);
      
      if (response.ok) {
        const data = await response.json();
        const billData = data.bill;
        setBill(billData);
        setFormData({
          title: billData.title || '',
          description: billData.description || '',
          amount: billData.amount?.toString() || '',
          due_date: billData.due_date || '',
          category: billData.category || 'General',
          priority: billData.priority || 'medium'
        });
      } else {
        console.error('Failed to fetch bill');
        toast.error('Failed to load bill');
        router.push('/bills');
      }
    } catch (error) {
      console.error('Error fetching bill:', error);
      toast.error('Error loading bill');
      router.push('/bills');
    } finally {
      setLoading(false);
    }
  }, [params.id, router]);

  const handleSave = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.title || !formData.amount || !formData.due_date) {
      toast.error('Please fill in all required fields');
      return;
    }

    try {
      setSaving(true);
      
      const response = await fetch(`/api/bills/${params.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        toast.success('Bill updated successfully!');
        router.push('/bills');
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to update bill');
      }
    } catch (error) {
      console.error('Error updating bill:', error);
      toast.error('Something went wrong. Please try again.');
    } finally {
      setSaving(false);
    }
  }, [formData, params.id, router]);

  const handleDelete = useCallback(async () => {
    if (!confirm('Are you sure you want to delete this bill? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`/api/bills/${params.id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success('Bill deleted successfully!');
        router.push('/bills');
      } else {
        toast.error('Failed to delete bill');
      }
    } catch (error) {
      console.error('Error deleting bill:', error);
      toast.error('Something went wrong');
    }
  }, [params.id, router]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading bill...</p>
        </div>
      </div>
    );
  }

  if (!bill) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-gray-400 text-6xl mb-4">üí∞</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">Bill not found</h3>
          <p className="text-gray-600 mb-4">The bill you're looking for doesn't exist or you don't have access to it.</p>
          <button
            onClick={() => router.push('/bills')}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
          >
            Back to Bills
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Edit Bill</h1>
              <p className="text-gray-600">Update bill details and information</p>
            </div>
            <button
              onClick={handleDelete}
              className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors"
            >
              Delete Bill
            </button>
          </div>
        </div>

        {/* Edit Form */}
        <div className="bg-white rounded-lg shadow p-6">
          <form onSubmit={handleSave} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
                  Bill Title *
                </label>
                <input
                  type="text"
                  id="title"
                  required
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="e.g., Electricity Bill - January 2024"
                />
              </div>
              
              <div>
                <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
                  Amount (AUD) *
                </label>
                <input
                  type="number"
                  id="amount"
                  required
                  step="0.01"
                  min="0"
                  value={formData.amount}
                  onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="89.50"
                />
              </div>
              
              <div>
                <label htmlFor="due_date" className="block text-sm font-medium text-gray-700 mb-1">
                  Due Date *
                </label>
                <input
                  type="date"
                  id="due_date"
                  required
                  value={formData.due_date}
                  onChange={(e) => setFormData({ ...formData, due_date: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
              
              <div>
                <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
                  Category
                </label>
                <select
                  id="category"
                  value={formData.category}
                  onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="General">General</option>
                  <option value="Utilities">Utilities</option>
                  <option value="Insurance">Insurance</option>
                  <option value="Rent/Mortgage">Rent/Mortgage</option>
                  <option value="Subscriptions">Subscriptions</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              
              <div>
                <label htmlFor="priority" className="block text-sm font-medium text-gray-700 mb-1">
                  Priority
                </label>
                <select
                  id="priority"
                  value={formData.priority}
                  onChange={(e) => setFormData({ ...formData, priority: e.target.value as any })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="urgent">Urgent</option>
                </select>
              </div>
              
              <div className="md:col-span-2">
                <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                  Description
                </label>
                <textarea
                  id="description"
                  rows={3}
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Optional description or notes about this bill"
                />
              </div>
            </div>
            
            <div className="flex justify-end space-x-3 pt-4">
              <button
                type="button"
                onClick={() => router.push('/bills')}
                className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={saving}
                className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {saving ? 'Saving...' : 'Save Changes'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect, useCallback, useMemo } from 'react';
import { useUser } from '@clerk/nextjs';
import { useRouter } from 'next/navigation';
import { toast } from 'sonner';
import { postEventTypes } from '@/lib/postEvent';
import { useUserData } from '@/hooks/useUserData';

interface Bill {
  id: string;
  title: string;
  description?: string;
  amount: number;
  currency: string;
  due_date: string;
  issued_date: string;
  paid_date?: string;
  status: 'pending' | 'paid' | 'overdue' | 'cancelled';
  category?: string;
  priority: 'low' | 'medium' | 'high' | 'urgent';
  source: string;
  created_at: string;
}

export default function BillsPage() {
  const { user } = useUser();
  const { userData } = useUserData();
  const router = useRouter();
  const [bills, setBills] = useState<Bill[]>([]);
  const [loading, setLoading] = useState(true);
  const [creatingBill, setCreatingBill] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  
  // Form state
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    amount: '',
    due_date: '',
    category: 'General',
    priority: 'medium' as Bill['priority'],
  });

  const fetchBills = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/bills');
      
      if (response.ok) {
        const data = await response.json();
        setBills(data.bills || []);
      } else {
        console.error('Failed to fetch bills');
        toast.error('Failed to load bills');
      }
    } catch (error) {
      console.error('Error fetching bills:', error);
      toast.error('Error loading bills');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (user) {
      void fetchBills();
    }
  }, [fetchBills, user]);

  const handleCreateBill = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.title || !formData.amount || !formData.due_date) {
      toast.error('Please fill in all required fields');
      return;
    }

    try {
      setCreatingBill(true);
      
      const response = await fetch('/api/bills', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        await response.json();
        toast.success('Bill created successfully!');
        
        // Reset form and refresh bills
        setFormData({
          title: '',
          description: '',
          amount: '',
          due_date: '',
          category: 'General',
          priority: 'medium'
        });
        setShowCreateForm(false);
        void fetchBills();
        
        // Trigger automation event
        try {
          if (userData?.household_id) {
            await postEventTypes.billCreated({
              household_id: userData.household_id,
              title: formData.title,
              amount: parseFloat(formData.amount),
              due_date: formData.due_date,
              category: formData.category
            });
          }
        } catch (error) {
          console.error('Failed to trigger automation event:', error);
        }
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to create bill');
      }
    } catch (error) {
      console.error('Error creating bill:', error);
      toast.error('Something went wrong. Please try again.');
    } finally {
      setCreatingBill(false);
    }
  }, [fetchBills, formData, userData?.household_id]);

  const handleMarkAsPaid = useCallback(async (billId: string) => {
    try {
      const response = await fetch(`/api/bills/${billId}/mark-paid`, {
        method: 'POST',
      });

      if (response.ok) {
        toast.success('Bill marked as paid!');
        void fetchBills();
        
        // Trigger automation event
        try {
          if (userData?.household_id) {
            await postEventTypes.billPaid({ 
              household_id: userData.household_id,
              bill_id: billId 
            });
          }
        } catch (error) {
          console.error('Failed to trigger automation event:', error);
        }
      } else {
        toast.error('Failed to mark bill as paid');
      }
    } catch (error) {
      console.error('Error marking bill as paid:', error);
      toast.error('Something went wrong');
    }
  }, [fetchBills, userData?.household_id]);

  const handleTestAutomation = useCallback(async () => {
    try {
      if (!userData?.household_id) {
        toast.error('Please complete onboarding first');
        return;
      }
      
      // Trigger a test bill event
      await postEventTypes.billEmailReceived({
        household_id: userData.household_id,
        subject: 'Test Electricity Bill',
        amount: 89.50,
        due_date: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        vendor: 'Origin Energy'
      });
      
      toast.success('Test automation event triggered! Check the Inbox for details.');
    } catch (error) {
      console.error('Error triggering test automation:', error);
      toast.error('Failed to trigger test automation');
    }
  }, [userData?.household_id]);

  const getStatusColor = useCallback((status: Bill['status']) => {
    switch (status) {
      case 'paid': return 'text-green-600 bg-green-100';
      case 'overdue': return 'text-red-600 bg-red-100';
      case 'pending': return 'text-yellow-600 bg-yellow-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  }, []);

  const getPriorityColor = useCallback((priority: Bill['priority']) => {
    switch (priority) {
      case 'urgent': return 'text-red-600 bg-red-100';
      case 'high': return 'text-orange-600 bg-orange-100';
      case 'medium': return 'text-yellow-600 bg-yellow-100';
      case 'low': return 'text-green-600 bg-green-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  }, []);

  const pendingCount = useMemo(
    () => bills.filter((bill) => bill.status === 'pending').length,
    [bills],
  );

  const overdueCount = useMemo(
    () => bills.filter((bill) => bill.status === 'overdue').length,
    [bills],
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading bills...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">üí∞ Bills</h1>
              <p className="text-gray-600">Manage your household bills and automate bill tracking</p>
            </div>
            <div className="flex space-x-3">
              <button
                onClick={handleTestAutomation}
                className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700 transition-colors"
              >
                üß™ Test Automation
              </button>
              <button
                onClick={() => setShowCreateForm(!showCreateForm)}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
              >
                {showCreateForm ? 'Cancel' : '+ Add Bill'}
              </button>
            </div>
          </div>
        </div>

        {/* Create Bill Form */}
        {showCreateForm && (
          <div className="bg-white rounded-lg shadow p-6 mb-8">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Create New Bill</h2>
            <form onSubmit={handleCreateBill} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
                    Bill Title *
                  </label>
                  <input
                    type="text"
                    id="title"
                    required
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="e.g., Electricity Bill - January 2024"
                  />
                </div>
                
                <div>
                  <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">
                    Amount (AUD) *
                  </label>
                  <input
                    type="number"
                    id="amount"
                    required
                    step="0.01"
                    min="0"
                    value={formData.amount}
                    onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="89.50"
                  />
                </div>
                
                <div>
                  <label htmlFor="due_date" className="block text-sm font-medium text-gray-700 mb-1">
                    Due Date *
                  </label>
                  <input
                    type="date"
                    id="due_date"
                    required
                    value={formData.due_date}
                    onChange={(e) => setFormData({ ...formData, due_date: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                
                <div>
                  <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
                    Category
                  </label>
                  <select
                    id="category"
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="General">General</option>
                    <option value="Utilities">Utilities</option>
                    <option value="Insurance">Insurance</option>
                    <option value="Rent/Mortgage">Rent/Mortgage</option>
                    <option value="Subscriptions">Subscriptions</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
                
                <div>
                  <label htmlFor="priority" className="block text-sm font-medium text-gray-700 mb-1">
                    Priority
                  </label>
                  <select
                    id="priority"
                    value={formData.priority}
                    onChange={(e) => setFormData({ ...formData, priority: e.target.value as any })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                    <option value="urgent">Urgent</option>
                  </select>
                </div>
                
                <div className="md:col-span-2">
                  <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea
                    id="description"
                    rows={3}
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Optional description or notes about this bill"
                  />
                </div>
              </div>
              
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setShowCreateForm(false)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={creatingBill}
                  className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  {creatingBill ? 'Creating...' : 'Create Bill'}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Bills List */}
        <div className="bg-white rounded-lg shadow">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-900">Your Bills</h2>
            <p className="text-sm text-gray-600 mt-1">
              {bills.length} bill{bills.length !== 1 ? 's' : ''} ‚Ä¢ 
              {pendingCount} pending ‚Ä¢ 
              {overdueCount} overdue
            </p>
          </div>
          
          {bills.length === 0 ? (
            <div className="px-6 py-12 text-center">
              <div className="text-gray-400 text-6xl mb-4">üí∞</div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No bills yet</h3>
              <p className="text-gray-600 mb-4">
                Create your first bill to get started with automated bill management.
              </p>
              <button
                onClick={() => setShowCreateForm(true)}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
              >
                Create Your First Bill
              </button>
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {bills.map((bill) => (
                <div key={bill.id} className="px-6 py-4 hover:bg-gray-50 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <h3 className="text-lg font-medium text-gray-900">{bill.title}</h3>
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(bill.status)}`}>
                          {bill.status}
                        </span>
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPriorityColor(bill.priority)}`}>
                          {bill.priority}
                        </span>
                      </div>
                      
                      {bill.description && (
                        <p className="text-gray-600 text-sm mb-2">{bill.description}</p>
                      )}
                      
                      <div className="flex items-center space-x-6 text-sm text-gray-500">
                        <span>Amount: <span className="font-medium text-gray-900">${bill.amount}</span></span>
                        <span>Due: <span className="font-medium text-gray-900">{new Date(bill.due_date).toLocaleDateString()}</span></span>
                        {bill.category && <span>Category: <span className="font-medium text-gray-900">{bill.category}</span></span>}
                        <span>Source: <span className="font-medium text-gray-900">{bill.source}</span></span>
                      </div>
                    </div>
                    
                    <div className="flex items-center space-x-2">
                      {bill.status === 'pending' && (
                        <button
                          onClick={() => handleMarkAsPaid(bill.id)}
                          className="px-3 py-1 bg-green-600 text-white text-sm rounded-md hover:bg-green-700 transition-colors"
                        >
                          Mark Paid
                        </button>
                      )}
                      <button 
                        onClick={() => router.push(`/bills/${bill.id}/edit`)}
                        className="px-3 py-1 border border-gray-300 text-gray-700 text-sm rounded-md hover:bg-gray-50 transition-colors"
                      >
                        Edit
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Automation Info */}
        <div className="mt-8 bg-blue-50 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-blue-900 mb-3">ü§ñ Automation Features</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-blue-800">
            <div>
              <h4 className="font-medium mb-1">üìß Email Integration</h4>
              <p>Automatically create bills from email receipts</p>
            </div>
            <div>
              <h4 className="font-medium mb-1">‚è∞ Smart Reminders</h4>
              <p>Get notified before bills are due</p>
            </div>
            <div>
              <h4 className="font-medium mb-1">üìä Status Tracking</h4>
              <p>Automatically mark bills as overdue</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useCallback, useEffect, useMemo, useState } from 'react';
import { useAuth } from '@clerk/nextjs';
import { useUserData } from '@/hooks/useUserData';
import { toast } from 'sonner';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

import {
  Plus,
  Clock,
  Brain,
  Lightbulb,
  Target,
  Users,
  Zap,
  Sparkles,
  RotateCcw,
  CheckCircle,
  X,
  BarChart3,
  TrendingUp,
} from 'lucide-react';

interface Chore {
  id: string;
  title: string;
  description?: string;
  assigned_to?: string;
  due_at?: string;
  recurrence?: string;
  created_by: string;
  household_id: string;
  category: string;
  priority: 'low' | 'medium' | 'high' | 'urgent';
  status: 'pending' | 'assigned' | 'in_progress' | 'completed' | 'skipped';
  ai_difficulty_rating: number;
  ai_estimated_duration: number;
  ai_preferred_time: string;
  ai_energy_level: 'low' | 'medium' | 'high';
  ai_skill_requirements: string[];
  ai_confidence: number;
  ai_suggested: boolean;
  created_at: string;
  updated_at: string;
}

interface AIChoreInsights {
  total_chores: number;
  pending_chores: number;
  completed_chores: number;
  ai_suggested_chores: number;
  average_difficulty: number;
  average_duration: number;
  fairness_score: number;
  household_patterns: string[];
  suggested_improvements: string[];
  ai_learning_progress: number;
  optimal_scheduling: string[];
  skill_gaps: string[];
  energy_distribution: {
    low: number;
    medium: number;
    high: number;
  };
  category_breakdown: Record<string, number>;
  user_workload_distribution: Record<string, number>;
  completion_efficiency: number;
}

type AssignmentStrategy = 'auto' | 'round_robin' | 'fairness' | 'preference' | 'ai_hybrid' | 'manual';

type HouseholdMember = {
  userId: string;
  name: string | null;
  email: string | null;
  role: string | null;
};

type AssignmentRecommendation = {
  strategy: AssignmentStrategy;
  confidence: number;
  reasoning: string;
};

const isAssignmentStrategy = (value: string): value is AssignmentStrategy =>
  ['auto', 'round_robin', 'fairness', 'preference', 'ai_hybrid', 'manual'].includes(value);

export default function ChoresPage() {
  const { userId } = useAuth();
  const { userData } = useUserData();
  const [chores, setChores] = useState<Chore[]>([]);
  const [aiInsights, setAiInsights] = useState<AIChoreInsights | null>(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [householdId, setHouseholdId] = useState<string | null>(null);

  const [choreForm, setChoreForm] = useState({
    title: '',
    description: '',
    category: 'general',
    due_at: '',
    priority: 'medium' as Chore['priority'],
    ai_estimated_duration: 30,
    recurrence_type: 'none',
    recurrence_day: 1,
    recurrence_interval: 1,
    custom_rrule: '',
    assignment_strategy: 'auto' as AssignmentStrategy,
    assigned_to: ''
  });

  const [assignmentLoading, setAssignmentLoading] = useState(false);
  const [assignmentRecommendations, setAssignmentRecommendations] = useState<AssignmentRecommendation[]>([]);
  const [householdMembers, setHouseholdMembers] = useState<HouseholdMember[]>([]);
  const [showRecommendations, setShowRecommendations] = useState(false);

  // Get household ID from user data
  useEffect(() => {
    if (userData?.household_id) {
      setHouseholdId(userData.household_id);
    }
  }, [userData?.household_id]);

  const fetchChores = useCallback(async () => {
    if (!householdId) {
      setLoading(false);
      return;
    }

    try {
      const response = await fetch('/api/chores');
      if (response.ok) {
        const data = await response.json();
        setChores(data.data?.chores || []);
      }
    } catch (error) {
      console.error('Error fetching chores:', error);
      toast.error('Failed to load chores');
    } finally {
      setLoading(false);
    }
  }, [householdId]);

  const fetchHouseholdMembers = useCallback(async () => {
    if (!householdId) {
      return;
    }

    try {
      const response = await fetch('/api/household-members');
      if (!response.ok) {
        throw new Error('Failed to fetch household members');
      }

      const data: { members: HouseholdMember[] } = await response.json();
      setHouseholdMembers(data.members ?? []);
    } catch (error) {
      console.error('Error fetching household members:', error);
      toast.error('Failed to load household members');
    }
  }, [householdId]);

  const fetchAIInsights = useCallback(async () => {
    try {
      const response = await fetch('/api/ai/chore-insights');
      if (response.ok) {
        const data = await response.json();
        setAiInsights(data.insights);
      }
    } catch (error) {
      console.error('Error fetching AI insights:', error);
      toast.error('Failed to load AI insights');
    }
  }, []);

  // Chore completion and management functions
  const completeChore = useCallback(async (choreId: string) => {
    try {
      const response = await fetch(`/api/chores/completions`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: choreId,
          completed_by: userId,
          completion_notes: ''
        })
      });

      if (response.ok) {
        // Update local state
        setChores((prev) =>
          prev.map((chore) =>
            chore.id === choreId ? { ...chore, status: 'completed' as const } : chore
          )
        );
        void fetchChores();
      }
    } catch (error) {
      console.error('Error completing chore:', error);
    }
  }, [fetchChores, userId]);

  const updateChoreStatus = useCallback(async (choreId: string, newStatus: Chore['status']) => {
    try {
      const response = await fetch(`/api/chores/${choreId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus })
      });

      if (response.ok) {
        setChores((prev) =>
          prev.map((chore) =>
            chore.id === choreId ? { ...chore, status: newStatus } : chore
          )
        );
      }
    } catch (error) {
      console.error('Error updating chore status:', error);
    }
  }, []);

  const deleteChore = useCallback(async (choreId: string) => {
    if (!confirm('Are you sure you want to delete this chore?')) return;
    
    try {
      const response = await fetch(`/api/chores/${choreId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        setChores((prev) => prev.filter((chore) => chore.id !== choreId));
      }
    } catch (error) {
      console.error('Error deleting chore:', error);
    }
  }, []);

  // AI Assignment Functions
  const getAssignmentRecommendations = useCallback(async () => {
    if (!choreForm.title || !choreForm.category) {
      toast.error('Please fill in chore title and category first');
      return;
    }

    setAssignmentLoading(true);
    try {
      const response = await fetch(`/api/ai/chore-assignment?choreId=temp&householdId=${householdId}`);
      if (response.ok) {
        const data = await response.json();
        setAssignmentRecommendations(data.recommendations || []);
        setShowRecommendations(true);
      }
    } catch (error) {
      console.error('Error getting assignment recommendations:', error);
    } finally {
      setAssignmentLoading(false);
    }
  }, [choreForm.category, choreForm.title, householdId]);

  const executeAIAssignment = useCallback(async (strategy: string) => {
    try {
      const mockChore = {
        id: 'temp',
        title: choreForm.title,
        category: choreForm.category,
        ai_difficulty_rating: 50,
        ai_energy_level: 'medium',
        priority: choreForm.priority
      };

      const response = await fetch('/api/ai/chore-assignment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          chore: mockChore,
          strategy,
          householdId,
        })
      });

      if (response.ok) {
        const data = await response.json();
        // Update the form with the AI assignment
        setChoreForm((prev) => ({
          ...prev,
          assignment_strategy: strategy as typeof choreForm.assignment_strategy,
          assigned_to: data.assignment.assignedTo
        }));
        
        toast.success(`AI assigned chore using ${strategy} strategy!`);
        setShowRecommendations(false);
      }
    } catch (error) {
      console.error('Error executing AI assignment:', error);
    }
  }, [choreForm.category, choreForm.priority, choreForm.title, householdId]);

  // Calculate chore statistics
  const choreStats = useMemo(() => ({
    total: chores.length,
    pending: chores.filter(c => c.status === 'pending').length,
    inProgress: chores.filter(c => c.status === 'in_progress').length,
    completed: chores.filter(c => c.status === 'completed').length,
    overdue: chores.filter(c => c.due_at && new Date(c.due_at) < new Date() && c.status !== 'completed').length,
    highPriority: chores.filter(c => c.priority === 'high' || c.priority === 'urgent').length,
  }), [chores]);

  const completionRate = useMemo(
    () => (chores.length > 0 ? Math.round((choreStats.completed / chores.length) * 100) : 0),
    [choreStats.completed, chores.length]
  );

  const avgDifficulty = useMemo(
    () =>
      chores.length > 0
        ? Math.round(chores.reduce((sum, c) => sum + (c.ai_difficulty_rating || 50), 0) / chores.length)
        : 50,
    [chores]
  );

  const totalEstimatedTime = useMemo(
    () =>
      chores
        .filter((c) => c.status !== 'completed')
        .reduce((sum, c) => sum + (c.ai_estimated_duration || 30), 0),
    [chores]
  );

  // Generate RRULE based on recurrence settings
  const generateRRULE = () => {
    if (choreForm.recurrence_type === 'none') return null;
    if (choreForm.recurrence_type === 'custom') return choreForm.custom_rrule;
    
    let rrule = '';
    
    switch (choreForm.recurrence_type) {
      case 'daily':
        rrule = `FREQ=DAILY;INTERVAL=${choreForm.recurrence_interval}`;
        break;
      case 'weekly':
        rrule = `FREQ=WEEKLY;INTERVAL=${choreForm.recurrence_interval};BYDAY=${choreForm.recurrence_day}`;
        break;
      case 'monthly':
        rrule = `FREQ=MONTHLY;INTERVAL=${choreForm.recurrence_interval};BYMONTHDAY=${choreForm.recurrence_day}`;
        break;
    }
    
    return rrule;
  };

  // Handle chore creation
  const handleCreateChore = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      const rrule = generateRRULE();
    const choreData = {
        title: choreForm.title,
        description: choreForm.description,
        category: choreForm.category,
        due_at: choreForm.due_at || null,
        priority: choreForm.priority,
        ai_estimated_duration: choreForm.ai_estimated_duration,
        rrule: rrule,
        dtstart: choreForm.due_at ? new Date(choreForm.due_at).toISOString() : null,
        assignment_strategy: choreForm.assignment_strategy,
        assigned_to: choreForm.assignment_strategy === 'manual' ? choreForm.assigned_to : null
      };

      const response = await fetch('/api/chores', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(choreData),
      });

      if (response.ok) {
                 // Reset form and refresh chores
         setChoreForm({
           title: '',
           description: '',
           category: 'general',
           due_at: '',
           priority: 'medium',
           ai_estimated_duration: 30,
           recurrence_type: 'none',
           recurrence_day: 1,
           recurrence_interval: 1,
           custom_rrule: '',
           assignment_strategy: 'auto',
           assigned_to: ''
         });
        setActiveTab('overview');
        fetchChores();
      } else {
        console.error('Failed to create chore');
      }
    } catch (error) {
      console.error('Error creating chore:', error);
    }
  };

  useEffect(() => {
    if (userId && householdId) {
      void fetchChores();
      void fetchAIInsights();
      void fetchHouseholdMembers();
    } else if (userId && !householdId) {
      // User authenticated but householdId not loaded yet, keep loading
      setLoading(true);
    } else {
      // User not authenticated, stop loading
      setLoading(false);
    }
  }, [fetchAIInsights, fetchChores, fetchHouseholdMembers, householdId, userId]);

  // Redirect unauthenticated users
  if (!userId) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Target className="h-12 w-12 mx-auto mb-4 text-blue-500" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Authentication Required</h2>
          <p className="text-gray-600 mb-4">Please sign in to access the Smart Chores system.</p>
          <Button onClick={() => window.location.href = '/sign-in'}>
            Sign In
          </Button>
        </div>
      </div>
    );
  }

  

type AssignmentStrategy = 'auto' | 'round_robin' | 'fairness' | 'preference' | 'ai_hybrid' | 'manual';

type HouseholdMember = {
  userId: string;
  name: string | null;
  email: string | null;
  role: string | null;
};

type AssignmentRecommendation = {
  strategy: AssignmentStrategy;
  confidence: number;
  reasoning: string;
};

const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'bg-red-100 text-red-800';
      case 'high': return 'bg-orange-100 text-orange-800';
      case 'medium': return 'bg-blue-100 text-blue-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'assigned': return 'bg-purple-100 text-purple-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getDifficultyColor = (difficulty: number) => {
    if (difficulty >= 80) return 'text-red-600';
    if (difficulty >= 60) return 'text-orange-600';
    if (difficulty >= 40) return 'text-yellow-600';
    return 'text-green-600';
  };

  const getEnergyColor = (energy: string) => {
    switch (energy) {
      case 'high': return 'text-red-600';
      case 'medium': return 'text-yellow-600';
      case 'low': return 'text-green-600';
      default: return 'text-gray-600';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Target className="h-12 w-12 animate-pulse mx-auto mb-4 text-blue-500" />
          <p className="text-lg text-gray-600">Loading Smart Chores...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-4">
          <Target className="h-8 w-8 text-blue-500" />
          <h1 className="text-3xl font-bold text-gray-900">Smart Chore Management</h1>
          <Badge variant="secondary" className="bg-purple-100 text-purple-800">
            <Brain className="h-4 w-4 mr-1" />
            AI-Powered
          </Badge>
        </div>
        <p className="text-gray-600 text-lg">
          Intelligent chore assignment with AI fairness optimization and smart scheduling
        </p>
      </div>

      {/* AI Insights Summary */}
      {aiInsights && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Chores</CardTitle>
              <Target className="h-4 w-4 text-blue-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{aiInsights.total_chores}</div>
              <p className="text-xs text-gray-500">Chores created</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Pending</CardTitle>
              <Clock className="h-4 w-4 text-orange-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{aiInsights.pending_chores}</div>
              <p className="text-xs text-gray-500">Awaiting completion</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Fairness Score</CardTitle>
              <Users className="h-4 w-4 text-green-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{aiInsights.fairness_score}%</div>
              <p className="text-xs text-gray-500">Workload balance</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">AI Learning</CardTitle>
              <Brain className="h-4 w-4 text-purple-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{aiInsights.ai_learning_progress}%</div>
              <p className="text-xs text-gray-500">Pattern recognition</p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="chores">All Chores</TabsTrigger>
          <TabsTrigger value="ai-insights">AI Insights</TabsTrigger>
          <TabsTrigger value="suggestions">Smart Suggestions</TabsTrigger>
          <TabsTrigger value="create">Create Chore</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Zap className="h-5 w-5 text-yellow-500" />
                Quick Actions
              </CardTitle>
              <CardDescription>
                Manage your chores with AI-powered assistance
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex flex-wrap gap-3">
                <Button 
                  onClick={() => setActiveTab('create')}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Create Chore
                </Button>
                <Button 
                  variant="outline" 
                  className="border-purple-200 text-purple-700 hover:bg-purple-50"
                  onClick={() => setActiveTab('suggestions')}
                >
                  <Lightbulb className="h-4 w-4 mr-2" />
                  AI Suggestions
                </Button>
                <Button 
                  variant="outline" 
                  className="border-green-200 text-green-700 hover:bg-green-50"
                  onClick={() => setActiveTab('ai-insights')}
                >
                  <Brain className="h-4 w-4 mr-2" />
                  AI Insights
                </Button>
                <Button 
                  variant="outline" 
                  className="border-orange-200 text-orange-700 hover:bg-orange-50"
                  onClick={() => setActiveTab('chores')}
                >
                  <RotateCcw className="h-4 w-4 mr-2" />
                  Manage Chores
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Recent Chores */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="h-5 w-5" />
                Recent Chores
              </CardTitle>
              <CardDescription>
                Your latest chore activities and assignments
              </CardDescription>
            </CardHeader>
            <CardContent>
              {chores.slice(0, 5).length > 0 ? (
                <div className="space-y-4">
                  {chores.slice(0, 5).map((chore) => (
                    <div key={chore.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <h4 className="font-medium">{chore.title}</h4>
                          {chore.ai_suggested && (
                            <Badge variant="secondary" className="bg-purple-100 text-purple-800 text-xs">
                              <Sparkles className="h-3 w-3 mr-1" />
                              AI Suggested
                            </Badge>
                          )}
                          <Badge className={getPriorityColor(chore.priority)}>
                            {chore.priority}
                          </Badge>
                          <Badge variant="outline" className={getStatusColor(chore.status)}>
                            {chore.status}
                          </Badge>
                        </div>
                        <div className="flex items-center gap-4 text-sm text-gray-600">
                          <span className={getDifficultyColor(chore.ai_difficulty_rating)}>
                            Difficulty: {chore.ai_difficulty_rating}%
                          </span>
                          <span>{chore.ai_estimated_duration} min</span>
                          <span className={getEnergyColor(chore.ai_energy_level)}>
                            {chore.ai_energy_level} energy
                          </span>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="text-sm text-gray-600 mb-1">Category</div>
                        <Badge variant="outline">{chore.category}</Badge>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-center py-8">
                  No chores yet. Create your first chore to get started!
                </p>
              )}
            </CardContent>
          </Card>

          {/* AI Learning Progress */}
          {aiInsights && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Brain className="h-5 w-5 text-purple-500" />
                  AI Learning Progress
                </CardTitle>
                <CardDescription>
                  How well the AI understands your household patterns
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex justify-between text-sm">
                    <span>Pattern Recognition</span>
                    <span>{aiInsights.ai_learning_progress}%</span>
                  </div>
                  <Progress value={aiInsights.ai_learning_progress} />
                  <p className="text-sm text-gray-600">
                    The AI is learning from your chore patterns to provide better assignments and suggestions
                  </p>
                </div>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* All Chores Tab */}
        <TabsContent value="chores" className="space-y-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold">All Chores</h2>
            <Button 
              onClick={() => setActiveTab('create')}
              className="bg-blue-600 hover:bg-blue-700"
            >
              <Plus className="h-4 w-4 mr-2" />
              New Chore
            </Button>
          </div>

          {chores.length > 0 ? (
            <div className="space-y-4">
              {chores.map((chore) => (
                <Card key={chore.id} className="hover:shadow-md transition-shadow">
                  <CardContent className="p-6">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <h4 className="font-medium text-lg">{chore.title}</h4>
                          {chore.ai_suggested && (
                            <Badge variant="secondary" className="bg-purple-100 text-purple-800 text-xs">
                              <Sparkles className="h-3 w-3 mr-1" />
                              AI Suggested
                            </Badge>
                          )}
                          <Badge className={getPriorityColor(chore.priority)}>
                            {chore.priority}
                          </Badge>
                          <Badge variant="outline" className={getStatusColor(chore.status)}>
                            {chore.status}
                          </Badge>
                        </div>
                        
                        {chore.description && (
                          <p className="text-gray-600 mb-3">{chore.description}</p>
                        )}
                        
                        <div className="flex items-center gap-4 text-sm text-gray-600 mb-4">
                          <span className={getDifficultyColor(chore.ai_difficulty_rating)}>
                            Difficulty: {chore.ai_difficulty_rating}%
                          </span>
                          <span>{chore.ai_estimated_duration} min</span>
                          <span className={getEnergyColor(chore.ai_energy_level)}>
                            {chore.ai_energy_level} energy
                          </span>
                          <span>Category: {chore.category}</span>
                        </div>
                        
                        {chore.due_at && (
                          <div className="text-sm text-gray-600 mb-4">
                            <Clock className="h-4 w-4 inline mr-1" />
                            Due: {new Date(chore.due_at).toLocaleDateString()}
                          </div>
                        )}
                      </div>
                      
                      <div className="flex flex-col gap-2 ml-4">
                        {chore.status === 'pending' && (
                          <Button
                            size="sm"
                            onClick={() => completeChore(chore.id)}
                            className="bg-green-600 hover:bg-green-700"
                          >
                            <CheckCircle className="h-4 w-4 mr-1" />
                            Complete
                          </Button>
                        )}
                        
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => updateChoreStatus(chore.id, 'in_progress')}
                          disabled={chore.status === 'completed'}
                        >
                          <Clock className="h-4 w-4 mr-1" />
                          Start
                        </Button>
                        
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => deleteChore(chore.id)}
                        >
                          <X className="h-4 w-4 mr-1" />
                          Delete
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <Card>
              <CardContent className="text-center py-12">
                <Target className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No chores yet</h3>
                <p className="text-gray-600 mb-4">
                  Create your first chore to start managing household tasks
                </p>
                <Button 
                  onClick={() => setActiveTab('create')}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Create First Chore
                </Button>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="ai-insights" className="space-y-6">
          {aiInsights ? (
            <>
              {/* AI Learning Progress */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Brain className="h-5 w-5 text-purple-500" />
                    AI Learning Progress
                  </CardTitle>
                  <CardDescription>
                    How well the AI understands your household patterns
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between text-sm">
                      <span>Pattern Recognition</span>
                      <span>{aiInsights.ai_learning_progress}%</span>
                    </div>
                    <Progress value={aiInsights.ai_learning_progress} />
                    <p className="text-sm text-gray-600">
                      The AI is learning from your chore patterns to provide better assignments and suggestions
                    </p>
                  </div>
                </CardContent>
              </Card>

              {/* Workload Distribution */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Users className="h-5 w-5 text-green-500" />
                    Workload Distribution
                  </CardTitle>
                  <CardDescription>
                    How chores are distributed among household members
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {Object.entries(aiInsights.user_workload_distribution).map(([user, count]) => (
                      <div key={user} className="flex items-center justify-between">
                        <span className="text-sm font-medium">{user}</span>
                        <div className="flex items-center gap-2">
                          <div className="w-32 bg-gray-200 rounded-full h-2">
                            <div 
                              className="bg-green-500 h-2 rounded-full" 
                              style={{ width: `${(count / Math.max(...Object.values(aiInsights.user_workload_distribution))) * 100}%` }}
                            />
                          </div>
                          <span className="text-sm text-gray-600">{count}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Category Breakdown */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <BarChart3 className="h-5 w-5 text-blue-500" />
                    Category Breakdown
                  </CardTitle>
                  <CardDescription>
                    Distribution of chores by category
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {Object.entries(aiInsights.category_breakdown).map(([category, count]) => (
                      <div key={category} className="flex items-center justify-between">
                        <span className="text-sm font-medium capitalize">{category}</span>
                        <div className="flex items-center gap-2">
                          <div className="w-32 bg-gray-200 rounded-full h-2">
                            <div 
                              className="bg-blue-500 h-2 rounded-full" 
                              style={{ width: `${(count / Math.max(...Object.values(aiInsights.category_breakdown))) * 100}%` }}
                            />
                          </div>
                          <span className="text-sm text-gray-600">{count}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Energy Distribution */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Zap className="h-5 w-5 text-yellow-500" />
                    Energy Level Distribution
                  </CardTitle>
                  <CardDescription>
                    Distribution of chores by energy requirement
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-3 gap-4">
                    <div className="text-center">
                      <div className="text-2xl font-bold text-green-600">{aiInsights.energy_distribution.low}</div>
                      <div className="text-sm text-gray-600">Low Energy</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-yellow-600">{aiInsights.energy_distribution.medium}</div>
                      <div className="text-sm text-gray-600">Medium Energy</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-red-600">{aiInsights.energy_distribution.high}</div>
                      <div className="text-sm text-gray-600">High Energy</div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Completion Efficiency */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <TrendingUp className="h-5 w-5 text-green-500" />
                    Completion Efficiency
                  </CardTitle>
                  <CardDescription>
                    How efficiently chores are being completed
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between text-sm">
                      <span>Efficiency Score</span>
                      <span>{aiInsights.completion_efficiency}%</span>
                    </div>
                    <Progress value={aiInsights.completion_efficiency} />
                    <p className="text-sm text-gray-600">
                      Higher scores indicate better chore completion patterns
                    </p>
                  </div>
                </CardContent>
              </Card>
            </>
          ) : (
            <Card>
              <CardContent className="text-center py-12">
                <Brain className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">AI Insights Loading</h3>
                <p className="text-gray-600">
                  Creating your first chore to generate AI insights
                </p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="suggestions" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Lightbulb className="h-5 w-5 text-yellow-500" />
                Smart Suggestions
              </CardTitle>
              <CardDescription>
                AI-powered recommendations to improve your chore management
              </CardDescription>
            </CardHeader>
            <CardContent>
              {aiInsights?.suggested_improvements && aiInsights.suggested_improvements.length > 0 ? (
                <div className="space-y-4">
                  {aiInsights.suggested_improvements.map((suggestion, index) => (
                    <div key={index} className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                      <div className="flex items-start gap-3">
                        <Lightbulb className="h-5 w-5 text-blue-500 mt-0.5 flex-shrink-0" />
                        <p className="text-sm text-blue-800">{suggestion}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Lightbulb className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No suggestions yet</h3>
                  <p className="text-gray-600">
                    Create more chores to get personalized AI recommendations
                  </p>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Optimal Scheduling */}
          {aiInsights?.optimal_scheduling && aiInsights.optimal_scheduling.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Clock className="h-5 w-5 text-green-500" />
                  Optimal Scheduling
                </CardTitle>
                <CardDescription>
                  AI-recommended times for different types of chores
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {aiInsights.optimal_scheduling.map((schedule, index) => (
                    <div key={index} className="p-3 bg-green-50 rounded-lg border border-green-200">
                      <p className="text-sm text-green-800">{schedule}</p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Skill Gaps */}
          {aiInsights?.skill_gaps && aiInsights.skill_gaps.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Target className="h-5 w-5 text-orange-500" />
                  Skill Gaps
                </CardTitle>
                <CardDescription>
                  Areas where household members could improve their skills
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {aiInsights.skill_gaps.map((skill, index) => (
                    <div key={index} className="p-3 bg-orange-50 rounded-lg border border-orange-200">
                      <p className="text-sm text-orange-800">{skill}</p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Household Patterns */}
          {aiInsights?.household_patterns && aiInsights.household_patterns.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <TrendingUp className="h-5 w-5 text-purple-500" />
                  Household Patterns
                </CardTitle>
                <CardDescription>
                  Patterns the AI has identified in your household's chore habits
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {aiInsights.household_patterns.map((pattern, index) => (
                    <div key={index} className="p-3 bg-purple-50 rounded-lg border border-purple-200">
                      <p className="text-sm text-purple-800">{pattern}</p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="create" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Plus className="h-5 w-5 text-blue-500" />
                Create New Chore
              </CardTitle>
              <CardDescription>
                Create a new chore with smart scheduling and assignment
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleCreateChore} className="space-y-6">
                {/* Basic Information */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Chore Title *
                    </label>
                    <input
                      type="text"
                      value={choreForm.title}
                      onChange={(e) => setChoreForm({...choreForm, title: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="e.g., Clean Kitchen"
                      required
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Category
                    </label>
                    <select
                      value={choreForm.category}
                      onChange={(e) => setChoreForm({...choreForm, category: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="general">General</option>
                      <option value="kitchen">Kitchen</option>
                      <option value="bathroom">Bathroom</option>
                      <option value="laundry">Laundry</option>
                      <option value="cleaning">Cleaning</option>
                      <option value="maintenance">Maintenance</option>
                      <option value="garden">Garden</option>
                      <option value="shopping">Shopping</option>
                    </select>
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea
                    value={choreForm.description}
                    onChange={(e) => setChoreForm({...choreForm, description: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    rows={3}
                    placeholder="Describe what needs to be done..."
                  />
                </div>

                {/* Scheduling */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Due Date
                    </label>
                    <input
                      type="datetime-local"
                      value={choreForm.due_at}
                      onChange={(e) => setChoreForm({...choreForm, due_at: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Priority
                    </label>
                    <select
                      value={choreForm.priority}
                      onChange={(e) => setChoreForm({...choreForm, priority: e.target.value as any})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                      <option value="urgent">Urgent</option>
                    </select>
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Estimated Duration (min)
                    </label>
                    <input
                      type="number"
                      value={choreForm.ai_estimated_duration}
                      onChange={(e) => setChoreForm({...choreForm, ai_estimated_duration: parseInt(e.target.value) || 30})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      min="5"
                      max="480"
                    />
                  </div>
                </div>

                {/* Recurrence Settings */}
                <div className="border-t pt-6">
                  <h4 className="text-lg font-medium text-gray-900 mb-4">Recurrence Settings</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Recurrence Type
                      </label>
                      <select
                        value={choreForm.recurrence_type}
                        onChange={(e) => setChoreForm({...choreForm, recurrence_type: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="none">No Recurrence</option>
                        <option value="daily">Daily</option>
                        <option value="weekly">Weekly</option>
                        <option value="monthly">Monthly</option>
                        <option value="custom">Custom RRULE</option>
                      </select>
                    </div>
                    
                    {choreForm.recurrence_type === 'weekly' && (
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Day of Week
                        </label>
                        <select
                          value={choreForm.recurrence_day}
                          onChange={(e) => setChoreForm({...choreForm, recurrence_day: parseInt(e.target.value) || 1})}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                          <option value="1">Monday</option>
                          <option value="2">Tuesday</option>
                          <option value="3">Wednesday</option>
                          <option value="4">Thursday</option>
                          <option value="5">Friday</option>
                          <option value="6">Saturday</option>
                          <option value="0">Sunday</option>
                        </select>
                      </div>
                    )}
                    
                    {choreForm.recurrence_type === 'monthly' && (
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Day of Month
                        </label>
                        <input
                          type="number"
                          value={choreForm.recurrence_day}
                          onChange={(e) => setChoreForm({...choreForm, recurrence_day: parseInt(e.target.value) || 1})}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                          min="1"
                          max="31"
                        />
                      </div>
                    )}
                    
                    {choreForm.recurrence_type !== 'none' && (
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Interval
                        </label>
                        <input
                          type="number"
                          value={choreForm.recurrence_interval}
                          onChange={(e) => setChoreForm({...choreForm, recurrence_interval: parseInt(e.target.value) || 1})}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                          min="1"
                          max="52"
                          placeholder="Every X days/weeks/months"
                        />
                      </div>
                    )}
                  </div>
                  
                  {choreForm.recurrence_type === 'custom' && (
                    <div className="mt-4">
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Custom RRULE
                      </label>
                      <input
                        type="text"
                        value={choreForm.custom_rrule}
                        onChange={(e) => setChoreForm({...choreForm, custom_rrule: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="FREQ=WEEKLY;BYDAY=MO,WE,FR"
                      />
                      <p className="text-xs text-gray-500 mt-1">
                        Use iCalendar RRULE format (e.g., FREQ=WEEKLY;BYDAY=MO,WE,FR for every Monday, Wednesday, Friday)
                      </p>
                    </div>
                  )}
                </div>

                {/* Smart Assignment */}
                <div className="border-t pt-6">
                  <h4 className="text-lg font-medium text-gray-900 mb-4">Smart Assignment</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Assignment Strategy
                      </label>
                      <select
                        value={choreForm.assignment_strategy}
                        onChange={(e) => {
                          const { value } = e.target;
                          setChoreForm((prev) => ({
                            ...prev,
                            assignment_strategy: isAssignmentStrategy(value) ? value : prev.assignment_strategy,
                          }));
                        }}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="auto">Auto-assign (AI-powered)</option>
                        <option value="round_robin">Round Robin</option>
                        <option value="fairness">Fairness-based</option>
                        <option value="preference">Preference-based</option>
                        <option value="ai_hybrid">AI Hybrid</option>
                        <option value="manual">Manual Assignment</option>
                      </select>
                    </div>
                    
                    {choreForm.assignment_strategy === 'manual' && (
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Assign To
                        </label>
                        <select
                        value={choreForm.assigned_to}
                        onChange={(e) => setChoreForm({ ...choreForm, assigned_to: e.target.value })}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                        <option value="">Select household member</option>
                        {householdMembers.map((member) => (
                          <option key={member.userId} value={member.userId}>
                            {member.name || member.email || member.userId}
                          </option>
                        ))}
                        </select>
                      </div>
                    )}
                  </div>
                  
                  {/* AI Assignment Recommendations */}
                  {choreForm.assignment_strategy !== 'manual' && (
                    <div className="mt-4">
                      <Button
                        type="button"
                        variant="outline"
                        onClick={getAssignmentRecommendations}
                        disabled={assignmentLoading}
                        className="w-full"
                      >
                        {assignmentLoading ? (
                          <>
                            <Brain className="h-4 w-4 mr-2 animate-spin" />
                            Getting AI Recommendations...
                          </>
                        ) : (
                          <>
                            <Brain className="h-4 w-4 mr-2" />
                            Get AI Assignment Recommendations
                          </>
                        )}
                      </Button>
                      
                      {showRecommendations && assignmentRecommendations.length > 0 && (
                        <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                          <h5 className="font-medium text-gray-900 mb-3">AI Recommendations</h5>
                          <div className="space-y-3">
                            {assignmentRecommendations.map((rec, index) => (
                              <div key={index} className="flex items-center justify-between p-3 bg-white rounded border">
                                <div className="flex-1">
                                  <div className="flex items-center gap-2 mb-1">
                                    <Badge variant="secondary" className="bg-blue-100 text-blue-800">
                                      {rec.strategy}
                                    </Badge>
                                    <span className="text-sm font-medium">
                                      Confidence: {rec.confidence}%
                                    </span>
                                  </div>
                                  <p className="text-sm text-gray-600">{rec.reasoning}</p>
                                </div>
                                <Button
                                  type="button"
                                  size="sm"
                                  onClick={() => executeAIAssignment(rec.strategy)}
                                  className="ml-3"
                                >
                                  Use This
                                </Button>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                {/* Submit Button */}
                <div className="flex justify-end space-x-3 pt-6 border-t">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setActiveTab('overview')}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="bg-blue-600 hover:bg-blue-700"
                    disabled={!choreForm.title.trim()}
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Create Chore
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

'use client';

import { useCallback, useEffect, useState } from 'react';
import { useUserData } from '@/hooks/useUserData';
import ConflictDetection from '@/components/ConflictDetection';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { AlertCircle, Info, AlertTriangle, Clock, Users, Calendar } from 'lucide-react';

export default function ConflictsPage() {
  const { userData, isLoading } = useUserData();
  const [entitlements, setEntitlements] = useState<Record<string, unknown> | null>(null);
  const [entitlementsLoading, setEntitlementsLoading] = useState(true);
  const householdId = userData?.household_id;

  const loadEntitlements = useCallback(async () => {
    if (!householdId) {
      return;
    }

    try {
      setEntitlementsLoading(true);
      const response = await fetch(`/api/entitlements/${householdId}`);
      const data = await response.json();
      
      if (response.ok) {
        setEntitlements(data);
      } else {
        console.error('Failed to load entitlements:', data.error);
      }
    } catch (error) {
      console.error('Error loading entitlements:', error);
    } finally {
      setEntitlementsLoading(false);
    }
  }, [householdId]);

  useEffect(() => {
    if (householdId) {
      void loadEntitlements();
    }
  }, [householdId, loadEntitlements]);

  if (isLoading || entitlementsLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                <span className="ml-2 text-gray-600">Loading...</span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!userData?.household_id) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <Card>
            <CardContent className="p-6">
              <div className="text-center">
                <AlertCircle className="h-12 w-12 mx-auto mb-4 text-red-500" />
                <h2 className="text-xl font-semibold text-gray-900 mb-2">
                  Household Required
                </h2>
                <p className="text-gray-600">
                  You need to be part of a household to access conflict detection features.
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
