import { auth } from '@clerk/nextjs/server';
import { redirect } from 'next/navigation';
import { createServerComponentClient } from '@supabase/auth-helpers-nextjs';
import { cookies } from 'next/headers';
import InboxEvents from '@/components/Inbox/InboxEvents';

export default async function InboxPage() {
  const { userId } = await auth();
  
  if (!userId) {
    redirect('/sign-in');
  }

  const supabase = createServerComponentClient({ cookies });
  
  // Get user's household - try both clerk_id and id patterns
  let { data: userData } = await supabase
    .from('users')
    .select('household_id, id, onboarding_completed')
    .eq('clerk_id', userId)
    .single();
    
  // If not found by clerk_id, try by id (for users with user_ prefix)
  if (!userData) {
    const { data: userDataById } = await supabase
      .from('users')
      .select('household_id, id, onboarding_completed')
      .eq('id', userId)
      .single();
    userData = userDataById;
  }
  
  // Debug logging
  console.log('Inbox page - User ID:', userId);
  console.log('Inbox page - User data found:', userData);

  if (!userData?.household_id) {
    // Debug information to help troubleshoot
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Inbox</h1>
            <p className="text-gray-600 mb-4">
              View automation events and system notifications for your household
            </p>
            <div className="bg-red-50 border border-red-200 rounded-md p-4">
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-red-800">
                    Debug: User Data Issue
                  </h3>
                  <div className="mt-2 text-sm text-red-700">
                    <p>
                      <strong>User ID:</strong> {userId}<br/>
                      <strong>User Data Found:</strong> {userData ? 'Yes' : 'No'}<br/>
                      <strong>Household ID:</strong> {userData?.household_id || 'Missing'}<br/>
                      <strong>Onboarding Completed:</strong> {userData?.onboarding_completed ? 'Yes' : 'No'}<br/>
                    </p>
                    <p className="mt-2">
                      This suggests there's a mismatch between your Clerk user ID and the database record.
                    </p>
                  </div>
                  <div className="mt-4 space-x-2">
                    <a
                      href="/onboarding"
                      className="bg-red-800 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                    >
                      Go to Onboarding
                    </a>
                    <a
                      href="/dashboard"
                      className="bg-gray-800 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
                    >
                      Go to Dashboard
                    </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Inbox</h1>
          <p className="text-gray-600">
            View automation events and system notifications for your household
          </p>
        </div>
        
        <InboxEvents householdId={userData.household_id} />
      </div>
    </div>
  );
}

'use client';

import { useAuth } from '@clerk/nextjs';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';

interface LeaderboardEntry {
  id: string;
  xp: number;
  username: string;
}

interface LeaderboardData {
  success: boolean;
  leaderboard: LeaderboardEntry[];
  householdId: string;
}

export default function LeaderboardPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    fetchLeaderboard();
  }, [isLoaded, isSignedIn, router]);

  async function fetchLeaderboard() {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('/api/leaderboard');
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch leaderboard');
      }

      const data: LeaderboardData = await response.json();
      setLeaderboard(data.leaderboard);
      
      console.log('‚úÖ Leaderboard fetched:', data);
    } catch (err) {
      console.error('‚ùå Error fetching leaderboard:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch leaderboard');
    } finally {
      setLoading(false);
    }
  }

  // Show loading spinner while auth is loading or data is being fetched
  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading leaderboard...</p>
        </div>
      </div>
    );
  }

  // This should not be reached if redirect is working, but just in case
  if (!isSignedIn) {
    return null;
  }

  // Show error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white shadow rounded-lg p-6 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
            <h1 className="text-xl font-semibold text-gray-900 mb-2">Error</h1>
            <p className="text-gray-600 mb-4">{error}</p>
            <button 
              onClick={() => {
          if (typeof window !== 'undefined') {
            window.location.reload();
          }
        }}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => router.back()}
            className="text-blue-600 hover:text-blue-700 mb-4 flex items-center"
          >
            ‚Üê Back
          </button>
          
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            üèÜ Household Leaderboard
          </h1>
          <p className="text-gray-600">
            Top 10 members by XP
          </p>
        </div>

        {/* Leaderboard */}
        <div className="bg-white rounded-lg shadow">
          {leaderboard.length === 0 ? (
            <div className="p-8 text-center">
              <div className="text-gray-400 text-6xl mb-4">üèÜ</div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No leaderboard data</h3>
              <p className="text-gray-600">Complete some tasks to see the leaderboard!</p>
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {leaderboard.map((entry, index) => (
                <div key={entry.id} className="p-6 flex items-center">
                  {/* Rank */}
                  <div className="flex-shrink-0 w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mr-4">
                    <span className="text-lg font-bold text-gray-700">
                      {index + 1}
                    </span>
                  </div>
                  
                  {/* User Info */}
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-gray-900">
                      {entry.username}
                    </h3>
                    <p className="text-sm text-gray-500">
                      {entry.xp} XP
                    </p>
                  </div>
                  
                  {/* Trophy for top 3 */}
                  {index < 3 && (
                    <div className="flex-shrink-0">
                      <span className="text-2xl">
                        {index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : 'ü•â'}
                      </span>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Refresh button */}
        <div className="mt-6 text-center">
          <button
            onClick={fetchLeaderboard}
            className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
          >
            Refresh Leaderboard
          </button>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useMemo } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { MealSlot, MealType, WeeklyPlannerProps } from './types';
import { MealSlotSelector } from './MealSlotSelector';

export function WeeklyPlanner({
  weekDays,
  mealPlan,
  recipes,
  onAssignRecipe,
  onAddWeekIngredients,
  onCopyWeek,
  onClearWeek,
  onNavigateWeek,
}: WeeklyPlannerProps) {
  const planByDay = useMemo(
    () =>
      weekDays.map((day) => ({
        ...day,
        meals: mealPlan?.meals?.[day.name] ?? {},
      })),
    [weekDays, mealPlan?.meals],
  );

  return (
    <Card>
      <CardHeader className="flex justify-between items-center">
        <CardTitle className="text-lg font-semibold">This Week&apos;s Plan</CardTitle>
        <div className="flex gap-2">
          <Button variant="outline" onClick={() => onNavigateWeek('prev')} size="sm">
            Previous Week
          </Button>
          <Button variant="outline" onClick={() => onNavigateWeek('next')} size="sm">
            Next Week
          </Button>
          <Button variant="outline" onClick={onCopyWeek} size="sm">
            Copy Last Week
          </Button>
          <Button variant="outline" onClick={onClearWeek} size="sm">
            Clear Week
          </Button>
          <Button onClick={onAddWeekIngredients} size="sm">
            Add Ingredients
          </Button>
        </div>
      </CardHeader>
      <CardContent className="grid grid-cols-1 gap-4">
        {planByDay.map((day) => (
          <div key={day.date} className="p-4 border rounded-lg space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-semibold">{day.label}</h3>
                <p className="text-sm text-gray-500">{day.date}</p>
              </div>
              <Badge variant="secondary">{day.meals ? Object.keys(day.meals).length : 0} meals</Badge>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {(MEAL_TYPES).map((mealType) => (
                <MealSlotSelector
                  key={`${day.date}-${mealType}`}
                  date={day.date}
                  mealType={mealType}
                  recipe={day.meals?.[mealType] ?? undefined}
                  recipes={recipes}
                  onAssignRecipe={(recipe) => onAssignRecipe(day.date, mealType, recipe)}
                />
              ))}
            </div>
          </div>
        ))}
      </CardContent>
    </Card>
  );
}

const MEAL_TYPES: MealType[] = ['breakfast', 'lunch', 'dinner'];

'use client';

import { useUserData } from '@/hooks/useUserData';
import { 
  useMealPlan
} from '@/hooks/useMealPlans';
import { 
  useRecipes
} from '@/hooks/useRecipes';

export default function MealPlannerPageRefactored() {
  // User data
  const { isLoading: userDataLoading } = useUserData();
  
  // React Query hooks for meal plans
  const { 
    data: mealPlansData, 
    isLoading: mealPlansLoading, 
    error: mealPlansError 
  } = useMealPlan();
  
  // React Query hooks for recipes
  const { 
    data: recipesData, 
    isLoading: recipesLoading, 
    error: recipesError 
  } = useRecipes();
  
  // Extract data from React Query
  const mealPlans = mealPlansData ? [mealPlansData] : [];
  const recipes = recipesData?.recipes || [];
  
  // Loading and error states
  const loading = userDataLoading || mealPlansLoading || recipesLoading;
  const error = mealPlansError || recipesError;
  
  // Loading state
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-lg text-gray-600">Loading Meal Planner...</p>
        </div>
      </div>
    );
  }
  
  // Error state
  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Error Loading Meal Planner</h2>
          <p className="text-gray-600 mb-6">
            {error.message || 'Failed to load meal planner data'}
          </p>
          <button 
            onClick={() => window.location.reload()}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }
  
  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Meal Planner</h1>
        <p className="text-gray-600">
          Plan your meals for the week and organize your recipes
        </p>
      </div>
      
      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Current Week</h3>
          <p className="text-3xl font-bold text-blue-500">
            {mealPlans.length > 0 ? 'Active' : 'No Plan'}
          </p>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Total Recipes</h3>
          <p className="text-3xl font-bold text-green-500">{recipes.length}</p>
        </div>
        
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Meals Planned</h3>
          <p className="text-3xl font-bold text-purple-500">
            {mealPlans.length > 0 ? 'This Week' : 'None'}
          </p>
        </div>
      </div>
      
      {/* Meal Plans List */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Current Week</h2>
        
        {mealPlans.length > 0 ? (
          <div className="space-y-4">
            {mealPlans.map((mealPlan) => (
              <div key={mealPlan.id} className="border rounded-lg p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="text-lg font-medium text-gray-900">Week of {new Date(mealPlan.week_start_date).toLocaleDateString()}</h3>
                    <p className="text-sm text-gray-500">
                      {Object.keys(mealPlan.meals).length} days with meals planned
                    </p>
                  </div>
                  
                  <div className="text-right">
                    <span className="px-2 py-1 rounded-full text-xs bg-green-100 text-green-800">
                      Active
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <p className="text-gray-500">No meal plan for this week. Use the main meal planner to create one!</p>
          </div>
        )}
      </div>
      
      {/* Recipes List */}
      <div className="bg-white rounded-lg shadow p-6 mt-8">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Your Recipes</h2>
        
        {recipes.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {recipes.map((recipe) => (
              <div key={recipe.id} className="border rounded-lg p-4">
                <h3 className="text-lg font-medium text-gray-900 mb-2">{recipe.name}</h3>
                {recipe.description && (
                  <p className="text-gray-600 text-sm mb-2">{recipe.description}</p>
                )}
                <div className="flex items-center gap-4 text-sm text-gray-500">
                  <span>Prep: {recipe.prep_time}m</span>
                  <span>Cook: {recipe.cook_time}m</span>
                  <span>Servings: {recipe.servings}</span>
                </div>
                <div className="mt-2">
                  <span className={`px-2 py-1 rounded-full text-xs ${
                    recipe.difficulty === 'easy' ? 'bg-green-100 text-green-800' :
                    recipe.difficulty === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {recipe.difficulty}
                  </span>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <p className="text-gray-500">No recipes yet. Use the main meal planner to create your first one!</p>
          </div>
        )}
      </div>
      
      {/* Note: This is just an example of the refactored structure */}
      <div className="mt-8 p-4 bg-blue-50 rounded-lg">
        <p className="text-blue-800 text-sm">
          <strong>Note:</strong> This is an example refactored component showing the basic structure. 
          For full functionality, use the main meal planner page.
        </p>
      </div>
    </div>
  );
}

