'use client';

import { useAuth, useUser } from '@clerk/nextjs';
import { useRouter } from 'next/navigation';
import { useState, useEffect } from 'react';
import { toast } from 'sonner';
import { z } from 'zod';

// Validation schemas
const HouseholdSchema = z.object({
  name: z.string().min(1, 'Household name is required').max(100, 'Name too long'),
});

const StarterDataSchema = z.object({
  loadSampleRecipes: z.boolean(),
  loadSamplePlannerItems: z.boolean(),
});

export default function OnboardingPage() {
  const { user } = useUser();
  const { isSignedIn } = useAuth();
  const router = useRouter();
  const [isLoading, setIsLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  
  // Step 1: Household data
  const [householdName, setHouseholdName] = useState('');
  const [memberCount] = useState(1);
  
  // Step 2: Starter data options
  const [loadSampleRecipes, setLoadSampleRecipes] = useState(true);
  const [loadSamplePlannerItems, setLoadSamplePlannerItems] = useState(true);

  useEffect(() => {
    if (user?.firstName && user?.lastName) {
      setHouseholdName(`${user.firstName}'s Household`);
    } else if (user?.firstName) {
      setHouseholdName(`${user.firstName}'s Household`);
    }
  }, [user]);

  // Handle authentication redirect in useEffect to avoid SSR issues
  useEffect(() => {
    if (typeof window !== 'undefined' && !isSignedIn) {
      router.push('/sign-in');
    }
  }, [isSignedIn, router]);

  // Don't render anything if not signed in (will redirect via useEffect)
  if (!isSignedIn) {
    return null;
  }

  const handleHouseholdStep = async () => {
    try {
      const validation = HouseholdSchema.safeParse({ name: householdName });
      if (!validation.success) {
        const firstIssue = validation.error.issues[0];
        if (firstIssue) {
          toast.error(firstIssue.message);
        } else {
          toast.error('Invalid household name');
        }
        return;
      }

      setIsLoading(true);
      
      const response = await fetch('/api/onboarding/household', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: householdName,
        }),
      });

      if (response.ok) {
        setCurrentStep(2);
        toast.success('Household setup complete!');
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to setup household');
      }
    } catch (error) {
      console.error('Error setting up household:', error);
      toast.error('Something went wrong. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

    const handleStarterDataStep = async () => {
    try {
      const validation = StarterDataSchema.safeParse({
        loadSampleRecipes,
        loadSamplePlannerItems,
      });

      if (!validation.success) {
        toast.error('Invalid starter data options');
        return;
      }

      setIsLoading(true);

      const response = await fetch('/api/onboarding/seed', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          sampleRecipes: loadSampleRecipes,
          samplePlans: loadSamplePlannerItems,
        }),
      });

      if (response.ok) {
        const result = await response.json();
        const { recipesAdded, plansAdded } = result;
        
        setCurrentStep(3);
        
        // Enhanced toast with counts and action buttons
        if (recipesAdded > 0 || plansAdded > 0) {
          const message = `Loaded ${recipesAdded} recipes ‚Ä¢ ${plansAdded} plans`;
          toast.success(message, {
            duration: 6000,
            action: {
              label: recipesAdded > 0 ? 'View Meal Planner' : 'View Planner',
              onClick: () => {
                if (typeof window !== 'undefined') {
                  if (recipesAdded > 0) {
                    window.open('/meal-planner', '_blank');
                  } else {
                    window.open('/planner', '_blank');
                  }
                }
              },
            },
            cancel: plansAdded > 0 && recipesAdded > 0 ? {
              label: 'View Planner',
              onClick: () => {
                if (typeof window !== 'undefined') {
                  window.open('/planner', '_blank');
                }
              },
            } : undefined,
          });
        } else {
          toast.success('Starter data ready!');
        }
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to create starter data');
      }
    } catch (error) {
      console.error('Error creating starter data:', error);
      toast.error('Something went wrong. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCompleteOnboarding = async () => {
    try {
      setIsLoading(true);
      
      const response = await fetch('/api/onboarding/complete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        toast.success('Welcome to your new home management system!');
        router.push('/dashboard');
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to complete onboarding');
      }
    } catch (error) {
      console.error('Error completing onboarding:', error);
      toast.error('Something went wrong. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 px-4 py-8">
      <div className="max-w-2xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-600 to-indigo-600 px-6 py-8 text-white text-center">
            <h1 className="text-2xl md:text-3xl font-bold mb-2">
              Welcome to Home Management! üè†
            </h1>
            <p className="text-blue-100">
              Hello {user?.firstName || 'there'}! Let's get your household set up.
            </p>
            
            {/* Debug section */}
            <div className="mt-4 p-3 bg-blue-800 bg-opacity-50 rounded-lg">
              <p className="text-sm text-blue-100 mb-2">üîß Debug Tools (You shouldn't see this if properly onboarded)</p>
              <div className="flex flex-col sm:flex-row gap-2 justify-center">
                <button
                  onClick={async () => {
                    try {
                      const response = await fetch('/api/debug/user-status');
                      const data = await response.json();
                      console.log('üîç User Status:', data);
                      alert(`Check console for user status. hasOnboarded: ${  data.debug?.hasOnboarded}`);
                    } catch (error) {
                      console.error('Error checking status:', error);
                      alert(`Error checking status: ${  error}`);
                    }
                  }}
                  className="px-3 py-1 bg-blue-700 text-white text-xs rounded hover:bg-blue-600"
                >
                  üîç Check Status
                </button>
                <button
                  onClick={async () => {
                    try {
                      const response = await fetch('/api/debug/fix-onboarding', { method: 'POST' });
                      const data = await response.json();
                      console.log('üîß Fix Result:', data);
                      if (data.success) {
                        alert('‚úÖ Onboarding status fixed! Redirecting to dashboard...');
                        router.push('/dashboard');
                      } else {
                        alert(`‚ùå Failed to fix: ${  data.error}`);
                      }
                    } catch (error) {
                      console.error('Error fixing onboarding:', error);
                      alert(`Error fixing onboarding: ${  error}`);
                    }
                  }}
                  className="px-3 py-1 bg-green-700 text-white text-xs rounded hover:bg-green-600"
                >
                  üîß Fix Onboarding
                </button>
              </div>
            </div>
          </div>

                           {/* Progress indicator */}
                 <div className="px-6 py-6 bg-gray-50">
                   <div className="flex items-center justify-center space-x-2 md:space-x-4">
                     {[1, 2, 3].map((step) => (
                       <div key={step} className="flex items-center">
                         <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium transition-colors ${
                           currentStep >= step
                             ? 'bg-blue-600 text-white'
                             : 'bg-gray-200 text-gray-600'
                         }`}>
                           {step}
                         </div>
                         {step < 3 && (
                           <div className={`w-8 md:w-16 h-1 mx-2 transition-colors ${
                             currentStep > step ? 'bg-blue-600' : 'bg-gray-200'
                           }`} />
                         )}
                       </div>
                     ))}
                   </div>
                   <div className="flex justify-center mt-3">
                     <div className="text-center">
                       <div className="text-sm font-medium text-gray-700">
                         Step {currentStep} of 3
                       </div>
                       <div className="text-xs text-gray-500 mt-1">
                         {currentStep === 1 && 'Household Setup'}
                         {currentStep === 2 && 'Starter Data'}
                         {currentStep === 3 && 'Complete Setup'}
                       </div>
                     </div>
                   </div>
                 </div>

          {/* Step Content */}
          <div className="px-6 py-8">
            {/* Step 1: Household */}
            {currentStep === 1 && (
              <div className="space-y-6">
                <div className="text-center">
                  <h2 className="text-xl md:text-2xl font-semibold text-gray-900 mb-2">
                    Set Up Your Household
                  </h2>
                  <p className="text-gray-600">
                    Let's start with some basic information about your home.
                  </p>
                </div>

                <div className="space-y-4">
                  <div>
                    <label htmlFor="householdName" className="block text-sm font-medium text-gray-700 mb-2">
                      Household Name
                    </label>
                    <input
                      id="householdName"
                      type="text"
                      value={householdName}
                      onChange={(e) => setHouseholdName(e.target.value)}
                      placeholder="Enter your household name"
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                      maxLength={100}
                    />
                  </div>

                  <div className="bg-blue-50 rounded-lg p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-medium text-blue-900">Current Members</h3>
                        <p className="text-sm text-blue-700">You can invite more people later</p>
                      </div>
                      <div className="text-2xl font-bold text-blue-600">
                        {memberCount}
                      </div>
                    </div>
                  </div>

                  <div className="text-center">
                    <button
                      type="button"
                      className="text-sm text-blue-600 hover:text-blue-700 font-medium"
                      onClick={() => toast.info('You can invite members from the dashboard after setup!')}
                    >
                      üíå I'll invite people later
                    </button>
                  </div>
                </div>

                <button
                  onClick={handleHouseholdStep}
                  disabled={isLoading || !householdName.trim()}
                  className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isLoading ? 'Setting up...' : 'Continue'}
                </button>
              </div>
            )}

            {/* Step 2: Starter Data */}
            {currentStep === 2 && (
              <div className="space-y-6">
                <div className="text-center">
                  <h2 className="text-xl md:text-2xl font-semibold text-gray-900 mb-2">
                    Add Starter Content
                  </h2>
                  <p className="text-gray-600">
                    We can populate your account with some sample data to get you started.
                  </p>
                </div>

                <div className="space-y-4">
                  <div className="border border-gray-200 rounded-lg p-4">
                    <label className="flex items-start space-x-3 cursor-pointer">
                      <input
                        type="checkbox"
                        checked={loadSampleRecipes}
                        onChange={(e) => setLoadSampleRecipes(e.target.checked)}
                        className="mt-1 w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                      />
                      <div className="flex-1">
                        <div className="font-medium text-gray-900">Load sample recipes</div>
                        <div className="text-sm text-gray-500">
                          Add a variety of recipes to your meal planner (breakfast, lunch, dinner)
                        </div>
                      </div>
                    </label>
                  </div>

                  <div className="border border-gray-200 rounded-lg p-4">
                    <label className="flex items-start space-x-3 cursor-pointer">
                      <input
                        type="checkbox"
                        checked={loadSamplePlannerItems}
                        onChange={(e) => setLoadSamplePlannerItems(e.target.checked)}
                        className="mt-1 w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                      />
                      <div className="flex-1">
                        <div className="font-medium text-gray-900">Load sample planner items</div>
                        <div className="text-sm text-gray-500">
                          Add example tasks and planning items to help you get organized
                        </div>
                      </div>
                    </label>
                  </div>
                </div>

                <div className="flex flex-col sm:flex-row gap-3">
                  <button
                    onClick={() => setCurrentStep(1)}
                    className="sm:w-auto px-6 py-3 border border-gray-300 text-gray-700 rounded-lg font-medium hover:bg-gray-50 transition-colors"
                  >
                    Back
                  </button>
                  <button
                    onClick={handleStarterDataStep}
                    disabled={isLoading}
                    className="flex-1 bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isLoading ? 'Creating starter data...' : 'Create starter data'}
                  </button>
                </div>
              </div>
            )}

            {/* Step 3: Finish */}
            {currentStep === 3 && (
              <div className="space-y-6 text-center">
                <div className="text-6xl mb-4">üéâ</div>
                <div>
                  <h2 className="text-xl md:text-2xl font-semibold text-gray-900 mb-2">
                    You're All Set!
                  </h2>
                  <p className="text-gray-600">
                    Your household is ready to go. Welcome to your new home management system!
                  </p>
                </div>

                <div className="bg-green-50 rounded-lg p-4 text-left">
                  <h3 className="font-medium text-green-900 mb-2">What's been set up:</h3>
                  <ul className="text-sm text-green-700 space-y-1">
                    <li>‚úÖ Your household: {householdName}</li>
                    {loadSampleRecipes ? <li>‚úÖ Sample recipes added</li> : null}
                    {loadSamplePlannerItems ? <li>‚úÖ Sample planner items added</li> : null}
                    <li>‚úÖ Your account is ready to use</li>
                  </ul>
                </div>

                <div className="flex flex-col sm:flex-row gap-3">
                  <button
                    onClick={() => setCurrentStep(2)}
                    className="sm:w-auto px-6 py-3 border border-gray-300 text-gray-700 rounded-lg font-medium hover:bg-gray-50 transition-colors"
                  >
                    Back
                  </button>
                  <button
                    onClick={handleCompleteOnboarding}
                    disabled={isLoading}
                    className="flex-1 bg-green-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-green-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isLoading ? 'Finalizing...' : 'Go to Dashboard'}
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useAuth, useUser } from '@clerk/nextjs';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';

interface UserData {
  email: string;
  role: 'owner' | 'member';
  plan: 'free' | 'pro' | 'pro_plus';
  household: {
    id: string;
    plan: string;
  };
}

interface PlannerItem {
  id: string;
  title: string;
  description?: string;
  category: 'trip' | 'renovation' | 'dream' | 'goal' | 'event' | 'other';
  status: 'planning' | 'in-progress' | 'completed' | 'on-hold';
  priority: 'low' | 'medium' | 'high';
  due_date?: string;
  created_by: string;
  household_id: string;
  created_at: string;
  updated_at: string;
}

export default function PlannerPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const { user } = useUser();
  const router = useRouter();
  
  const [userData, setUserData] = useState<UserData | null>(null);
  const [plannerItems, setPlannerItems] = useState<PlannerItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [showCreateModal, setShowCreateModal] = useState(false);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    async function fetchUserData() {
      if (!user?.id) return;

      try {
        setLoading(true);
        setError(null);

        const response = await fetch('/api/user-data');
        const result = await response.json();

        if (!response.ok) {
          console.error('Error fetching user data:', result.error);
          setError(result.error || 'Failed to load user data');
          return;
        }

        if (result.success && result.data) {
          setUserData({
            email: result.data.email,
            role: result.data.role,
            plan: result.data.plan || 'free',
            household: result.data.household
          });
        } else {
          setError('User not found in database');
        }
      } catch (err) {
        console.error('Exception fetching user data:', err);
        setError('An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }

    fetchUserData();
  }, [isLoaded, isSignedIn, user?.id, router]);

  useEffect(() => {
    if (userData?.household?.id) {
      fetchPlannerItems();
    }
  }, [userData]);

  async function fetchPlannerItems() {
    if (!userData?.household?.id) return;

    try {
      const response = await fetch('/api/planner');
      const result = await response.json();

      if (response.ok) {
        // API returns array directly
        setPlannerItems(Array.isArray(result) ? result : []);
      }
    } catch (err) {
      console.error('Error fetching planner items:', err);
    }
  }

  function getCategoryIcon(category: string): string {
    switch (category) {
      case 'trip': return '‚úàÔ∏è';
      case 'renovation': return 'üè†';
      case 'dream': return 'üí≠';
      case 'goal': return 'üéØ';
      case 'event': return 'üìÖ';
      default: return 'üìù';
    }
  }

  function getStatusColor(status: string): string {
    switch (status) {
      case 'planning': return 'bg-blue-100 text-blue-800';
      case 'in-progress': return 'bg-yellow-100 text-yellow-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'on-hold': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  }

  function getPriorityColor(priority: string): string {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  }

  function formatDate(dateString: string): string {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  }

  const filteredItems = selectedCategory === 'all' 
    ? plannerItems 
    : plannerItems.filter(item => item.category === selectedCategory);

  const categories = [
    { id: 'all', name: 'All Plans', icon: 'üìã' },
    { id: 'trip', name: 'Trips', icon: '‚úàÔ∏è' },
    { id: 'renovation', name: 'Renovations', icon: 'üè†' },
    { id: 'dream', name: 'Dreams', icon: 'üí≠' },
    { id: 'goal', name: 'Goals', icon: 'üéØ' },
    { id: 'event', name: 'Events', icon: 'üìÖ' },
    { id: 'other', name: 'Other', icon: 'üìù' }
  ];

  // Show loading spinner while auth is loading or data is being fetched
  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!isSignedIn) {
    return null;
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white shadow rounded-lg p-6 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
            <h1 className="text-xl font-semibold text-gray-900 mb-2">Error</h1>
            <p className="text-gray-600 mb-4">{error}</p>
            <button 
              onClick={() => {
          if (typeof window !== 'undefined') {
            window.location.reload();
          }
        }}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-4 sm:py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white shadow rounded-lg mb-4 sm:mb-6">
          <div className="px-4 sm:px-6 py-4 border-b border-gray-200">
            <div className="flex flex-col space-y-4 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
              <div>
                <h1 className="text-xl sm:text-2xl font-bold text-gray-900">Collaborative Planner</h1>
                <p className="text-sm sm:text-base text-gray-600">Plan trips, renovations, dreams, goals, and more together</p>
              </div>
              <button
                onClick={() => setShowCreateModal(true)}
                className="bg-blue-600 text-white px-4 py-3 sm:py-2 rounded-md hover:bg-blue-700 active:bg-blue-800 font-medium text-base sm:text-sm touch-manipulation"
              >
                + New Plan
              </button>
            </div>
          </div>

          {/* Category Filter */}
          <div className="px-4 sm:px-6 py-4">
            <div className="flex space-x-2 overflow-x-auto pb-2 -mb-2">
              {categories.map((category) => (
                <button
                  key={category.id}
                  onClick={() => setSelectedCategory(category.id)}
                  className={`flex items-center space-x-2 px-3 py-2 rounded-md text-sm font-medium whitespace-nowrap transition-colors touch-manipulation ${
                    selectedCategory === category.id
                      ? 'bg-blue-100 text-blue-800'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200 active:bg-gray-300'
                  }`}
                >
                  <span>{category.icon}</span>
                  <span>{category.name}</span>
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Planner Items Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6">
          {filteredItems.map((item) => (
            <div
              key={item.id}
              className="bg-white shadow rounded-lg p-4 sm:p-6 cursor-pointer hover:shadow-md active:scale-95 transition-all duration-200 touch-manipulation"
            >
              <div className="flex items-start justify-between mb-3">
                <div className="flex items-center space-x-2 min-w-0 flex-1">
                  <span className="text-xl sm:text-2xl flex-shrink-0">{getCategoryIcon(item.category)}</span>
                  <h3 className="font-semibold text-gray-900 truncate">{item.title}</h3>
                </div>
                <div className="flex flex-col sm:flex-row space-y-1 sm:space-y-0 sm:space-x-1 ml-2">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium whitespace-nowrap ${getStatusColor(item.status)}`}>
                    {item.status.replace('-', ' ')}
                  </span>
                  <span className={`px-2 py-1 rounded-full text-xs font-medium whitespace-nowrap ${getPriorityColor(item.priority)}`}>
                    {item.priority}
                  </span>
                </div>
              </div>

              {item.description ? <p className="text-gray-600 text-sm mb-3 line-clamp-2">{item.description}</p> : null}

              <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between text-xs text-gray-500 space-y-1 sm:space-y-0">
                <span>Created {formatDate(item.created_at)}</span>
                {item.due_date ? <span className="font-medium">
                    Due: {formatDate(item.due_date)}
                  </span> : null}
              </div>
            </div>
          ))}
        </div>

        {filteredItems.length === 0 && (
          <div className="text-center py-12">
            <div className="text-gray-400 text-6xl mb-4">üìã</div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No plans yet</h3>
            <p className="text-gray-600 mb-4">
              {selectedCategory === 'all' 
                ? 'Create your first plan to get started!'
                : `No ${categories.find(c => c.id === selectedCategory)?.name.toLowerCase()} plans yet.`
              }
            </p>
            <button
              onClick={() => setShowCreateModal(true)}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Create Your First Plan
            </button>
          </div>
        )}

        {/* Create Plan Modal */}
        {showCreateModal ? <CreatePlanModal
            onClose={() => setShowCreateModal(false)}
            onCreated={(newItem) => {
              setPlannerItems([newItem, ...plannerItems]); // Add to beginning
              setShowCreateModal(false);
              // Optionally refresh the list to ensure consistency
              setTimeout(() => fetchPlannerItems(), 100);
            }}
            householdId={userData?.household?.id || ''}
          /> : null}
      </div>
    </div>
  );
}

interface CreatePlanModalProps {
  onClose: () => void;
  onCreated: (item: PlannerItem) => void;
  householdId: string;
}

function CreatePlanModal({ onClose, onCreated, householdId: _householdId }: CreatePlanModalProps) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('trip');
  const [priority, setPriority] = useState('medium');
  const [dueDate, setDueDate] = useState('');
  const [creating, setCreating] = useState(false);

  async function handleCreate() {
    if (!title.trim()) return;

    setCreating(true);
    try {
      const response = await fetch('/api/planner', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: title.trim(),
          description: description.trim(),
          category,
          priority,
          due_date: dueDate || null
        }),
      });

      if (response.ok) {
        const result = await response.json();
        console.log('‚úÖ Plan created successfully:', result);
        onCreated(result); // API returns the item directly
      } else {
        const errorData = await response.json();
        console.error('‚ùå Failed to create plan:', errorData);
        alert(`Failed to create plan: ${errorData.error || 'Unknown error'}`);
      }
    } catch (err) {
      console.error('Error creating plan:', err);
      alert('Failed to create plan. Please try again.');
    } finally {
      setCreating(false);
    }
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-4 sm:p-6">
          <div className="flex items-center justify-between mb-4 sm:mb-6">
            <h2 className="text-lg sm:text-xl font-bold text-gray-900">Create New Plan</h2>
            <button 
              onClick={onClose} 
              className="text-gray-400 hover:text-gray-600 p-2 -mr-2 touch-manipulation"
              aria-label="Close modal"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Title *
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="w-full px-4 py-3 sm:px-3 sm:py-2 text-base sm:text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter plan title..."
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                rows={3}
                className="w-full px-4 py-3 sm:px-3 sm:py-2 text-base sm:text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                placeholder="Enter description..."
              />
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Category
                </label>
                <select
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  className="w-full px-4 py-3 sm:px-3 sm:py-2 text-base sm:text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="trip">üåç Trip</option>
                  <option value="renovation">üè† Renovation</option>
                  <option value="dream">üí≠ Dream</option>
                  <option value="goal">üéØ Goal</option>
                  <option value="event">üéâ Event</option>
                  <option value="other">üìã Other</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Priority
                </label>
                <select
                  value={priority}
                  onChange={(e) => setPriority(e.target.value)}
                  className="w-full px-4 py-3 sm:px-3 sm:py-2 text-base sm:text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="low">üü¢ Low</option>
                  <option value="medium">üü° Medium</option>
                  <option value="high">üî¥ High</option>
                </select>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Due Date (Optional)
              </label>
              <input
                type="date"
                value={dueDate}
                onChange={(e) => setDueDate(e.target.value)}
                className="w-full px-4 py-3 sm:px-3 sm:py-2 text-base sm:text-sm border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          <div className="mt-6 flex flex-col sm:flex-row justify-end space-y-3 sm:space-y-0 sm:space-x-3">
            <button
              onClick={onClose}
              className="px-4 py-3 sm:py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 active:bg-gray-100 font-medium text-base sm:text-sm touch-manipulation"
            >
              Cancel
            </button>
            <button
              onClick={handleCreate}
              disabled={!title.trim() || creating}
              className="px-4 py-3 sm:py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 active:bg-blue-800 disabled:opacity-50 disabled:cursor-not-allowed font-medium text-base sm:text-sm touch-manipulation"
            >
              {creating ? 'Creating...' : 'Create Plan'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useCallback, useEffect, useState } from 'react';
import { useUserData } from '@/hooks/useUserData';
import QuietHours from '@/components/QuietHours';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { AlertCircle, Info, BellOff, Clock, Moon } from 'lucide-react';

export default function QuietHoursPage() {
  const { userData, isLoading } = useUserData();
  const [entitlements, setEntitlements] = useState<Record<string, unknown> | null>(null);
  const [entitlementsLoading, setEntitlementsLoading] = useState(true);
  const householdId = userData?.household_id;

  const loadEntitlements = useCallback(async () => {
    if (!householdId) {
      return;
    }

    try {
      setEntitlementsLoading(true);
      const response = await fetch(`/api/entitlements/${householdId}`);
      const data = await response.json();
      
      if (response.ok) {
        setEntitlements(data);
      } else {
        console.error('Failed to load entitlements:', data.error);
      }
    } catch (error) {
      console.error('Error loading entitlements:', error);
    } finally {
      setEntitlementsLoading(false);
    }
  }, [householdId]);

  // Load entitlements
  useEffect(() => {
    if (householdId) {
      void loadEntitlements();
    }
  }, [householdId, loadEntitlements]);

  if (isLoading || entitlementsLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                <span className="ml-2 text-gray-600">Loading...</span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!userData?.household_id) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <Card>
            <CardContent className="p-6">
              <div className="text-center">
                <AlertCircle className="h-12 w-12 mx-auto mb-4 text-red-500" />
                <h2 className="text-xl font-semibold text-gray-900 mb-2">
                  Household Required
                </h2>
                <p className="text-gray-600">
                  You need to be part of a household to access quiet hours features.
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* Page Header */}
        <div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Quiet Hours</h1>
          <p className="text-gray-600">
            Manage notification quiet hours to automatically silence alerts during specific times.
          </p>
        </div>

        {/* Quiet Hours Component */}
        <QuietHours 
          householdId={userData.household_id}
          entitlements={entitlements}
        />

        {/* Additional Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Info className="h-5 w-5" />
              About Quiet Hours
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="p-4 border rounded-lg">
                <h4 className="font-medium text-gray-900 mb-2 flex items-center gap-2">
                  <BellOff className="h-4 w-4" />
                  What Gets Silenced
                </h4>
                <ul className="text-sm text-gray-600 space-y-1">
                  <li>‚Ä¢ Push notifications for new chores, events, and reminders</li>
                  <li>‚Ä¢ Email alerts for household activities</li>
                  <li>‚Ä¢ In-app notification sounds and banners</li>
                  <li>‚Ä¢ Automated system notifications</li>
                </ul>
              </div>
              
              <div className="p-4 border rounded-lg">
                <h4 className="font-medium text-gray-900 mb-2 flex items-center gap-2">
                  <Clock className="h-4 w-4" />
                  How It Works
                </h4>
                <ul className="text-sm text-gray-600 space-y-1">
                  <li>‚Ä¢ Set your preferred start and end times</li>
                  <li>‚Ä¢ Choose which days of the week to apply quiet hours</li>
                  <li>‚Ä¢ Notifications are automatically silenced during these times</li>
                  <li>‚Ä¢ You can still access the app normally during quiet hours</li>
                </ul>
              </div>

              <div className="p-4 border rounded-lg">
                <h4 className="font-medium text-gray-900 mb-2 flex items-center gap-2">
                  <Moon className="h-4 w-4" />
                  Pro Feature
                </h4>
                <p className="text-sm text-gray-600">
                  Quiet hours are a Pro feature that helps you maintain a healthy work-life balance 
                  by automatically managing when you receive household notifications.
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

'use client';

import { useAuth } from '@clerk/nextjs';
import React, { useState, useEffect } from 'react';
import { useRouter, useParams } from 'next/navigation';
import { toast } from 'sonner';
import { useRecipes, Recipe } from '@/hooks/useRecipes';

export default function EditRecipePage() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  const params = useParams();
  const recipeId = params.id as string;
  
  const { data: recipes, isLoading: recipesLoading, error: recipesError } = useRecipes();
  const recipesData = recipes as { success: boolean; recipes: Recipe[] } | undefined;
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    prep_time: 0,
    cook_time: 0,
    servings: 1,
    ingredients: '',
    instructions: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Find the recipe to edit
  const recipe = recipesData?.recipes?.find(r => r.id === recipeId);

  useEffect(() => {
    if (recipe) {
      setFormData({
        title: recipe.name || '',
        description: recipe.description || '',
        prep_time: recipe.prep_time || 0,
        cook_time: recipe.cook_time || 0,
        servings: recipe.servings || 1,
        ingredients: Array.isArray(recipe.ingredients) 
          ? recipe.ingredients.map((ing: any) => 
              `${ing.amount || ''} ${ing.unit || ''} ${ing.name || ''}`.trim()
            ).join('\n')
          : '',
        instructions: Array.isArray(recipe.instructions) 
          ? recipe.instructions.join('\n')
          : ''
      });
      setIsLoading(false);
    } else if (recipesData && !recipesLoading) {
      // Recipe not found
      toast.error('Recipe not found');
      router.push('/recipes');
    }
  }, [recipe, recipesData, recipesLoading, router]);

  if (!isLoaded) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!isSignedIn) {
    router.push('/sign-in');
    return null;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading recipe...</p>
        </div>
      </div>
    );
  }

  if (!recipe) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-red-500 text-6xl mb-4">‚ùå</div>
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Recipe Not Found</h1>
          <p className="text-gray-600 mb-6">The recipe you're looking for doesn't exist or you don't have access to it.</p>
          <button
            onClick={() => router.push('/recipes')}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Back to Recipes
          </button>
        </div>
      </div>
    );
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Format ingredients and instructions properly
      const formattedData = {
        ...formData,
        ingredients: formData.ingredients.trim(),
        instructions: formData.instructions.trim()
      };

      const response = await fetch(`/api/recipes/${recipeId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formattedData),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          toast.success('Recipe updated successfully!');
          router.push('/recipes');
        } else {
          toast.error(`Failed to update recipe: ${result.error || 'Unknown error'}`);
        }
      } else {
        const error = await response.json();
        toast.error(`Failed to update recipe: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      toast.error('Failed to update recipe. Please try again.');
      console.error('Error updating recipe:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'prep_time' || name === 'cook_time' || name === 'servings' 
        ? parseInt(value) || 0 
        : value
    }));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        <div className="bg-white shadow rounded-lg">
          {/* Header */}
          <div className="px-6 py-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Edit Recipe</h1>
                <p className="text-gray-600">Update your recipe details</p>
              </div>
              <button
                onClick={() => router.back()}
                className="text-gray-400 hover:text-gray-600"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="p-6 space-y-6">
            {/* Recipe Name */}
            <div>
              <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-2">
                Recipe Name *
              </label>
              <input
                type="text"
                id="title"
                name="title"
                required
                value={formData.title}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter recipe name"
              />
            </div>

            {/* Description */}
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                rows={3}
                value={formData.description}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Brief description of the recipe"
              />
            </div>

            {/* Time and Servings */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label htmlFor="prep_time" className="block text-sm font-medium text-gray-700 mb-2">
                  Prep Time (min)
                </label>
                <input
                  type="number"
                  id="prep_time"
                  name="prep_time"
                  min="0"
                  value={formData.prep_time}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <div>
                <label htmlFor="cook_time" className="block text-sm font-medium text-gray-700 mb-2">
                  Cook Time (min)
                </label>
                <input
                  type="number"
                  id="cook_time"
                  name="cook_time"
                  min="0"
                  value={formData.cook_time}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <div>
                <label htmlFor="servings" className="block text-sm font-medium text-gray-700 mb-2">
                  Servings
                </label>
                <input
                  type="number"
                  id="servings"
                  name="servings"
                  min="1"
                  value={formData.servings}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            {/* Ingredients */}
            <div>
              <label htmlFor="ingredients" className="block text-sm font-medium text-gray-700 mb-2">
                Ingredients *
              </label>
              <textarea
                id="ingredients"
                name="ingredients"
                rows={4}
                required
                value={formData.ingredients}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="List ingredients, one per line or separated by commas"
              />
            </div>

            {/* Instructions */}
            <div>
              <label htmlFor="instructions" className="block text-sm font-medium text-gray-700 mb-2">
                Instructions *
              </label>
              <textarea
                id="instructions"
                name="instructions"
                rows={6}
                required
                value={formData.instructions}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Step-by-step cooking instructions"
              />
            </div>

            {/* Submit Button */}
            <div className="flex justify-end space-x-3 pt-4">
              <button
                type="button"
                onClick={() => router.back()}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? 'Updating...' : 'Update Recipe'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

