'use client';

import { useAuth } from '@clerk/nextjs';
import React, { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { toast } from 'sonner';
import { useUserData } from '@/hooks/useUserData';
import { useRecipes, Recipe } from '@/hooks/useRecipes';

import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { LoadingSpinner } from '@/components/ui/LoadingSpinner';
import { ErrorDisplay } from '@/components/ui/ErrorDisplay';

export default function RecipesPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const router = useRouter();
  
  // Data fetching hooks
  const { userData, isLoading: userDataLoading, error: userDataError } = useUserData();
  const { data: recipes, isLoading: recipesLoading, error: recipesError, refetch } = useRecipes();
  const recipesData = recipes as { success: boolean; recipes: Recipe[] } | undefined;
  
  // Loading and error states
  const loading = userDataLoading || recipesLoading;
  const error = userDataError || recipesError;
  
  // Check if user has completed onboarding
  const hasCompletedOnboarding = userData?.has_onboarded && userData?.household_id;

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }
    
    if (isSignedIn && !loading && !hasCompletedOnboarding) {
      router.push('/onboarding');
      return;
    }
  }, [isLoaded, isSignedIn, loading, hasCompletedOnboarding, router]);

  // Show loading spinner while auth is loading or data is being fetched
  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner size="lg" text="Loading recipes..." />
      </div>
    );
  }

  if (!isSignedIn) {
    return null;
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <ErrorDisplay 
          error={error.message || 'Failed to load recipes'} 
          onRetry={() => window.location.reload()}
        />
      </div>
    );
  }

  // Check if user has completed onboarding
  if (!hasCompletedOnboarding) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <Card className="max-w-md w-full mx-4">
          <CardHeader className="text-center">
            <div className="text-blue-500 text-6xl mb-4">üè†</div>
            <CardTitle>Complete Onboarding First</CardTitle>
          </CardHeader>
          <CardContent className="text-center">
            <p className="text-gray-600 mb-4">
              You need to complete the onboarding process to access recipes.
            </p>
            <Button 
              onClick={() => router.push('/onboarding')}
              className="w-full"
            >
              Go to Onboarding
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Final safety check - ensure user data is loaded
  if (!userData || !userData.household_id) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner size="lg" text="Loading user data..." />
      </div>
    );
  }

  const handleCreateRecipe = () => {
    router.push('/recipes/create');
  };

  const handleBackToMealPlanner = () => {
    router.push('/meal-planner');
  };

  const handleEditRecipe = (recipeId: string) => {
    router.push(`/recipes/${recipeId}/edit`);
  };

  const handleDeleteRecipe = async (recipeId: string) => {
    if (!confirm('Are you sure you want to delete this recipe? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`/api/recipes/${recipeId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success('Recipe deleted successfully!');
        refetch(); // Refresh the recipes list
      } else {
        toast.error('Failed to delete recipe');
      }
    } catch (error) {
      console.error('Error deleting recipe:', error);
      toast.error('Failed to delete recipe');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">My Recipes</h1>
              <p className="text-gray-600 mt-2">Manage and organize your recipe collection</p>
            </div>
            <div className="flex items-center space-x-4">
              <Button
                variant="outline"
                onClick={handleBackToMealPlanner}
              >
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
                </svg>
                Back to Meal Planner
              </Button>
              <Button
                onClick={handleCreateRecipe}
                className="bg-green-600 hover:bg-green-700"
              >
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
                Create New Recipe
              </Button>
            </div>
          </div>
        </div>

        {/* Recipes Grid */}
        {recipesData?.recipes && recipesData.recipes.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {recipesData.recipes.map((recipe) => (
              <Card key={recipe.id} className="hover:shadow-md transition-shadow">
                <CardHeader className="pb-3">
                  <div className="flex items-start justify-between">
                    <CardTitle className="text-lg line-clamp-2">{recipe.name}</CardTitle>
                    <div className="flex items-center space-x-1">
                      <Badge variant="secondary" className="text-xs">
                        {recipe.difficulty || 'Easy'}
                      </Badge>
                    </div>
                  </div>
                  {recipe.description && (
                    <p className="text-sm text-gray-600 line-clamp-2">{recipe.description}</p>
                  )}
                </CardHeader>
                <CardContent className="pt-0">
                  <div className="space-y-3">
                    {/* Recipe Stats */}
                    <div className="grid grid-cols-3 gap-2 text-xs text-gray-600">
                      <div className="text-center">
                        <div className="font-medium text-gray-900">{recipe.prep_time || 0}</div>
                        <div>Prep (min)</div>
                      </div>
                      <div className="text-center">
                        <div className="font-medium text-gray-900">{recipe.cook_time || 0}</div>
                        <div>Cook (min)</div>
                      </div>
                      <div className="text-center">
                        <div className="font-medium text-gray-900">{recipe.servings || 1}</div>
                        <div>Servings</div>
                      </div>
                    </div>

                    {/* Tags */}
                    {recipe.tags && recipe.tags.length > 0 && (
                      <div className="flex flex-wrap gap-1">
                        {recipe.tags.slice(0, 3).map((tag, index) => (
                          <Badge key={index} variant="outline" className="text-xs">
                            {tag}
                          </Badge>
                        ))}
                        {recipe.tags.length > 3 && (
                          <Badge variant="outline" className="text-xs">
                            +{recipe.tags.length - 3}
                          </Badge>
                        )}
                      </div>
                    )}

                    {/* Action Buttons */}
                    <div className="flex items-center space-x-2 pt-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEditRecipe(recipe.id)}
                        className="flex-1"
                      >
                        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                        </svg>
                        Edit
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleDeleteRecipe(recipe.id)}
                        className="text-red-600 hover:text-red-700 hover:bg-red-50"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          /* Empty State */
          <Card className="text-center py-12">
            <CardContent>
              <div className="text-gray-400 text-6xl mb-4">üçΩÔ∏è</div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No Recipes Yet</h3>
              <p className="text-gray-500 mb-6">
                Start building your recipe collection to make meal planning easier.
              </p>
              <div className="flex items-center justify-center space-x-4">
                <Button
                  onClick={handleCreateRecipe}
                  className="bg-green-600 hover:bg-green-700"
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Create Your First Recipe
                </Button>
                <Button
                  variant="outline"
                  onClick={handleBackToMealPlanner}
                >
                  Back to Meal Planner
                </Button>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

'use client'

import { useAuth, useUser } from '@clerk/nextjs';
import { useCallback, useEffect, useMemo, useState } from 'react';
import { useRouter } from 'next/navigation';
import { getReminders, addReminder, deleteReminder } from '@/lib/reminders';
import { getChores } from '@/lib/chores';
import { getCalendarEvents } from '@/lib/calendar';
import { canAccessFeature } from '@/lib/planFeatures';

interface UserData {
  email: string;
  role: 'owner' | 'member';
  plan: 'free' | 'pro' | 'pro_plus';
  xp: number;
  coins: number;
  household: {
    id: string;
    plan: string;
    game_mode: string;
    created_at: string;
  };
}

interface Reminder {
  id: string;
  title: string;
  related_type: 'chore' | 'calendar_event';
  related_id: string;
  remind_at: string;
  created_by: string;
  household_id: string;
  created_at: string;
}

interface Chore {
  id: string;
  title: string;
  description?: string;
  assigned_to?: string;
  due_at?: string;
  recurrence?: string;
  created_by: string;
  household_id: string;
  created_at: string;
}

interface CalendarEvent {
  id: string;
  title: string;
  description?: string;
  start_time: string;
  end_time: string;
  created_by: string;
  household_id: string;
  created_at: string;
}

export default function RemindersPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const { user } = useUser();
  const router = useRouter();
  
  const [userData, setUserData] = useState<UserData | null>(null);
  const [reminders, setReminders] = useState<Reminder[]>([]);
  const [chores, setChores] = useState<Chore[]>([]);
  const [calendarEvents, setCalendarEvents] = useState<CalendarEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [addingReminder, setAddingReminder] = useState(false);
  const [deletingReminder, setDeletingReminder] = useState<string | null>(null);
  const [loadingRelatedItems, setLoadingRelatedItems] = useState(false);
  
  // Form state
  const [formData, setFormData] = useState({
    title: '',
    related_type: 'chore' as 'chore' | 'calendar_event',
    related_id: '',
    remind_at: ''
  });

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    async function fetchUserData() {
      if (!user?.id) return;

      try {
        setLoading(true);
        setError(null);

        const response = await fetch('/api/user-data');
        const result = await response.json();

        if (!response.ok) {
          console.error('Error fetching user data:', result.error);
          setError(result.error || 'Failed to load user data');
          return;
        }

        if (result.success && result.data) {
          const userDataObj = {
            email: result.data.email,
            role: result.data.role,
            plan: result.data.plan || 'free',
            xp: result.data.xp || 0,
            coins: result.data.coins || 0,
            household: result.data.household
          };
          
          setUserData(userDataObj);

          // Check if user can access reminders feature
          if (!canAccessFeature(userDataObj.plan, 'reminders')) {
            router.push('/upgrade');
          }
        } else {
          setError('User not found in database');
        }
      } catch (err) {
        console.error('Exception fetching user data:', err);
        setError('An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }

    fetchUserData();
  }, [isLoaded, isSignedIn, user?.id, router]);

  const householdId = userData?.household?.id;

  const fetchRelatedItems = useCallback(async () => {
    if (!householdId) return;

    try {
      setLoadingRelatedItems(true);
      const [choresData, eventsData] = await Promise.all([
        getChores(householdId),
        getCalendarEvents(householdId),
      ]);
      setChores(Array.isArray(choresData) ? choresData : choresData?.chores ?? []);
      setCalendarEvents(Array.isArray(eventsData) ? eventsData : eventsData?.events ?? []);
    } catch (err) {
      console.error('Error fetching related items:', err);
      setError('Failed to load chores and events');
    } finally {
      setLoadingRelatedItems(false);
    }
  }, [householdId]);

  const fetchReminders = useCallback(async () => {
    if (!householdId) return;

    try {
      setLoading(true);
      const data = await getReminders(householdId);
      setReminders(data);
    } catch (err) {
      console.error('Error fetching reminders:', err);
      setError('Failed to load reminders');
    } finally {
      setLoading(false);
    }
  }, [householdId]);

  useEffect(() => {
    if (householdId) {
      void fetchReminders();
      void fetchRelatedItems();
    }
  }, [fetchRelatedItems, fetchReminders, householdId]);

  const handleAddReminder = useCallback(async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (!user?.id || !householdId) return;

    try {
      setAddingReminder(true);
      setError(null);

      await addReminder({
        title: formData.title,
        related_type: formData.related_type,
        related_id: formData.related_id,
        remind_at: formData.remind_at,
        created_by: user.id,
        household_id: householdId,
      });

      setFormData({
        title: '',
        related_type: 'chore',
        related_id: '',
        remind_at: '',
      });

      await fetchReminders();
    } catch (err) {
      console.error('Error adding reminder:', err);
      setError('Failed to add reminder');
    } finally {
      setAddingReminder(false);
    }
  }, [fetchReminders, formData, householdId, user?.id]);

  const handleRelatedTypeChange = useCallback((newType: 'chore' | 'calendar_event') => {
    setFormData((prev) => ({
      ...prev,
      related_type: newType,
      related_id: '',
    }));
  }, []);

  const handleDeleteReminder = useCallback(async (reminderId: string) => {
    if (!user?.id) return;

    try {
      setDeletingReminder(reminderId);
      setError(null);

      await deleteReminder(reminderId);
      await fetchReminders();
    } catch (err) {
      console.error('Error deleting reminder:', err);
      setError('Failed to delete reminder');
    } finally {
      setDeletingReminder(null);
    }
  }, [fetchReminders, user?.id]);

  const formatDateTime = useCallback((dateTimeString: string) => {
    return new Date(dateTimeString).toLocaleString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  }, []);

  // Show loading spinner while auth is loading or data is being fetched
  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  // This should not be reached if redirect is working, but just in case
  if (!isSignedIn) {
    return null;
  }

  // Show error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white shadow rounded-lg p-6 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
            <h1 className="text-xl font-semibold text-gray-900 mb-2">Error</h1>
            <p className="text-gray-600 mb-4">{error}</p>
            <button 
              onClick={() => {
          if (typeof window !== 'undefined') {
            window.location.reload();
          }
        }}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">‚è∞ Reminders</h1>
          <p className="text-gray-600">Manage your household reminders</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Add Reminder Form */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Add New Reminder</h2>
              
              <form onSubmit={handleAddReminder} className="space-y-4">
                <div>
                  <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
                    Title *
                  </label>
                  <input
                    type="text"
                    id="title"
                    required
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Reminder title"
                  />
                </div>

                                 <div>
                   <label htmlFor="related_type" className="block text-sm font-medium text-gray-700 mb-1">
                     Related Type *
                   </label>
                   <select
                     id="related_type"
                     required
                     value={formData.related_type}
                     onChange={(e) => handleRelatedTypeChange(e.target.value as 'chore' | 'calendar_event')}
                     className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                   >
                     <option value="chore">Chore</option>
                     <option value="calendar_event">Calendar Event</option>
                   </select>
                 </div>

                 <div>
                   <label htmlFor="related_id" className="block text-sm font-medium text-gray-700 mb-1">
                     Related Item *
                   </label>
                   {loadingRelatedItems ? (
                     <div className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-gray-500">
                       Loading {formData.related_type === 'chore' ? 'chores' : 'events'}...
                     </div>
                   ) : (
                     <select
                       id="related_id"
                       required
                       value={formData.related_id}
                       onChange={(e) => setFormData({ ...formData, related_id: e.target.value })}
                       className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                     >
                       <option value="">Select a {formData.related_type === 'chore' ? 'chore' : 'event'}</option>
                       {formData.related_type === 'chore' ? (
                         chores.map((chore) => (
                           <option key={chore.id} value={chore.id}>
                             {chore.title}
                           </option>
                         ))
                       ) : (
                         calendarEvents.map((event) => (
                           <option key={event.id} value={event.id}>
                             {event.title}
                           </option>
                         ))
                       )}
                     </select>
                   )}
                   {formData.related_type === 'chore' && chores.length === 0 && !loadingRelatedItems && (
                     <p className="text-sm text-gray-500 mt-1">No chores available. Create some chores first.</p>
                   )}
                   {formData.related_type === 'calendar_event' && calendarEvents.length === 0 && !loadingRelatedItems && (
                     <p className="text-sm text-gray-500 mt-1">No calendar events available. Create some events first.</p>
                   )}
                 </div>

                <div>
                  <label htmlFor="remind_at" className="block text-sm font-medium text-gray-700 mb-1">
                    Remind At *
                  </label>
                  <input
                    type="datetime-local"
                    id="remind_at"
                    required
                    value={formData.remind_at}
                    onChange={(e) => setFormData({ ...formData, remind_at: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <button
                  type="submit"
                  disabled={addingReminder}
                  className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {addingReminder ? 'Adding...' : 'Add Reminder'}
                </button>
              </form>
            </div>
          </div>

          {/* Reminders List */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow overflow-hidden">
              <div className="px-6 py-4 border-b border-gray-200">
                <h2 className="text-xl font-semibold text-gray-900">Upcoming Reminders</h2>
              </div>
              
              {reminders.length === 0 ? (
                <div className="p-6 text-center">
                  <div className="text-gray-400 text-6xl mb-4">‚è∞</div>
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No reminders set</h3>
                  <p className="text-gray-600">Add your first reminder to get started!</p>
                </div>
              ) : (
                <ul className="divide-y divide-gray-200">
                  {reminders.map((reminder) => (
                    <li key={reminder.id} className="px-6 py-4 hover:bg-gray-50">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <h3 className="text-lg font-semibold text-gray-900">{reminder.title}</h3>
                            <span className={`text-xs px-2 py-1 rounded-full ${
                              reminder.related_type === 'chore' 
                                ? 'bg-green-100 text-green-800' 
                                : 'bg-blue-100 text-blue-800'
                            }`}>
                              {reminder.related_type === 'chore' ? 'Chore' : 'Event'}
                            </span>
                          </div>
                          
                          <div className="text-sm text-gray-500 space-y-1">
                            <div>‚è∞ {formatDateTime(reminder.remind_at)}</div>
                            <div>üîó Related ID: {reminder.related_id}</div>
                            <div>üë§ Created by: {reminder.created_by.replace('user_', '').slice(0, 6)}...</div>
                          </div>
                        </div>
                        
                        {reminder.created_by === user?.id && (
                          <button
                            onClick={() => handleDeleteReminder(reminder.id)}
                            disabled={deletingReminder === reminder.id}
                            className="ml-4 text-red-600 hover:text-red-800 disabled:opacity-50 disabled:cursor-not-allowed"
                          >
                            {deletingReminder === reminder.id ? 'Deleting...' : 'üóëÔ∏è'}
                          </button>
                        )}
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// @/app/rewards/history/page.tsx
'use client'

import { useEffect, useState } from 'react'
import { getRewardHistory } from '@/lib/supabase/rewardHistory'
import { ProBadge } from '@/components/ProBadge'

// Simple date formatter as fallback
function formatDate(dateString: string) {
  try {
    const date = new Date(dateString)
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    })
  } catch (error) {
    return dateString
  }
}

export default function RewardHistoryPage() {
  const [history, setHistory] = useState<Array<{
    rewards: Array<{
      name: string;
      pro_only: boolean;
      xp_cost: number;
      coin_cost: number;
    }>;
    created_at: string;
    reward_id: string;
  }>>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        console.log('üîÑ Fetching user data...')
        const res = await fetch('/api/user-data')
        if (!res.ok) {
          throw new Error(`Failed to fetch user data: ${res.status}`)
        }
        const user = await res.json()
        console.log('üë§ User data:', user)
        
        if (!user?.id) {
          console.error('‚ùå No user ID found')
          setHistory([])
          return
        }
        
        console.log('üéØ Fetching reward history for user:', user.id)
        const data = await getRewardHistory(user.id)
        console.log('üìä Reward history result:', data)
        setHistory(data)
      } catch {
        console.error('‚ùå Error fetching reward history')
        setHistory([])
      } finally {
        setLoading(false)
      }
    }

    fetchHistory()
  }, [])

  if (loading) return <div className="p-4">Loading history...</div>

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">üéñÔ∏è Claimed Rewards</h1>
      <div className="space-y-4">
        {history.map((entry, i) => {
          const reward = entry.rewards[0]; // Get the first reward from the array
          if (!reward) return null; // Skip if no reward data
          
          return (
            <div key={i} className="p-4 rounded shadow bg-white">
              <div className="flex justify-between items-center">
                <div>
                  <div className="font-semibold text-lg">
                    {reward.name}{' '}
                    {reward.pro_only ? <ProBadge size="sm" /> : null}
                  </div>
                  <div className="text-sm text-gray-600">
                    Claimed on {formatDate(entry.created_at)}
                  </div>
                </div>
                <div className="text-right text-sm">
                  <div>üü° {reward.xp_cost} XP</div>
                  <div>ü™ô {reward.coin_cost} Coins</div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  )
}

'use client';

import { useAuth, useUser } from '@clerk/nextjs';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { getClaimedRewards } from '@/lib/supabase/rewards';
import { getRewards } from '@/lib/rewards';
import { canAccessFeature } from '@/lib/planFeatures';
import { ProBadge } from '@/components/ProBadge';

interface UserData {
  email: string;
  role: 'owner' | 'member';
  plan: 'free' | 'pro' | 'pro_plus';
  xp: number;
  coins: number;
  household: {
    id: string;
    plan: string;
    game_mode: string;
    created_at: string;
  };
}

interface Reward {
  id: string;
  title: string;
  cost_xp: number;
  cost_coins: number;
  pro_only: boolean;
  created_at: string;
}

export default function RewardsPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const { user } = useUser();
  const router = useRouter();
  
  const [userData, setUserData] = useState<UserData | null>(null);
  const [rewards, setRewards] = useState<Reward[]>([]);
  const [claimedRewards, setClaimedRewards] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [claimingReward, setClaimingReward] = useState<string | null>(null);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    async function fetchData() {
      if (!user?.id) return;

      try {
        setLoading(true);
        setError(null);

        // Fetch user data with cache busting
        const userResponse = await fetch('/api/user-data', {
          headers: {
            'Cache-Control': 'no-cache',
            'Pragma': 'no-cache'
          }
        });
        const userResult = await userResponse.json();

        if (!userResponse.ok) {
          console.error('Error fetching user data:', userResult.error);
          setError(userResult.error || 'Failed to load user data');
          return;
        }

        if (userResult.success && userResult.data) {
          const userDataObj = {
            email: userResult.data.email,
            role: userResult.data.role,
            plan: userResult.data.plan || 'free',
            xp: userResult.data.xp || 0,
            coins: userResult.data.coins || 0,
            household: userResult.data.household
          };
          
          setUserData(userDataObj);

          // Debug: Log user ID and household ID
          console.log("Your user ID:", user?.id);
          console.log("Your household ID:", userDataObj.household?.id);

          // Check if user can access rewards feature
          if (!canAccessFeature(userDataObj.plan, 'xp_rewards')) {
            router.push('/upgrade');
            return;
          }

          // Fetch rewards and claimed rewards in parallel
          const [rewardsData, claimedRewardsData] = await Promise.all([
            getRewards(userDataObj.household.id),
            getClaimedRewards(user.id)
          ]);

          setRewards(rewardsData);
          setClaimedRewards(claimedRewardsData);
        } else {
          setError('User not found in database');
        }
      } catch (err) {
        console.error('Exception fetching data:', err);
        setError('An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [isLoaded, isSignedIn, user?.id, router]);

  const handleClaimReward = async (rewardId: string) => {
    if (!user?.id) return;

    try {
      setClaimingReward(rewardId);
      
      // Send POST request to /api/claim-reward
      const response = await fetch('/api/claim-reward', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ rewardId }),
      });

      const result = await response.json();

      if (!response.ok) {
        console.error('Error claiming reward:', result.error);
        setError(result.error || 'Failed to claim reward');
        return;
      }

      // Success - refresh claimed rewards
      const updatedClaimedRewards = await getClaimedRewards(user.id);
      setClaimedRewards(updatedClaimedRewards);
      
      console.log('‚úÖ Reward claimed successfully:', result);
    } catch (err) {
      console.error('Error claiming reward:', err);
      setError('Failed to claim reward');
    } finally {
      setClaimingReward(null);
    }
  };

  const isRewardClaimed = (rewardId: string) => {
    return claimedRewards.includes(rewardId);
  };

  const canAffordReward = (reward: Reward) => {
    if (!userData) return false;
    return userData.xp >= reward.cost_xp && userData.coins >= reward.cost_coins;
  };

  const handleSyncUser = async () => {
    try {
      const response = await fetch('/api/sync-user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      const result = await response.json();
      console.log('Sync user response:', result);
    } catch (error) {
      console.error('Error syncing user:', error);
    }
  };

  // Show loading spinner while auth is loading or data is being fetched
  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  // This should not be reached if redirect is working, but just in case
  if (!isSignedIn) {
    return null;
  }

  // Show error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white shadow rounded-lg p-6 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
            <h1 className="text-xl font-semibold text-gray-900 mb-2">Error</h1>
            <p className="text-gray-600 mb-4">{error}</p>
            <button 
              onClick={() => {
          if (typeof window !== 'undefined') {
            window.location.reload();
          }
        }}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                 <div className="mb-8">
           <h1 className="text-3xl font-bold text-gray-900 mb-2">üéÅ Reward Store</h1>
           {userData ? <div className="flex items-center space-x-4 text-sm text-gray-600">
               <span>XP: {userData.xp}</span>
               <span>Coins: {userData.coins}</span>
             </div> : null}
           
           {/* Temporary debug button */}
           <div className="mt-4">
             <button
               onClick={handleSyncUser}
               className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition-colors"
             >
               üîÑ Sync User (Debug)
             </button>
           </div>
         </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {rewards.map((reward) => {
            const isClaimed = isRewardClaimed(reward.id);
            const canAfford = canAffordReward(reward);
            const isClaiming = claimingReward === reward.id;

            return (
              <div key={reward.id} className="bg-white rounded-lg shadow-md overflow-hidden relative">
                {reward.pro_only ? <div className="absolute top-2 right-2 z-10">
                    <ProBadge size="sm" />
                  </div> : null}
                
                <div className="p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    {reward.title}
                  </h3>
                  
                  <div className="space-y-2 mb-4">
                    {reward.cost_xp > 0 && (
                      <div className="flex items-center text-sm text-gray-600">
                        <span className="mr-2">üü°</span>
                        <span>{reward.cost_xp} XP</span>
                      </div>
                    )}
                    {reward.cost_coins > 0 && (
                      <div className="flex items-center text-sm text-gray-600">
                        <span className="mr-2">ü™ô</span>
                        <span>{reward.cost_coins} Coins</span>
                      </div>
                    )}
                  </div>

                  <button
                    onClick={() => handleClaimReward(reward.id)}
                    disabled={isClaimed || !canAfford || isClaiming}
                    className={`w-full py-2 px-4 rounded-md font-medium transition-colors ${
                      isClaimed
                        ? 'bg-gray-100 text-gray-500 cursor-not-allowed'
                        : !canAfford
                        ? 'bg-red-100 text-red-600 cursor-not-allowed'
                        : isClaiming
                        ? 'bg-blue-100 text-blue-600 cursor-not-allowed'
                        : 'bg-blue-600 text-white hover:bg-blue-700'
                    }`}
                  >
                    {isClaiming ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current mr-2" />
                        Claiming...
                      </div>
                    ) : isClaimed ? (
                      'Claimed'
                    ) : !canAfford ? (
                      'Insufficient Resources'
                    ) : (
                      'Claim Reward'
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>

        {rewards.length === 0 && !loading && (
          <div className="text-center py-12">
            <div className="text-gray-400 text-6xl mb-4">üéÅ</div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No rewards available</h3>
            <p className="text-gray-600">Check back later for new rewards!</p>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useAuth, useUser } from '@clerk/nextjs';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';


interface UserData {
  email: string;
  role: 'owner' | 'member';
  plan: 'free' | 'pro' | 'pro_plus';
}

export default function PlanSettingsPage() {
  const { isSignedIn, isLoaded } = useAuth();
  const { user } = useUser();
  const router = useRouter();
  
  const [userData, setUserData] = useState<UserData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [updating, setUpdating] = useState(false);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [billingLoading, setBillingLoading] = useState(false);

  useEffect(() => {
    if (!isLoaded) return;

    if (!isSignedIn) {
      router.push('/sign-in');
      return;
    }

    async function fetchUserData() {
      if (!user?.id) return;

      try {
        setLoading(true);
        setError(null);

        const response = await fetch('/api/user-data');
        const result = await response.json();

        if (!response.ok) {
          console.error('Error fetching user data:', result.error);
          setError(result.error || 'Failed to load user data');
          return;
        }

        if (result.success && result.data) {
          setUserData({
            email: result.data.email,
            role: result.data.role,
            plan: result.data.plan || 'free'
          });
        } else {
          setError('User not found in database');
        }
      } catch (err) {
        console.error('Exception fetching user data:', err);
        setError('An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    }

    fetchUserData();
  }, [isLoaded, isSignedIn, user?.id, router]);

  const handlePlanToggle = async () => {
    if (!userData || userData.role !== 'owner') return;

    // Cycle through plans: free -> pro -> pro_plus -> free
    let newPlan: 'free' | 'pro' | 'pro_plus';
    if (userData.plan === 'free') {
      newPlan = 'pro';
    } else if (userData.plan === 'pro') {
      newPlan = 'pro_plus';
    } else {
      newPlan = 'free';
    }
    
    try {
      setUpdating(true);
      setError(null);
      setSuccessMessage(null);

      const response = await fetch('/api/update-plan', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ plan: newPlan }),
      });

      const result = await response.json();

      if (!response.ok) {
        console.error('Error updating plan:', result.error);
        setError(result.error || 'Failed to update plan');
        return;
      }

      if (result.success) {
        setUserData(prev => prev ? { ...prev, plan: newPlan } : null);
        const planDisplayName = newPlan === 'pro_plus' ? 'Pro+' : newPlan === 'pro' ? 'Pro' : 'Free';
        setSuccessMessage(`Plan successfully updated to ${planDisplayName}!`);
      }
    } catch (err) {
      console.error('Exception updating plan:', err);
      setError('An unexpected error occurred while updating plan');
    } finally {
      setUpdating(false);
    }
  };

  const handleBillingPortal = async () => {
    if (!userData) return;

    setBillingLoading(true);
    
    try {
      const response = await fetch('/api/stripe/create-portal-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      if (data.success && data.url) {
        window.location.href = data.url;
      } else {
        console.error('Failed to create portal session:', data.error);
        alert('Failed to open billing portal. Please try again.');
      }
    } catch (error) {
      console.error('Error opening billing portal:', error);
      alert('An error occurred. Please try again.');
    } finally {
      setBillingLoading(false);
    }
  };

  // Show loading spinner while auth is loading or data is being fetched
  if (!isLoaded || loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  // This should not be reached if redirect is working, but just in case
  if (!isSignedIn) {
    return null;
  }

  // Check if user is owner
  if (userData && userData.role !== 'owner') {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white shadow rounded-lg p-6 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">üö´</div>
            <h1 className="text-xl font-semibold text-gray-900 mb-2">Access Denied</h1>
            <p className="text-gray-600 mb-4">Only household owners can manage plan settings.</p>
            <button 
              onClick={() => router.push('/dashboard')}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Show error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white shadow rounded-lg p-6 max-w-md w-full mx-4">
          <div className="text-center">
            <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
            <h1 className="text-xl font-semibold text-gray-900 mb-2">Error</h1>
            <p className="text-gray-600 mb-4">{error}</p>
            <button 
              onClick={() => {
          if (typeof window !== 'undefined') {
            window.location.reload();
          }
        }}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Show plan settings content
  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white shadow rounded-lg p-6">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Plan Settings
            </h1>
            <p className="text-gray-600">
              Manage your household's subscription plan
            </p>
          </div>

          {successMessage ? <div className="mb-6 bg-green-50 border border-green-200 rounded-md p-4">
              <div className="flex">
                <div className="flex-shrink-0">
                  <div className="text-green-400">‚úì</div>
                </div>
                <div className="ml-3">
                  <p className="text-sm text-green-800">{successMessage}</p>
                </div>
              </div>
            </div> : null}

          <div className="grid md:grid-cols-2 gap-8">
            {/* Current Plan Section */}
            <div className="bg-gray-50 rounded-lg p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Current Plan</h2>
              <div className="space-y-4">
                <div>
                  <p className="text-sm text-gray-600 mb-2">Your current plan is:</p>
                  <div className="flex items-center">
                    <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                      userData?.plan === 'pro_plus' 
                        ? 'bg-purple-100 text-purple-800' 
                        : userData?.plan === 'pro'
                        ? 'bg-blue-100 text-blue-800'
                        : 'bg-gray-100 text-gray-800'
                    }`}>
                      {userData?.plan === 'pro_plus' ? '‚≠ê Pro+' : userData?.plan === 'pro' ? '‚≠ê Pro' : 'üÜì Free'}
                    </span>
                  </div>
                </div>

                <div className="pt-4 space-y-3">
                  {userData?.plan !== 'free' && (
                    <button
                      onClick={handleBillingPortal}
                      disabled={billingLoading}
                      className={`w-full px-4 py-2 rounded-md font-medium transition-colors ${
                        billingLoading
                          ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                          : 'bg-green-600 text-white hover:bg-green-700'
                      }`}
                    >
                      {billingLoading ? 'Loading...' : 'Manage Billing'}
                    </button>
                  )}
                  
                  <button
                    onClick={handlePlanToggle}
                    disabled={updating}
                    className={`w-full px-4 py-2 rounded-md font-medium transition-colors ${
                      updating
                        ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                        : 'bg-blue-600 text-white hover:bg-blue-700'
                    }`}
                  >
                    {updating ? 'Updating...' : `Switch to ${userData?.plan === 'free' ? 'Pro' : userData?.plan === 'pro' ? 'Pro+' : 'Free'}`}
                  </button>
                </div>
              </div>
            </div>

            {/* Plan Comparison */}
            <div className="bg-gray-50 rounded-lg p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Plan Features</h2>
              <div className="space-y-6">
                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Free Plan</h3>
                  <ul className="text-sm text-gray-600 space-y-1">
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Shopping List
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Calendar & Events
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Chores
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Basic Reminders
                    </li>
                    <li className="flex items-center">
                      <span className="text-red-500 mr-2">‚úó</span>
                      Finance Tracking
                    </li>
                    <li className="flex items-center">
                      <span className="text-red-500 mr-2">‚úó</span>
                      Advanced Analytics
                    </li>
                  </ul>
                </div>

                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Pro Plan</h3>
                  <ul className="text-sm text-gray-600 space-y-1">
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Everything in Free
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Finance Tracking
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Advanced Analytics
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      AI Features
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Google Calendar Read
                    </li>
                    <li className="flex items-center">
                      <span className="text-red-500 mr-2">‚úó</span>
                      Multi-household
                    </li>
                  </ul>
                </div>

                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Pro+ Plan</h3>
                  <ul className="text-sm text-gray-600 space-y-1">
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Everything in Pro
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Two-way Google Calendar Sync
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Multi-household (up to 3)
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Admin Tools
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Unlimited Automations
                    </li>
                    <li className="flex items-center">
                      <span className="text-green-500 mr-2">‚úì</span>
                      Priority Support
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>

          <div className="mt-8 text-center">
            <button 
              onClick={() => router.push('/dashboard')}
              className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
            >
              Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useAuth } from '@clerk/nextjs';
import { useRouter, useParams } from 'next/navigation';
import { toast } from 'sonner';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { 
  ArrowLeft, 
  Plus, 
  ShoppingCart, 
  Brain, 
  CheckCircle, 
  Clock,
  Edit,
  Trash2,
  X,
  Sparkles
} from 'lucide-react';
import { fetchWithCSRF } from '@/lib/csrf-client';

interface ShoppingItem {
  id: string;
  name: string;
  quantity: number;
  unit: string;
  category: string;
  is_completed: boolean;
  notes?: string;
  created_at: string;
  updated_at: string;
}

interface ShoppingList {
  id: string;
  name: string;
  description?: string;
  household_id: string;
  created_by: string;
  created_at: string;
  is_completed: boolean;
  total_items: number;
  completed_items: number;
  ai_suggestions_count: number;
  ai_confidence: number;
}

export default function ShoppingListDetailPage() {
  const { userId } = useAuth();
  const router = useRouter();
  const params = useParams();
  const listId = params.id as string;
  
  const [list, setList] = useState<ShoppingList | null>(null);
  const [items, setItems] = useState<ShoppingItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [showAddItemModal, setShowAddItemModal] = useState(false);
  const [showEditListModal, setShowEditListModal] = useState(false);
  const [newItem, setNewItem] = useState({
    name: '',
    quantity: 1,
    unit: '',
    category: 'general',
    notes: ''
  });
  const [editListData, setEditListData] = useState({
    name: '',
    description: ''
  });

  const categories = [
    'general', 'produce', 'dairy', 'meat', 'pantry', 'frozen', 'bakery', 'beverages', 'snacks', 'health', 'household'
  ];

  useEffect(() => {
    if (listId) {
      fetchListDetails();
    }
  }, [listId]);

  const fetchListDetails = async () => {
    try {
      // Fetch list details
      const listResponse = await fetch(`/api/shopping-lists/${listId}`);
      if (listResponse.ok) {
        const listData = await listResponse.json();
        // Handle the correct API response structure
        const list = listData.data?.list || listData.list;
        if (list) {
          setList(list);
          setEditListData({
            name: list.name || list.title, // Handle both 'name' and 'title' fields
            description: list.description || ''
          });
        } else {
          console.error('List not found in response:', listData);
          setError('List not found');
        }
      } else {
        setError('Failed to load shopping list');
      }

      // Fetch items
      const itemsResponse = await fetch(`/api/shopping-lists/${listId}/items`);
      if (itemsResponse.ok) {
        const itemsData = await itemsResponse.json();
        setItems(itemsData.items || []);
      }
    } catch (error) {
      console.error('Error fetching list details:', error);
      toast.error('Failed to load shopping list');
    } finally {
      setLoading(false);
    }
  };

  const handleAddItem = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newItem.name.trim()) return;

    try {
      const response = await fetch(`/api/shopping-lists/${listId}/items`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newItem),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          toast.success('Item added successfully!');
          setItems(prev => [...prev, result.item]);
          setNewItem({ name: '', quantity: 1, unit: '', category: 'general', notes: '' });
          setShowAddItemModal(false);
          fetchListDetails(); // Refresh list stats
        } else {
          toast.error(`Failed to add item: ${result.error || 'Unknown error'}`);
        }
      } else {
        const error = await response.json();
        toast.error(`Failed to add item: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      toast.error('Failed to add item. Please try again.');
      console.error('Error adding item:', error);
    }
  };

  const handleToggleItem = async (itemId: string, isCompleted: boolean) => {
    try {
      const response = await fetch(`/api/shopping-items/toggle`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ itemId, isCompleted }),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          setItems(prev => prev.map(item => 
            item.id === itemId ? { ...item, is_completed: isCompleted } : item
          ));
          fetchListDetails(); // Refresh list stats
        } else {
          toast.error(`Failed to update item: ${result.error || 'Unknown error'}`);
        }
      } else {
        const error = await response.json();
        toast.error(`Failed to update item: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      toast.error('Failed to update item. Please try again.');
      console.error('Error updating item:', error);
    }
  };

  const handleDeleteItem = async (itemId: string) => {
    if (!confirm('Are you sure you want to delete this item?')) return;

    try {
      const response = await fetchWithCSRF(`/api/shopping-lists/${listId}/items/${itemId}`, {
        method: 'DELETE',
        credentials: 'include'
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          toast.success('Item deleted successfully!');
          setItems(prev => prev.filter(item => item.id !== itemId));
          fetchListDetails(); // Refresh list stats
        } else {
          toast.error(`Failed to delete item: ${result.error || 'Unknown error'}`);
        }
      } else {
        const error = await response.json();
        toast.error(`Failed to delete item: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      toast.error('Failed to delete item. Please try again.');
      console.error('Error deleting item:', error);
    }
  };

  const handleEditList = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editListData.name.trim()) return;

    try {
      const response = await fetch(`/api/shopping-lists/${listId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editListData),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          toast.success('List updated successfully!');
          setList(prev => prev ? { ...prev, ...editListData } : null);
          setShowEditListModal(false);
        } else {
          toast.error(`Failed to update list: ${result.error || 'Unknown error'}`);
        }
      } else {
        const error = await response.json();
        toast.error(`Failed to update list: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      toast.error('Failed to update list. Please try again.');
      console.error('Error updating list:', error);
    }
  };

  const getCompletionPercentage = () => {
    if (items.length === 0) return 0;
    const completed = items.filter(item => item.is_completed).length;
    return Math.round((completed / items.length) * 100);
  };

  const getCategoryColor = (category: string) => {
    const colors: Record<string, string> = {
      produce: 'bg-green-100 text-green-800',
      dairy: 'bg-blue-100 text-blue-800',
      meat: 'bg-red-100 text-red-800',
      pantry: 'bg-yellow-100 text-yellow-800',
      frozen: 'bg-purple-100 text-purple-800',
      bakery: 'bg-orange-100 text-orange-800',
      beverages: 'bg-cyan-100 text-cyan-800',
      snacks: 'bg-pink-100 text-pink-800',
      health: 'bg-indigo-100 text-indigo-800',
      household: 'bg-gray-100 text-gray-800',
      general: 'bg-gray-100 text-gray-800'
    };
    return colors[category] || colors.general;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <ShoppingCart className="h-12 w-12 animate-pulse mx-auto mb-4 text-blue-500" />
          <p className="text-lg text-gray-600">Loading shopping list...</p>
        </div>
      </div>
    );
  }

  if (!list) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <ShoppingCart className="h-16 w-16 mx-auto mb-6 text-red-500" />
          <h2 className="text-2xl font-bold text-gray-900 mb-4">List Not Found</h2>
          <p className="text-gray-600 mb-6">
            The shopping list you're looking for doesn't exist or you don't have access to it.
          </p>
          <Button onClick={() => router.push('/shopping-lists')}>
            Back to Shopping Lists
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Button
                variant="outline"
                onClick={() => router.push('/shopping-lists')}
                className="flex items-center gap-2"
              >
                <ArrowLeft className="h-4 w-4" />
                Back to Lists
              </Button>
              <div>
                <h1 className="text-3xl font-bold text-gray-900">{list.name}</h1>
                {list.description && (
                  <p className="text-gray-600 mt-2">{list.description}</p>
                )}
              </div>
            </div>
            <div className="flex items-center gap-3">
              <Button
                variant="outline"
                onClick={() => setShowEditListModal(true)}
                className="flex items-center gap-2"
              >
                <Edit className="h-4 w-4" />
                Edit List
              </Button>
              <Button
                onClick={() => setShowAddItemModal(true)}
                className="bg-blue-600 hover:bg-blue-700 flex items-center gap-2"
              >
                <Plus className="h-4 w-4" />
                Add Item
              </Button>
            </div>
          </div>
        </div>

        {/* Progress Card */}
        <Card className="mb-8">
          <CardContent className="pt-6">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="text-lg font-medium">Progress</h3>
                <p className="text-sm text-gray-600">
                  {items.filter(item => item.is_completed).length} of {items.length} items completed
                </p>
              </div>
              <div className="text-right">
                <div className="text-2xl font-bold text-blue-600">{getCompletionPercentage()}%</div>
                <div className="text-sm text-gray-500">Complete</div>
              </div>
            </div>
            <Progress value={getCompletionPercentage()} className="h-2" />
          </CardContent>
        </Card>

        {/* Items List */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <ShoppingCart className="h-5 w-5" />
              Items ({items.length})
            </CardTitle>
            <CardDescription>
              Manage your shopping list items
            </CardDescription>
          </CardHeader>
          <CardContent>
            {items.length > 0 ? (
              <div className="space-y-3">
                {items.map((item) => (
                  <div
                    key={item.id}
                    className={`flex items-center gap-4 p-4 border rounded-lg ${
                      item.is_completed ? 'bg-green-50 border-green-200' : 'bg-white border-gray-200'
                    }`}
                  >
                    <button
                      onClick={() => handleToggleItem(item.id, !item.is_completed)}
                      className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${
                        item.is_completed
                          ? 'bg-green-500 border-green-500 text-white'
                          : 'border-gray-300 hover:border-green-500'
                      }`}
                    >
                      {item.is_completed && <CheckCircle className="h-4 w-4" />}
                    </button>
                    
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-1">
                        <h4 className={`font-medium ${item.is_completed ? 'line-through text-gray-500' : 'text-gray-900'}`}>
                          {item.name}
                        </h4>
                        <Badge className={getCategoryColor(item.category)}>
                          {item.category}
                        </Badge>
                        {item.quantity > 1 && (
                          <span className="text-sm text-gray-600">
                            {item.quantity} {item.unit}
                          </span>
                        )}
                      </div>
                      {item.notes && (
                        <p className="text-sm text-gray-600">{item.notes}</p>
                      )}
                    </div>
                    
                    <div className="flex items-center gap-2">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeleteItem(item.id)}
                        className="text-red-600 hover:text-red-700 hover:bg-red-50"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <ShoppingCart className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No items yet</h3>
                <p className="text-gray-500 mb-6">
                  Add your first item to get started with this shopping list
                </p>
                <Button
                  onClick={() => setShowAddItemModal(true)}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Add First Item
                </Button>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Add Item Modal */}
        {showAddItemModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-xl font-semibold">Add New Item</h2>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowAddItemModal(false)}
                  className="h-8 w-8 p-0"
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
              
              <form onSubmit={handleAddItem} className="space-y-4">
                <div>
                  <Label htmlFor="item-name">Item Name *</Label>
                  <Input
                    id="item-name"
                    value={newItem.name}
                    onChange={(e) => setNewItem(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="e.g., Milk"
                    className="mt-1"
                    required
                  />
                </div>
                
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="quantity">Quantity</Label>
                    <Input
                      id="quantity"
                      type="number"
                      min="1"
                      value={newItem.quantity}
                      onChange={(e) => setNewItem(prev => ({ ...prev, quantity: parseInt(e.target.value) || 1 }))}
                      className="mt-1"
                    />
                  </div>
                  <div>
                    <Label htmlFor="unit">Unit</Label>
                    <Input
                      id="unit"
                      value={newItem.unit}
                      onChange={(e) => setNewItem(prev => ({ ...prev, unit: e.target.value }))}
                      placeholder="e.g., liters"
                      className="mt-1"
                    />
                  </div>
                </div>
                
                <div>
                  <Label htmlFor="category">Category</Label>
                  <select
                    id="category"
                    value={newItem.category}
                    onChange={(e) => setNewItem(prev => ({ ...prev, category: e.target.value }))}
                    className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  >
                    {categories.map((category) => (
                      <option key={category} value={category}>
                        {category.charAt(0).toUpperCase() + category.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div>
                  <Label htmlFor="notes">Notes (optional)</Label>
                  <Textarea
                    id="notes"
                    value={newItem.notes}
                    onChange={(e) => setNewItem(prev => ({ ...prev, notes: e.target.value }))}
                    placeholder="Any additional notes..."
                    className="mt-1"
                    rows={2}
                  />
                </div>
                
                <div className="flex gap-3 pt-2">
                  <Button
                    type="submit"
                    disabled={!newItem.name.trim()}
                    className="flex-1"
                  >
                    Add Item
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setShowAddItemModal(false)}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Edit List Modal */}
        {showEditListModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-xl font-semibold">Edit List</h2>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowEditListModal(false)}
                  className="h-8 w-8 p-0"
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
              
              <form onSubmit={handleEditList} className="space-y-4">
