'use client';

import { useState } from 'react';
import Link from 'next/link';
import { formatDistanceToNow } from 'date-fns';

interface HouseholdEvent {
  id: string;
  type: string;
  source: string;
  payload: Record<string, any>;
  occurred_at: string;
  created_at: string;
}

interface EventRowProps {
  event: HouseholdEvent;
}

export default function EventRow({ event }: EventRowProps) {
  const [showPayload, setShowPayload] = useState(false);

  const formatEventType = (type: string) => {
    return type.split('.').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const formatSource = (source: string) => {
    if (source === 'app') return 'App';
    if (source.startsWith('webhook:')) return source.replace('webhook:', 'Webhook: ');
    if (source === 'automation') return 'Automation';
    return source;
  };

  const getEventIcon = (type: string) => {
    if (type.includes('bill')) return 'üí∞';
    if (type.includes('chore')) return '‚úÖ';
    if (type.includes('shopping')) return 'üõí';
    if (type.includes('heartbeat')) return 'üíì';
    if (type.includes('notify')) return 'üîî';
    return 'üìã';
  };

  const getEventColor = (type: string) => {
    if (type.includes('bill')) return 'text-blue-600 bg-blue-100';
    if (type.includes('chore')) return 'text-green-600 bg-green-100';
    if (type.includes('shopping')) return 'text-purple-600 bg-purple-100';
    if (type.includes('heartbeat')) return 'text-gray-600 bg-gray-100';
    if (type.includes('notify')) return 'text-orange-600 bg-orange-100';
    return 'text-gray-600 bg-gray-100';
  };

  return (
    <div className="px-6 py-4 hover:bg-gray-50 transition-colors">
      <div className="flex items-start space-x-3">
        <div className="flex-shrink-0">
          <span className="text-2xl">{getEventIcon(event.type)}</span>
        </div>
        
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getEventColor(event.type)}`}>
                {formatEventType(event.type)}
              </span>
              <span className="text-sm text-gray-500">
                from {formatSource(event.source)}
              </span>
            </div>
            
            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-500">
                {formatDistanceToNow(new Date(event.occurred_at), { addSuffix: true })}
              </span>
              
              <button
                onClick={() => setShowPayload(!showPayload)}
                className="text-sm text-blue-600 hover:text-blue-800"
              >
                {showPayload ? 'Hide' : 'Show'} Details
              </button>
              
              <Link
                href={`/inbox/${event.id}`}
                className="text-sm text-blue-600 hover:text-blue-800"
              >
                View Full
              </Link>
            </div>
          </div>
          
          {showPayload && (
            <div className="mt-3 p-3 bg-gray-50 rounded-md">
              {Object.keys(event.payload).length > 0 ? (
                <pre className="text-xs text-gray-700 whitespace-pre-wrap">
                  {JSON.stringify(event.payload, null, 2)}
                </pre>
              ) : (
                <div className="text-sm text-gray-500">
                  No additional data
                </div>
              )}
            </div>
          )}
          
          {!showPayload && Object.keys(event.payload).length === 0 && (
            <div className="mt-1 text-sm text-gray-500">
              No additional data
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { createBrowserClient } from '@supabase/ssr';
import { Database } from '@/types/supabase';
import EventRow from './EventRow';

interface HouseholdEvent {
  id: string;
  type: string;
  source: string;
  payload: Record<string, any>;
  occurred_at: string;
  created_at: string;
}

interface InboxEventsProps {
  householdId: string;
}

export default function InboxEvents({ householdId }: InboxEventsProps) {
  const [events, setEvents] = useState<HouseholdEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedType, setSelectedType] = useState<string>('all');
  const [eventTypes, setEventTypes] = useState<string[]>([]);

  const supabase = createBrowserClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
  );

  useEffect(() => {
    fetchEvents();
    fetchEventTypes();
  }, [householdId, selectedType]);

  const fetchEvents = async () => {
    try {
      setLoading(true);
      
      let query = supabase
        .from('household_events')
        .select('*')
        .eq('household_id', householdId)
        .order('occurred_at', { ascending: false })
        .limit(100);

      if (selectedType !== 'all') {
        query = query.eq('type', selectedType);
      }

      const { data, error } = await query;

      if (error) throw error;
      
      setEvents(data || []);
    } catch (err) {
      console.error('Error fetching events:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch events');
    } finally {
      setLoading(false);
    }
  };

  const fetchEventTypes = async () => {
    try {
      const { data, error } = await supabase
        .from('household_events')
        .select('type')
        .eq('household_id', householdId);

      if (error) throw error;
      
      const types = [...new Set(data?.map(e => e.type) || [])];
      setEventTypes(types);
    } catch (err) {
      console.error('Error fetching event types:', err);
    }
  };

  const handleRefresh = () => {
    fetchEvents();
    fetchEventTypes();
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error loading events</h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
            <div className="mt-4">
              <button
                onClick={handleRefresh}
                className="bg-red-100 text-red-800 px-3 py-2 rounded-md text-sm font-medium hover:bg-red-200"
              >
                Try again
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white shadow rounded-lg">
      <div className="px-6 py-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-medium text-gray-900">Recent Events</h2>
          <div className="flex items-center space-x-4">
            <select
              value={selectedType}
              onChange={(e) => setSelectedType(e.target.value)}
              className="block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
            >
              <option value="all">All Event Types</option>
              {eventTypes.map((type) => (
                <option key={type} value={type}>
                  {type}
                </option>
              ))}
            </select>
            <button
              onClick={handleRefresh}
              className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Refresh
            </button>
          </div>
        </div>
      </div>

      <div className="divide-y divide-gray-200">
        {events.length === 0 ? (
          <div className="px-6 py-12 text-center">
            <div className="text-gray-500">
              {selectedType === 'all' ? 'No events found' : `No events of type "${selectedType}" found`}
            </div>
          </div>
        ) : (
          events.map((event) => (
            <EventRow key={event.id} event={event} />
          ))
        )}
      </div>

      {events.length > 0 && (
        <div className="px-6 py-3 bg-gray-50 text-xs text-gray-500 text-center">
          Showing {events.length} most recent events
        </div>
      )}
    </div>
  );
}

'use client';

import React, { Suspense, lazy, ComponentType } from 'react';
import { LoadingSpinner } from '@/components/ui/LoadingSpinner';
import { ErrorBoundary } from '@/components/ErrorBoundary';

interface LazyPageWrapperProps {
  fallback?: React.ReactNode;
  errorFallback?: React.ReactNode;
}

// Higher-order component for lazy loading pages
export function withLazyLoading<P extends object>(
  Component: ComponentType<P>,
  fallback?: React.ReactNode
) {
  const LazyComponent = lazy(() => 
    Promise.resolve({ default: Component })
  );

  return function LazyPageWrapper(props: P & LazyPageWrapperProps) {
    const { fallback: customFallback, errorFallback, ...componentProps } = props;
    
    const defaultFallback = fallback || customFallback || (
      <div className="flex items-center justify-center min-h-screen">
        <LoadingSpinner size="lg" text="Loading page..." />
      </div>
    );

    return (
      <ErrorBoundary fallback={errorFallback}>
        <Suspense fallback={defaultFallback}>
          <LazyComponent {...(componentProps as P)} />
        </Suspense>
      </ErrorBoundary>
    );
  };
}

// Hook for lazy loading components
export function useLazyComponent<T extends ComponentType<any>>(
  importFn: () => Promise<{ default: T }>,
  fallback?: React.ReactNode
) {
  const [Component, setComponent] = React.useState<T | null>(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<Error | null>(null);

  React.useEffect(() => {
    importFn()
      .then((module) => {
        setComponent(() => module.default);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, [importFn]);

  if (loading) {
    return { Component: null, loading: true, error: null, fallback };
  }

  if (error) {
    return { Component: null, loading: false, error, fallback };
  }

  return { Component, loading: false, error: null, fallback };
}

// Lazy page wrapper component
export function LazyPageWrapper({ 
  children, 
  fallback, 
  errorFallback 
}: LazyPageWrapperProps & { children: React.ReactNode }) {
  const defaultFallback = fallback || (
    <div className="flex items-center justify-center min-h-screen">
      <LoadingSpinner size="lg" text="Loading page..." />
    </div>
  );

  return (
    <ErrorBoundary fallback={errorFallback}>
      <Suspense fallback={defaultFallback}>
        {children}
      </Suspense>
    </ErrorBoundary>
  );
}

// Preload function for critical pages
export function preloadPage(importFn: () => Promise<any>) {
  return importFn();
}

// Lazy page components with optimized loading
export const LazyDashboard = withLazyLoading(
  lazy(() => import('@/app/dashboard/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading dashboard..." />
  </div>
);

export const LazyShoppingLists = withLazyLoading(
  lazy(() => import('@/app/shopping-lists/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading shopping lists..." />
  </div>
);

export const LazyChores = withLazyLoading(
  lazy(() => import('@/app/chores/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading chores..." />
  </div>
);

export const LazyMealPlanner = withLazyLoading(
  lazy(() => import('@/app/meal-planner/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading meal planner..." />
  </div>
);

export const LazyRecipes = withLazyLoading(
  lazy(() => import('@/app/recipes/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading recipes..." />
  </div>
);

export const LazyCalendar = withLazyLoading(
  lazy(() => import('@/app/calendar/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading calendar..." />
  </div>
);

export const LazyBills = withLazyLoading(
  lazy(() => import('@/app/bills/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading bills..." />
  </div>
);

export const LazyRewards = withLazyLoading(
  lazy(() => import('@/app/rewards/page')),
  <div className="flex items-center justify-center min-h-screen">
    <LoadingSpinner size="lg" text="Loading rewards..." />
  </div>
);

// Lazy component components
export const LazyPWAInstallPrompt = lazy(() => import('@/components/PWAInstallPrompt'));
export const LazyPWAStatus = lazy(() => import('@/components/PWAStatus'));
export const LazyPushNotificationSetup = lazy(() => import('@/components/PushNotificationSetup'));
export const LazyHeartbeatProvider = lazy(() => import('@/components/HeartbeatProvider'));

// Lazy heavy components
export const LazyErrorBoundary = lazy(() => import('@/components/ErrorBoundary'));
export const LazyNavBar = lazy(() => import('@/components/NavBar'));
export const LazySyncUserClient = lazy(() => import('@/components/SyncUserClient'));

'use client';

import { useState, useEffect } from 'react';
import { useAuth } from '@clerk/nextjs';

export default function NotificationPermission() {
  const [permission, setPermission] = useState<NotificationPermission>('default');
  const [isSupported, setIsSupported] = useState(false);

  const [showPrompt, setShowPrompt] = useState(false);
  const { userId } = useAuth();

  useEffect(() => {
    // Check if notifications and service workers are supported
    if ('Notification' in window && 'serviceWorker' in navigator && 'PushManager' in window) {
      setIsSupported(true);
      setPermission(Notification.permission);
      
      // Show prompt after delay if not granted and user is signed in
      if (Notification.permission === 'default' && userId) {
        const timer = setTimeout(() => {
          setShowPrompt(true);
        }, 15000); // Show after 15 seconds
        
        return () => clearTimeout(timer);
      }
    }
    
    // Return empty cleanup function for cases where no timer is set
    return () => {};
  }, [userId]);

  const requestPermission = async () => {
    if (!isSupported) return;

    try {
      const permission = await Notification.requestPermission();
      setPermission(permission);
      
      if (permission === 'granted') {
        await subscribeToNotifications();
        setShowPrompt(false);
      }
    } catch (error) {
      console.error('Error requesting notification permission:', error);
    }
  };

  const subscribeToNotifications = async () => {
    if (!isSupported || !userId) return;

    try {
      const registration = await navigator.serviceWorker.ready;
      
      const subscription = await registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: urlB64ToUint8Array(process.env.NEXT_PUBLIC_VAPID_PUBLIC_KEY!)
      });

      // Send subscription to server
      const response = await fetch('/api/notifications/subscribe', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          subscription,
          userId
        }),
      });

      if (response.ok) {
        console.log('Successfully subscribed to push notifications');
      }
    } catch (error) {
      console.error('Error subscribing to notifications:', error);
    }
  };

  const dismissPrompt = () => {
    setShowPrompt(false);
    // Don't show again for this session
    sessionStorage.setItem('notification-prompt-dismissed', 'true');
  };

  // Don't show if not supported, already granted, or dismissed this session
  if (!isSupported || 
      permission === 'granted' || 
      permission === 'denied' || 
      !showPrompt ||
      sessionStorage.getItem('notification-prompt-dismissed')) {
    return null;
  }

  return (
    <div className="fixed bottom-4 left-4 right-4 z-50 mx-auto max-w-sm">
      <div className="bg-white border border-gray-200 rounded-lg shadow-lg p-4">
        <div className="flex items-start space-x-3">
          <div className="flex-shrink-0">
            <div className="w-10 h-10 bg-blue-600 rounded-lg flex items-center justify-center">
              <span className="text-white text-xl">üîî</span>
            </div>
          </div>
          <div className="flex-1 min-w-0">
            <h3 className="text-sm font-medium text-gray-900">
              Enable Notifications
            </h3>
            <p className="text-xs text-gray-500 mt-1">
              Get reminders for chores, meal planning, and household tasks!
            </p>
            <div className="mt-3 flex space-x-2">
              <button
                onClick={requestPermission}
                className="bg-blue-600 text-white px-3 py-1 rounded text-xs font-medium hover:bg-blue-700 transition-colors"
              >
                Enable
              </button>
              <button
                onClick={dismissPrompt}
                className="bg-gray-100 text-gray-700 px-3 py-1 rounded text-xs font-medium hover:bg-gray-200 transition-colors"
              >
                Not now
              </button>
            </div>
          </div>
          <button
            onClick={dismissPrompt}
            className="flex-shrink-0 text-gray-400 hover:text-gray-600"
            aria-label="Close"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}

// Helper function to convert VAPID key
function urlB64ToUint8Array(base64String: string) {
  const padding = '='.repeat((4 - base64String.length % 4) % 4);
  const base64 = (base64String + padding)
    .replace(/-/g, '+')
    .replace(/_/g, '/');

  const rawData = window.atob(base64);
  const outputArray = new Uint8Array(rawData.length);

  for (let i = 0; i < rawData.length; ++i) {
    outputArray[i] = rawData.charCodeAt(i);
  }
  return outputArray;
}

'use client';

import { useState, useEffect } from 'react';
import { usePWAPerformance } from '@/lib/performance';

interface BeforeInstallPromptEvent extends Event {
  prompt(): Promise<void>;
  userChoice: Promise<{
    outcome: 'accepted' | 'dismissed';
    platform: string;
  }>;
}

export default function PWAInstallPrompt() {
  const [deferredPrompt, setDeferredPrompt] = useState<BeforeInstallPromptEvent | null>(null);
  const [showPrompt, setShowPrompt] = useState(false);
  const [isInstalled, setIsInstalled] = useState(false);
  const { trackInstall } = usePWAPerformance();

  useEffect(() => {
    // Check if app is already installed
    const checkIfInstalled = () => {
      if (window.matchMedia('(display-mode: standalone)').matches) {
        setIsInstalled(true);
      }
      
      // Check for iOS Safari standalone mode
      if ((window.navigator as any).standalone === true) {
        setIsInstalled(true);
      }
    };

    checkIfInstalled();

    // Listen for the beforeinstallprompt event
    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      setDeferredPrompt(e as BeforeInstallPromptEvent);
      
      // Show prompt after a delay to not be too aggressive
      setTimeout(() => {
        setShowPrompt(true);
      }, 10000); // 10 seconds delay
    };

    // Listen for app installed event
    const handleAppInstalled = () => {
      setIsInstalled(true);
      setShowPrompt(false);
      setDeferredPrompt(null);
    };

    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    window.addEventListener('appinstalled', handleAppInstalled);

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      window.removeEventListener('appinstalled', handleAppInstalled);
    };
  }, []);

  const handleInstall = async () => {
    if (!deferredPrompt) return;

    deferredPrompt.prompt();
    const { outcome } = await deferredPrompt.userChoice;
    
    // Track installation outcome
    trackInstall(outcome);
    
    if (outcome === 'accepted') {
      console.log('PWA installation accepted');
    } else {
      console.log('PWA installation dismissed');
    }
    
    setDeferredPrompt(null);
    setShowPrompt(false);
  };

  const handleDismiss = () => {
    setShowPrompt(false);
    // Don't show again for this session
    sessionStorage.setItem('pwa-prompt-dismissed', 'true');
  };

  // Don't show if already installed or dismissed this session
  if (isInstalled || !showPrompt || sessionStorage.getItem('pwa-prompt-dismissed')) {
    return null;
  }

  return (
    <div className="fixed bottom-4 left-4 right-4 z-50 mx-auto max-w-sm">
      <div className="bg-white border border-gray-200 rounded-lg shadow-lg p-4">
        <div className="flex items-start space-x-3">
          <div className="flex-shrink-0">
            <div className="w-10 h-10 bg-blue-600 rounded-lg flex items-center justify-center">
              <span className="text-white text-xl">üè†</span>
            </div>
          </div>
          <div className="flex-1 min-w-0">
            <h3 className="text-sm font-medium text-gray-900">
              Install Home Manager
            </h3>
            <p className="text-xs text-gray-500 mt-1">
              Get quick access and offline features by installing our app!
            </p>
            <div className="mt-3 flex space-x-2">
              <button
                onClick={handleInstall}
                className="bg-blue-600 text-white px-3 py-1 rounded text-xs font-medium hover:bg-blue-700 transition-colors"
              >
                Install
              </button>
              <button
                onClick={handleDismiss}
                className="bg-gray-100 text-gray-700 px-3 py-1 rounded text-xs font-medium hover:bg-gray-200 transition-colors"
              >
                Not now
              </button>
            </div>
          </div>
          <button
            onClick={handleDismiss}
            className="flex-shrink-0 text-gray-400 hover:text-gray-600"
            aria-label="Close"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';

export default function PWAStatus() {
  const [isOnline, setIsOnline] = useState(true);
  const [updateAvailable, setUpdateAvailable] = useState(false);

  useEffect(() => {
    // Check online/offline status
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    setIsOnline(navigator.onLine);
    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    // Check for service worker updates
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.addEventListener('controllerchange', () => {
        setUpdateAvailable(true);
      });
    }

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const handleUpdate = () => {
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.getRegistration().then(registration => {
        if (registration && registration.waiting) {
          registration.waiting.postMessage({ type: 'SKIP_WAITING' });
          if (typeof window !== 'undefined') {
          window.location.reload();
        }
        }
      });
    }
  };

  if (!isOnline) {
    return (
      <div className="fixed top-0 left-0 right-0 z-50 bg-yellow-500 text-white px-4 py-2 text-center text-sm">
        <span className="font-medium">üì± You're offline</span>
        <span className="ml-2">Some features may be limited</span>
      </div>
    );
  }

  if (updateAvailable) {
    return (
      <div className="fixed top-0 left-0 right-0 z-50 bg-blue-600 text-white px-4 py-2 text-center text-sm">
        <span className="font-medium">üöÄ Update available!</span>
        <button
          onClick={handleUpdate}
          className="ml-2 underline hover:no-underline"
        >
          Refresh to update
        </button>
      </div>
    );
  }

  return null;
}

'use client';

import { useEffect } from 'react';
import { useUser } from '@clerk/nextjs';

const VAPID_PUBLIC_KEY = process.env.NEXT_PUBLIC_VAPID_PUBLIC_KEY;

export default function PushNotificationSetup() {
  const { user, isLoaded } = useUser();

  useEffect(() => {
    if (!isLoaded || !user) return;

    const setupPushNotifications = async () => {
      // Check if browser supports notifications and service workers
      if (!('Notification' in window) || !('serviceWorker' in navigator) || !('PushManager' in window)) {
        console.log('Push notifications not supported');
        return;
      }

      try {
        // Register service worker
        console.log('Registering service worker...');
        const registration = await navigator.serviceWorker.register('/sw.js', {
          scope: '/'
        });
        
        console.log('Service Worker registered successfully:', registration);

        // Wait for service worker to be ready
        await navigator.serviceWorker.ready;
        console.log('Service Worker is ready');

        // Check if already subscribed
        const existingSubscription = await registration.pushManager.getSubscription();
        if (existingSubscription) {
          console.log('Already subscribed to push notifications');
          return;
        }

        // Request notification permission if needed
        if (Notification.permission === 'default') {
          console.log('Requesting notification permission...');
          const permission = await Notification.requestPermission();
          console.log('Notification permission:', permission);
          
          if (permission !== 'granted') {
            console.log('Notification permission denied');
            return;
          }
        }

        // Create push subscription if permission granted
        if (Notification.permission === 'granted' && VAPID_PUBLIC_KEY) {
          console.log('Creating push subscription...');
          
          const applicationServerKey = urlBase64ToUint8Array(VAPID_PUBLIC_KEY);
          const subscription = await registration.pushManager.subscribe({
            userVisibleOnly: true,
            applicationServerKey,
          });

          console.log('Push subscription created:', subscription);

          // Send subscription to server
          const response = await fetch('/api/notifications/subscribe', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              subscription: subscription.toJSON(),
              userId: user.id,
            }),
          });

          if (response.ok) {
            console.log('‚úÖ Push subscription sent to server successfully');
          } else {
            console.error('‚ùå Failed to send subscription to server:', await response.text());
          }
        }

      } catch (error) {
        console.error('Error setting up push notifications:', error);
      }
    };

    // Setup with a small delay to ensure everything is loaded
    const timer = setTimeout(setupPushNotifications, 2000);
    return () => clearTimeout(timer);
  }, [isLoaded, user]);

  return null; // This component doesn't render anything
}

// Helper function to convert VAPID key
function urlBase64ToUint8Array(base64String: string): Uint8Array {
  const padding = '='.repeat((4 - base64String.length % 4) % 4);
  const base64 = (base64String + padding)
    .replace(/\-/g, '+')
    .replace(/_/g, '/');

  const rawData = window.atob(base64);
  const outputArray = new Uint8Array(rawData.length);

  for (let i = 0; i < rawData.length; ++i) {
    outputArray[i] = rawData.charCodeAt(i);
  }
  return outputArray;
}

