    ingredients_needed: Array<{
      name: string;
      quantity: string;
      unit: string;
      is_available: boolean;
    }>;
  }>;
  shopping: {
    urgent_items: Array<{
      id: string;
      name: string;
      quantity?: string;
      notes?: string;
      is_complete: boolean;
      list_name: string;
    }>;
    missing_ingredients: Array<{
      ingredient_name: string;
      meal_name: string;
      meal_type: string;
      quantity_needed: string;
    }>;
  };
  digest: {
    total_chores: number;
    completed_chores: number;
    upcoming_events: number;
    meals_planned: number;
    shopping_items_needed: number;
    xp_earned_today: number;
    xp_available_today: number;
  };
}

export default function TodayView() {
  const { user } = useUser();
  const [data, setData] = useState<TodayViewData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    fetchTodayData();
  }, [currentDate]);

  const fetchTodayData = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/api/today-view?date=${currentDate.toISOString()}`);
      if (response.ok) {
        const todayData = await response.json();
        setData(todayData);
      } else {
        setError('Failed to fetch today\'s data');
      }
    } catch (error) {
      console.error('Error fetching today data:', error);
      setError('Failed to fetch today\'s data');
    } finally {
      setLoading(false);
    }
  };

  const handlePreviousDay = () => {
    setCurrentDate(subDays(currentDate, 1));
  };

  const handleNextDay = () => {
    setCurrentDate(addDays(currentDate, 1));
  };

  const handleGoToToday = () => {
    setCurrentDate(new Date());
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return <CheckCircle2 className="h-4 w-4 text-green-500" />;
      case 'in_progress': return <Clock className="h-4 w-4 text-blue-500" />;
      default: return <AlertCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getMealTypeIcon = (mealType: string) => {
    switch (mealType) {
      case 'breakfast': return 'üåÖ';
      case 'lunch': return 'üåû';
      case 'dinner': return 'üåô';
      case 'snack': return 'üçé';
      default: return 'üçΩÔ∏è';
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <Calendar className="h-12 w-12 mx-auto mb-4 text-muted-foreground animate-spin" />
            <p className="text-muted-foreground">Loading today's overview...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !data) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="p-6">
            <div className="text-center">
              <AlertCircle className="h-12 w-12 mx-auto mb-4 text-destructive" />
              <h3 className="text-lg font-semibold mb-2">Error Loading Today View</h3>
              <p className="text-muted-foreground mb-4">{error}</p>
              <Button onClick={fetchTodayData}>
                Try Again
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const completionPercentage = data.digest.total_chores > 0 
    ? (data.digest.completed_chores / data.digest.total_chores) * 100 
    : 100;

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Today's Overview</h1>
          <p className="text-muted-foreground">
            {isToday(currentDate) ? 'Here\'s what\'s happening today' : `Overview for ${format(currentDate, 'MMMM d, yyyy')}`}
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Button variant="outline" size="sm" onClick={handlePreviousDay}>
            <ChevronLeft className="h-4 w-4" />
          </Button>
          <Button variant="outline" size="sm" onClick={handleGoToToday}>
            Today
          </Button>
          <Button variant="outline" size="sm" onClick={handleNextDay}>
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Digest Stats */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Daily Progress
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{data.digest.completed_chores}</div>
              <div className="text-sm text-muted-foreground">Chores Done</div>
              <div className="text-xs text-muted-foreground">of {data.digest.total_chores}</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">{data.digest.upcoming_events}</div>
              <div className="text-sm text-muted-foreground">Events Today</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">{data.digest.meals_planned}</div>
              <div className="text-sm text-muted-foreground">Meals Planned</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">{data.digest.xp_earned_today}</div>
              <div className="text-sm text-muted-foreground">XP Earned</div>
              <div className="text-xs text-muted-foreground">of {data.digest.xp_available_today}</div>
            </div>
          </div>
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Task Completion</span>
              <span>{Math.round(completionPercentage)}%</span>
            </div>
            <Progress value={completionPercentage} className="h-2" />
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Chores */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <CheckCircle2 className="h-5 w-5" />
              Today's Chores
            </CardTitle>
            <Link href="/chores">
              <Button variant="outline" size="sm">
                <Plus className="h-4 w-4 mr-2" />
                Add
              </Button>
            </Link>
          </CardHeader>
          <CardContent>
            {data.chores.length === 0 ? (
              <div className="text-center py-8">
                <CheckCircle2 className="h-12 w-12 mx-auto mb-4 text-green-500" />
                <p className="text-muted-foreground">No chores scheduled for today!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {data.chores.slice(0, 5).map((chore) => (
                  <div
                    key={chore.id}
                    className="flex items-center gap-3 p-3 border rounded-lg hover:bg-muted/50 transition-colors"
                  >
                    {getStatusIcon(chore.status)}
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <h4 className="font-medium text-sm">{chore.title}</h4>
                        <Badge className={`text-xs ${getPriorityColor(chore.priority)}`}>
                          {chore.priority}
                        </Badge>
                      </div>
                      {chore.estimated_duration && (
                        <p className="text-xs text-muted-foreground">
                          <Clock className="h-3 w-3 inline mr-1" />
                          {chore.estimated_duration} min
                        </p>
                      )}
                    </div>
                    <div className="flex items-center gap-1">
                      <Star className="h-4 w-4 text-yellow-500" />
                      <span className="text-sm font-medium">{chore.xp_reward}</span>
                    </div>
                  </div>
                ))}
                {data.chores.length > 5 && (
                  <div className="text-center pt-2">
                    <Link href="/chores">
                      <Button variant="ghost" size="sm">
                        View all {data.chores.length} chores
                      </Button>
                    </Link>
                  </div>
                )}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Events */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Calendar className="h-5 w-5" />
              Today's Events
            </CardTitle>
            <Link href="/calendar">
              <Button variant="outline" size="sm">
                <Plus className="h-4 w-4 mr-2" />
                Add
              </Button>
            </Link>
          </CardHeader>
          <CardContent>
            {data.events.length === 0 ? (
              <div className="text-center py-8">
                <Calendar className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <p className="text-muted-foreground">No events scheduled for today!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {data.events.slice(0, 5).map((event) => (
                  <div
                    key={event.id}
                    className="p-3 border rounded-lg hover:bg-muted/50 transition-colors"
                  >
                    <div className="flex items-start justify-between mb-2">
                      <h4 className="font-medium text-sm">{event.title}</h4>
                      {event.isAllDay && (
                        <Badge variant="secondary" className="text-xs">All day</Badge>
                      )}
                    </div>
                    <div className="space-y-1">
                      <p className="text-xs text-muted-foreground">
                        <Clock className="h-3 w-3 inline mr-1" />
                        {format(new Date(event.startAt), 'h:mm a')}
                        {!event.isAllDay && event.endAt && (
                          <> - {format(new Date(event.endAt), 'h:mm a')}</>
                        )}
                      </p>
                      {event.location && (
                        <p className="text-xs text-muted-foreground">
                          <MapPin className="h-3 w-3 inline mr-1" />
                          {event.location}
                        </p>
                      )}
                      {event.attendees && event.attendees.length > 0 && (
                        <p className="text-xs text-muted-foreground">
                          <Users className="h-3 w-3 inline mr-1" />
                          {event.attendees.length} attendee{event.attendees.length !== 1 ? 's' : ''}
                        </p>
                      )}
                    </div>
                  </div>
                ))}
                {data.events.length > 5 && (
                  <div className="text-center pt-2">
                    <Link href="/calendar">
                      <Button variant="ghost" size="sm">
                        View all {data.events.length} events
                      </Button>
                    </Link>
                  </div>
                )}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Meals */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <ChefHat className="h-5 w-5" />
              Today's Meals
            </CardTitle>
            <Link href="/meal-planner">
              <Button variant="outline" size="sm">
                <Plus className="h-4 w-4 mr-2" />
                Plan
              </Button>
            </Link>
          </CardHeader>
          <CardContent>
            {data.meals.length === 0 ? (
              <div className="text-center py-8">
                <ChefHat className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <p className="text-muted-foreground">No meals planned for today!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {data.meals.map((meal) => (
                  <div
                    key={meal.id}
                    className="flex items-center gap-3 p-3 border rounded-lg hover:bg-muted/50 transition-colors"
                  >
                    <span className="text-2xl">{getMealTypeIcon(meal.meal_type)}</span>
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <h4 className="font-medium text-sm capitalize">{meal.meal_type}</h4>
                        {meal.recipe_name && (
                          <Badge variant="secondary" className="text-xs">
                            {meal.recipe_name}
                          </Badge>
                        )}
                      </div>
                      {meal.ingredients_needed && meal.ingredients_needed.length > 0 && (
                        <p className="text-xs text-muted-foreground">
                          {meal.ingredients_needed.filter(ing => !ing.is_available).length} missing ingredients
                        </p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Shopping */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <ShoppingCart className="h-5 w-5" />
              Shopping Needs
            </CardTitle>
            <Link href="/shopping-lists">
              <Button variant="outline" size="sm">
                <Plus className="h-4 w-4 mr-2" />
                Add
              </Button>
            </Link>
          </CardHeader>
          <CardContent>
            {data.shopping.urgent_items.length === 0 && data.shopping.missing_ingredients.length === 0 ? (
              <div className="text-center py-8">
                <ShoppingCart className="h-12 w-12 mx-auto mb-4 text-green-500" />
                <p className="text-muted-foreground">All shopping needs covered!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {data.shopping.urgent_items.slice(0, 3).map((item) => (
                  <div
                    key={item.id}
                    className="flex items-center gap-3 p-3 border rounded-lg hover:bg-muted/50 transition-colors"
                  >
                    <div className="flex-1">
                      <h4 className="font-medium text-sm">{item.name}</h4>
                      <p className="text-xs text-muted-foreground">{item.list_name}</p>
                    </div>
                    {item.quantity && (
                      <Badge variant="outline" className="text-xs">
                        {item.quantity}
                      </Badge>
                    )}
                  </div>
                ))}
                {data.shopping.missing_ingredients.slice(0, 3).map((ingredient, index) => (
                  <div
                    key={`missing-${index}`}
                    className="flex items-center gap-3 p-3 border rounded-lg bg-orange-50 hover:bg-orange-100 transition-colors"
                  >
                    <AlertCircle className="h-4 w-4 text-orange-500" />
                    <div className="flex-1">
                      <h4 className="font-medium text-sm">{ingredient.ingredient_name}</h4>
                      <p className="text-xs text-muted-foreground">
                        For {ingredient.meal_name} ({ingredient.meal_type})
                      </p>
                    </div>
                    <Badge variant="outline" className="text-xs text-orange-600">
                      {ingredient.quantity_needed}
                    </Badge>
                  </div>
                ))}
                {(data.shopping.urgent_items.length > 3 || data.shopping.missing_ingredients.length > 3) && (
                  <div className="text-center pt-2">
                    <Link href="/shopping-lists">
                      <Button variant="ghost" size="sm">
                        View all shopping needs
                      </Button>
                    </Link>
                  </div>
                )}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter, DialogDescription } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { CheckCircle, XCircle, Edit3, MessageSquare } from 'lucide-react';

export interface AISuggestion {
  id: string;
  suggestion_type: string;
  suggestion_data: any;
  ai_reasoning: string;
  user_feedback: string;
  created_at: string;
  parsed_item?: {
    item_type: string;
    confidence_score: number;
    review_status: string;
    review_reason: string;
  };
}

export interface CorrectionModalProps {
  isOpen: boolean;
  onClose: () => void;
  suggestion: AISuggestion | null;
  onSaveCorrection: (correction: CorrectionData) => Promise<void>;
}

export interface CorrectionData {
  suggestionId: string;
  correctionType: 'correct' | 'mark_done' | 'ignore' | 'custom';
  correctionData?: any;
  userNotes: string;
}

export function CorrectionModal({ isOpen, onClose, suggestion, onSaveCorrection }: CorrectionModalProps) {
  const [correctionType, setCorrectionType] = useState<'correct' | 'mark_done' | 'ignore' | 'custom'>('correct');
  const [userNotes, setUserNotes] = useState('');
  const [customData, setCustomData] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  console.log('üîç CorrectionModal render:', { isOpen, suggestion: suggestion?.id });

  if (!suggestion) return null;

  const handleSave = async () => {
    if (!userNotes.trim()) {
      alert('Please provide notes about the correction');
      return;
    }

    setIsLoading(true);
    try {
      const correctionData: CorrectionData = {
        suggestionId: suggestion.id,
        correctionType,
        userNotes: userNotes.trim(),
        ...(correctionType === 'custom' && customData && { correctionData: JSON.parse(customData) })
      };

      await onSaveCorrection(correctionData);
      onClose();
      // Reset form
      setCorrectionType('correct');
      setUserNotes('');
      setCustomData('');
    } catch (error) {
      console.error('Failed to save correction:', error);
      alert('Failed to save correction. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const getCorrectionTypeDescription = (type: string) => {
    switch (type) {
      case 'correct': return 'Fix incorrect information';
      case 'mark_done': return 'Mark as completed';
      case 'ignore': return 'Ignore this suggestion';
      case 'custom': return 'Provide custom correction';
      default: return '';
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent 
        className="max-w-2xl max-h-[90vh] overflow-y-auto z-50 relative bg-white"
        style={{ 
          position: 'fixed', 
          top: '50%', 
          left: '50%', 
          transform: 'translate(-50%, -50%)',
          backgroundColor: 'white',
          boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)'
        }}
      >
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <MessageSquare className="w-5 h-5" />
            AI Suggestion Correction
          </DialogTitle>
          <DialogDescription className="text-sm text-gray-800 font-medium">
            Modal for correcting AI suggestions with options to fix information, mark as done, ignore, or provide custom corrections.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Original Suggestion Display */}
          <div className="bg-gray-50 p-4 rounded-lg">
            <h3 className="font-semibold text-sm text-gray-700 mb-2">Original Suggestion</h3>
            <div className="space-y-2">
              <div className="flex items-center gap-2">
                <Badge variant="outline">{suggestion.suggestion_type}</Badge>
                {suggestion.parsed_item && (
                  <>
                    <Badge variant={suggestion.parsed_item.review_status === 'auto_approved' ? 'default' : 'destructive'}>
                      {suggestion.parsed_item.review_status}
                    </Badge>
                    <Badge variant="secondary">
                      {(suggestion.parsed_item.confidence_score * 100).toFixed(0)}% confidence
                    </Badge>
                  </>
                )}
              </div>
              
              <div className="text-sm">
                <strong>AI Reasoning:</strong>
                <p className="text-gray-600 mt-1">{suggestion.ai_reasoning}</p>
              </div>

              {suggestion.suggestion_data && (
                <div className="text-sm">
                  <strong>Extracted Data:</strong>
                  <pre className="text-xs bg-white p-2 rounded mt-1 overflow-x-auto">
                    {JSON.stringify(suggestion.suggestion_data, null, 2)}
                  </pre>
                </div>
              )}
            </div>
          </div>

          {/* Correction Type Selection */}
          <div className="space-y-3">
            <Label htmlFor="correction-type">Correction Type</Label>
            <Select value={correctionType} onValueChange={(value: any) => setCorrectionType(value)}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="correct">
                  <div className="flex items-center gap-2">
                    <Edit3 className="w-4 h-4" />
                    <span>Correct Information</span>
                  </div>
                </SelectItem>
                <SelectItem value="mark_done">
                  <div className="flex items-center gap-2">
                    <CheckCircle className="w-4 h-4" />
                    <span>Mark as Done</span>
                  </div>
                </SelectItem>
                <SelectItem value="ignore">
                  <div className="flex items-center gap-2">
                    <XCircle className="w-4 h-4" />
                    <span>Ignore Suggestion</span>
                  </div>
                </SelectItem>
                <SelectItem value="custom">
                  <div className="flex items-center gap-2">
                    <MessageSquare className="w-4 h-4" />
                    <span>Custom Correction</span>
                  </div>
                </SelectItem>
              </SelectContent>
            </Select>
            
            <p className="text-sm text-gray-800 font-medium">
              {getCorrectionTypeDescription(correctionType)}
            </p>
          </div>

          {/* Custom Data Input (for custom corrections) */}
          {correctionType === 'custom' && (
            <div className="space-y-2">
              <Label htmlFor="custom-data">Custom Correction Data (JSON)</Label>
                             <Textarea
                 id="custom-data"
                 placeholder='{"corrected_field": "new_value"}'
                 value={customData}
                 onChange={(e) => setCustomData(e.target.value)}
                 className="font-mono text-sm text-gray-900 border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                 rows={3}
               />
                             <p className="text-xs text-gray-700 font-medium">
                 Enter valid JSON with the corrected information
               </p>
            </div>
          )}

          {/* User Notes */}
          <div className="space-y-2">
            <Label htmlFor="user-notes">
              Notes about the correction *
            </Label>
                         <Textarea
               id="user-notes"
               placeholder="Explain what was wrong and how it should be corrected..."
               value={userNotes}
               onChange={(e) => setUserNotes(e.target.value)}
               className="text-gray-900 border-gray-300 focus:border-blue-500 focus:ring-blue-500"
               rows={4}
               required
             />
                         <p className="text-xs text-gray-700 font-medium">
               Your feedback helps improve the AI system for future suggestions
             </p>
          </div>
        </div>

        <DialogFooter className="gap-2">
          <Button variant="outline" onClick={onClose} disabled={isLoading}>
            Cancel
          </Button>
          <Button 
            onClick={handleSave} 
            disabled={isLoading || !userNotes.trim()}
            className="min-w-[100px]"
          >
            {isLoading ? 'Saving...' : 'Save Correction'}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

// Real-time AI Test Component
// This can be easily removed if the real-time processing doesn't work

'use client';

import React, { useState, useEffect } from 'react';
import { useWebSocket } from '@/hooks/useWebSocket';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Loader2, CheckCircle, XCircle, Clock, Zap, Brain, ShoppingCart, Utensils } from 'lucide-react';

export function RealTimeAITest() {
  const {
    isConnected,
    isConnecting,
    error,
    processingRequests,
    completedRequests,
    requestAIProcessing,
    clearCompletedRequest,
    clearAllCompletedRequests
  } = useWebSocket();

  const [testResults, setTestResults] = useState<any[]>([]);

  const handleTestShoppingSuggestions = () => {
    try {
      const requestId = requestAIProcessing('shopping_suggestions', {
        dietaryRestrictions: ['vegetarian'],
        budget: 100,
        specialOccasions: ['weeknight']
      });
      console.log('Started shopping suggestions test:', requestId);
    } catch (error: any) {
      console.error('Failed to start shopping suggestions test:', error);
    }
  };

  const handleTestMealPlanning = () => {
    try {
      const requestId = requestAIProcessing('meal_planning', {
        mealType: 'dinner',
        dietaryRestrictions: ['vegetarian'],
        maxPrepTime: 30,
        servings: 4,
        cuisine: 'Italian'
      });
      console.log('Started meal planning test:', requestId);
    } catch (error: any) {
      console.error('Failed to start meal planning test:', error);
    }
  };

  const handleTestChoreAssignment = () => {
    try {
      const requestId = requestAIProcessing('chore_assignment', {
        householdId: 'test-household',
        availableUsers: ['user1', 'user2'],
        choreTypes: ['cleaning', 'cooking']
      });
      console.log('Started chore assignment test:', requestId);
    } catch (error: any) {
      console.error('Failed to start chore assignment test:', error);
    }
  };

  const handleTestEmailProcessing = () => {
    try {
      const requestId = requestAIProcessing('email_processing', {
        emailCount: 5,
        processingType: 'bills_and_receipts'
      });
      console.log('Started email processing test:', requestId);
    } catch (error: any) {
      console.error('Failed to start email processing test:', error);
    }
  };

  // Update test results when completed requests change
  useEffect(() => {
    const newResults = Array.from(completedRequests.entries()).map(([requestId, result]) => ({
      requestId,
      ...result,
      timestamp: new Date().toISOString()
    }));
    setTestResults(newResults);
  }, [completedRequests]);

  const getRequestTypeIcon = (requestId: string) => {
    if (requestId.includes('shopping')) return <ShoppingCart className="h-4 w-4" />;
    if (requestId.includes('meal')) return <Utensils className="h-4 w-4" />;
    if (requestId.includes('chore')) return <Clock className="h-4 w-4" />;
    if (requestId.includes('email')) return <Brain className="h-4 w-4" />;
    return <Zap className="h-4 w-4" />;
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Zap className="h-5 w-5" />
            Real-time AI Processing Test
          </CardTitle>
          <CardDescription>
            Test real-time AI processing with WebSocket connections
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Connection Status */}
          <div className="flex items-center gap-2">
            <div className={`h-2 w-2 rounded-full ${isConnected ? 'bg-green-500' : isConnecting ? 'bg-yellow-500' : 'bg-red-500'}`} />
            <span className="text-sm font-medium">
              {isConnecting ? 'Connecting...' : isConnected ? 'Connected' : 'Disconnected'}
            </span>
            {error && (
              <Badge variant="destructive" className="ml-2">
                Error: {error}
              </Badge>
            )}
          </div>

          {/* Test Buttons */}
          <div className="grid grid-cols-2 gap-4">
            <Button
              onClick={handleTestShoppingSuggestions}
              disabled={!isConnected || processingRequests.size > 0}
              className="flex items-center gap-2"
            >
              <ShoppingCart className="h-4 w-4" />
              Test Shopping AI
            </Button>
            <Button
              onClick={handleTestMealPlanning}
              disabled={!isConnected || processingRequests.size > 0}
              className="flex items-center gap-2"
            >
              <Utensils className="h-4 w-4" />
              Test Meal Planning AI
            </Button>
            <Button
              onClick={handleTestChoreAssignment}
              disabled={!isConnected || processingRequests.size > 0}
              className="flex items-center gap-2"
            >
              <Clock className="h-4 w-4" />
              Test Chore Assignment
            </Button>
            <Button
              onClick={handleTestEmailProcessing}
              disabled={!isConnected || processingRequests.size > 0}
              className="flex items-center gap-2"
            >
              <Brain className="h-4 w-4" />
              Test Email Processing
            </Button>
          </div>

          {/* Clear Results Button */}
          {completedRequests.size > 0 && (
            <Button
              onClick={clearAllCompletedRequests}
              variant="outline"
              size="sm"
              className="w-full"
            >
              Clear All Results
            </Button>
          )}
        </CardContent>
      </Card>

      {/* Processing Requests */}
      {processingRequests.size > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Loader2 className="h-5 w-5 animate-spin" />
              Processing Requests ({processingRequests.size})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Array.from(processingRequests.entries()).map(([requestId, progress]) => (
                <div key={requestId} className="space-y-2">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      {getRequestTypeIcon(requestId)}
                      <span className="text-sm font-medium">{progress.step}</span>
                    </div>
                    <span className="text-sm text-muted-foreground">{progress.progress}%</span>
                  </div>
                  <Progress value={progress.progress} className="h-2" />
                  <p className="text-sm text-muted-foreground">{progress.message}</p>
                  {progress.estimatedTimeRemaining && (
                    <p className="text-xs text-muted-foreground">
                      Est. time remaining: {progress.estimatedTimeRemaining}s
                    </p>
                  )}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Completed Requests */}
      {testResults.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CheckCircle className="h-5 w-5" />
              Test Results ({testResults.length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {testResults.map((result, index) => (
                <div key={result.requestId} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-2">
                      {result.success ? (
                        <CheckCircle className="h-4 w-4 text-green-500" />
                      ) : (
                        <XCircle className="h-4 w-4 text-red-500" />
                      )}
                      <span className="font-medium">
                        {result.success ? 'Success' : 'Failed'}
                      </span>
                      <Badge variant="outline">{result.provider}</Badge>
                      {result.fallbackUsed && (
                        <Badge variant="secondary">Fallback</Badge>
                      )}
                    </div>
                    <Button
                      onClick={() => clearCompletedRequest(result.requestId)}
                      variant="ghost"
                      size="sm"
                    >
                      <XCircle className="h-4 w-4" />
                    </Button>
                  </div>
                  
                  <div className="text-sm text-muted-foreground mb-2">
                    Request ID: {result.requestId}
                  </div>
                  
                  <div className="text-sm text-muted-foreground mb-2">
                    Processing Time: {result.processingTime}ms
                  </div>
                  
                  {result.error && (
                    <Alert className="mt-2">
                      <AlertDescription>{result.error}</AlertDescription>
                    </Alert>
                  )}
                  
                  {result.data && (
                    <div className="mt-2">
                      <details className="text-sm">
                        <summary className="cursor-pointer font-medium">View Results</summary>
                        <pre className="mt-2 p-2 bg-muted rounded text-xs overflow-auto">
                          {JSON.stringify(result.data, null, 2)}
                        </pre>
                      </details>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { 
  CheckCircle, 
  XCircle, 
  Edit3, 
  MessageSquare, 
  Clock,
  AlertTriangle
} from 'lucide-react';
import { CorrectionModal, CorrectionData, AISuggestion } from './CorrectionModal';

interface SuggestionCardProps {
  suggestion: AISuggestion;
  onCorrectionSaved?: () => void;
}

export function SuggestionCard({ suggestion, onCorrectionSaved }: SuggestionCardProps) {
  const [isCorrectionModalOpen, setIsCorrectionModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleSaveCorrection = async (correction: CorrectionData): Promise<void> => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/ai/corrections', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(correction),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to save correction');
      }

      // Call the callback to refresh data
      if (onCorrectionSaved) {
        onCorrectionSaved();
      }
    } catch (error) {
      console.error('Error saving correction:', error);
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const getSuggestionIcon = (type: string) => {
    switch (type) {
      case 'bill_action': return <AlertTriangle className="w-4 h-4" />;
      case 'shopping_list_update': return <CheckCircle className="w-4 h-4" />;
      case 'calendar_event': return <Clock className="w-4 h-4" />;
      case 'chore_creation': return <Edit3 className="w-4 h-4" />;
      default: return <MessageSquare className="w-4 h-4" />;
    }
  };



  const getStatusBadge = (feedback: string) => {
    switch (feedback) {
      case 'completed':
        return <Badge variant="default" className="bg-green-600">Completed</Badge>;
      case 'corrected':
        return <Badge variant="secondary">Corrected</Badge>;
      case 'ignored':
        return <Badge variant="outline">Ignored</Badge>;
      default:
        return <Badge variant="outline">Pending</Badge>;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <>
      <Card className="hover:shadow-md transition-shadow">
        <CardHeader className="pb-3">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-2">
              {getSuggestionIcon(suggestion.suggestion_type)}
              <CardTitle className="text-lg">
                {suggestion.suggestion_type.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
              </CardTitle>
            </div>
            <div className="flex items-center gap-2">
              {suggestion.parsed_item && (
                <Badge 
                  variant={suggestion.parsed_item.review_status === 'auto_approved' ? 'default' : 'destructive'}
                  className="text-xs"
                >
                  {suggestion.parsed_item.review_status === 'auto_approved' ? 'Auto-approved' : 'Needs Review'}
                </Badge>
              )}
              {suggestion.parsed_item && (
                <Badge variant="secondary" className="text-xs">
                  {(suggestion.parsed_item.confidence_score * 100).toFixed(0)}% confidence
                </Badge>
              )}
              {getStatusBadge(suggestion.user_feedback)}
            </div>
          </div>
          <div className="text-sm text-gray-500">
            {formatDate(suggestion.created_at)}
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* AI Reasoning */}
          <div>
            <h4 className="font-medium text-sm text-gray-700 mb-1">AI Reasoning:</h4>
            <p className="text-sm text-gray-600">{suggestion.ai_reasoning}</p>
          </div>

          {/* Suggestion Data */}
          {suggestion.suggestion_data && (
            <div>
              <h4 className="font-medium text-sm text-gray-700 mb-1">Extracted Data:</h4>
              <div className="bg-gray-50 p-3 rounded-md">
                <pre className="text-xs text-gray-700 whitespace-pre-wrap">
                  {JSON.stringify(suggestion.suggestion_data, null, 2)}
                </pre>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          {suggestion.user_feedback === 'pending' && (
            <div className="flex flex-wrap gap-2 pt-2 border-t">
              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  console.log('üîç Correct button clicked for suggestion:', suggestion.id);
                  setIsCorrectionModalOpen(true);
                  console.log('üîç Modal state set to true');
                }}
                className="flex items-center gap-2"
              >
                <Edit3 className="w-4 h-4" />
                Correct
              </Button>
              
              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  const correction: CorrectionData = {
                    suggestionId: suggestion.id,
                    correctionType: 'mark_done',
                    userNotes: 'Marked as completed by user'
                  };
                  handleSaveCorrection(correction);
                }}
                className="flex items-center gap-2"
                disabled={isLoading}
              >
                <CheckCircle className="w-4 h-4" />
                Mark as Done
              </Button>

              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  const correction: CorrectionData = {
                    suggestionId: suggestion.id,
                    correctionType: 'ignore',
                    userNotes: 'Ignored by user'
                  };
                  handleSaveCorrection(correction);
                }}
                className="flex items-center gap-2"
                disabled={isLoading}
              >
                <XCircle className="w-4 h-4" />
                Ignore
              </Button>
            </div>
          )}

          {/* Feedback Status */}
          {suggestion.user_feedback !== 'pending' && (
            <div className="pt-2 border-t">
              <p className="text-sm text-gray-600">
                <strong>Status:</strong> {suggestion.user_feedback}
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Correction Modal */}
      <CorrectionModal
        isOpen={isCorrectionModalOpen}
        onClose={() => setIsCorrectionModalOpen(false)}
        suggestion={suggestion}
        onSaveCorrection={handleSaveCorrection}
      />
    </>
  );
}

'use client';

import React, { useState, useRef } from 'react';
import { useAuth } from '@clerk/nextjs';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Upload, X, FileText, Image, CheckCircle, AlertCircle } from 'lucide-react';
import { toast } from 'sonner';

interface FileUploadProps {
  onUploadComplete?: (attachment: any) => void;
  onUploadError?: (error: string) => void;
  className?: string;
  maxFileSize?: number; // in bytes
  acceptedTypes?: string[];
}

interface UploadProgress {
  file: File;
  progress: number;
  status: 'uploading' | 'processing' | 'completed' | 'error';
  error?: string;
  attachment?: any;
}

export function FileUpload({ 
  onUploadComplete, 
  onUploadError,
  className = '',
  maxFileSize = 10 * 1024 * 1024, // 10MB default
  acceptedTypes = ['image/jpeg', 'image/png', 'image/gif', 'application/pdf']
}: FileUploadProps) {
  const { user, isLoaded, isSignedIn, getToken } = useAuth();
  const [uploads, setUploads] = useState<UploadProgress[]>([]);
  const [isDragOver, setIsDragOver] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Debug logging
  console.log('FileUpload auth state:', { user: !!user, isLoaded, isSignedIn });

  // Monitor authentication state changes
  React.useEffect(() => {
    console.log('FileUpload auth state changed:', { user: !!user, isLoaded, isSignedIn });
  }, [user, isLoaded, isSignedIn]);

  // Test token availability
  React.useEffect(() => {
    if (isLoaded && isSignedIn) {
      getToken().then(token => {
        console.log('üîë Token test:', token ? 'Available' : 'Not available');
      });
    }
  }, [isLoaded, isSignedIn, getToken]);

  const handleFileSelect = (files: FileList | null) => {
    if (!files) return;

    // Check if authentication is loaded and user is authenticated
    if (!isLoaded) {
      toast.error('Please wait for authentication to load');
      return;
    }

    if (!isSignedIn) {
      toast.error('Please sign in to upload files');
      return;
    }

    Array.from(files).forEach(file => {
      // Validate file
      if (!acceptedTypes.includes(file.type)) {
        toast.error(`File type ${file.type} is not supported`);
        return;
      }

      if (file.size > maxFileSize) {
        toast.error(`File size ${(file.size / 1024 / 1024).toFixed(1)}MB exceeds maximum ${(maxFileSize / 1024 / 1024).toFixed(1)}MB`);
        return;
      }

      // Add to uploads
      const upload: UploadProgress = {
        file,
        progress: 0,
        status: 'uploading'
      };

      setUploads(prev => [...prev, upload]);

      // Start upload
      uploadFile(upload);
    });
  };

  const uploadFile = async (upload: UploadProgress) => {
    try {
      // Check if authentication is loaded and user is still authenticated
      if (!isLoaded) {
        console.log('‚ùå Upload failed - Authentication not loaded');
        throw new Error('Authentication not loaded');
      }

      if (!isSignedIn) {
        console.log('‚ùå Upload failed - User not authenticated');
        throw new Error('User not authenticated');
      }

      console.log('‚úÖ Upload starting - Auth state:', { isLoaded, isSignedIn, user: !!user });

      // Upload file using FormData to the API route
      const formData = new FormData();
      formData.append('file', upload.file);

      // Update progress
      setUploads(prev => prev.map(u => 
        u.file === upload.file 
          ? { ...u, progress: 25, status: 'uploading' }
          : u
      ));

      // Upload file to API route (which handles Supabase storage with proper auth)
      const token = await getToken();
      console.log('üîë Auth token:', token ? 'Present' : 'Missing');
      
      const response = await fetch('/api/attachments/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Upload failed');
      }

      const { attachment } = await response.json();

      // Update progress
      setUploads(prev => prev.map(u => 
        u.file === upload.file 
          ? { ...u, progress: 75, status: 'processing', attachment }
          : u
      ));

      // Trigger OCR processing if it's an image
      if (upload.file.type.startsWith('image/')) {
        const processToken = await getToken();
        const processResponse = await fetch('/api/attachments/upload', {
          method: 'PUT',
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${processToken}`
          },
          body: JSON.stringify({ attachment_id: attachment.id })
        });

        if (!processResponse.ok) {
          throw new Error('Failed to start OCR processing');
        }

        const { attachment: processedAttachment } = await processResponse.json();
        
        // Update to completed
        setUploads(prev => prev.map(u => 
          u.file === upload.file 
            ? { 
                ...u, 
                progress: 100, 
                status: 'completed', 
                attachment: processedAttachment 
              }
            : u
        ));

        toast.success(`Receipt processed successfully! ${processedAttachment.receipt_items?.length || 0} items extracted.`);
        onUploadComplete?.(processedAttachment);
      } else {
        // Non-image file, mark as completed
        setUploads(prev => prev.map(u => 
          u.file === upload.file 
            ? { ...u, progress: 100, status: 'completed' }
            : u
        ));

        toast.success('File uploaded successfully!');
        onUploadComplete?.(attachment);
      }

    } catch (error) {
      console.error('‚ùå Upload error:', error);
      
      setUploads(prev => prev.map(u => 
        u.file === upload.file 
          ? { 
              ...u, 
              status: 'error', 
              error: error instanceof Error ? error.message : 'Upload failed'
            }
          : u
      ));

      toast.error(`Upload failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      onUploadError?.(error instanceof Error ? error.message : 'Upload failed');
    }
  };

  const removeUpload = (file: File) => {
    setUploads(prev => prev.filter(u => u.file !== file));
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    handleFileSelect(e.dataTransfer.files);
  };

  const getFileIcon = (fileType: string) => {
    if (fileType.startsWith('image/')) {
      return <Image className="h-4 w-4" />;
    }
    return <FileText className="h-4 w-4" />;
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'error':
        return <AlertCircle className="h-4 w-4 text-red-600" />;
      default:
        return null;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'error':
        return 'bg-red-100 text-red-800';
      case 'processing':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className={className}>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Upload className="h-5 w-5" />
            Upload Receipts & Documents
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Upload Area */}
          <div
            className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
              !isLoaded || !isSignedIn
                ? 'border-gray-200 bg-gray-50 cursor-not-allowed opacity-50'
                : isDragOver 
                  ? 'border-blue-400 bg-blue-50' 
                  : 'border-gray-300 hover:border-gray-400'
            }`}
            onDragOver={isLoaded && isSignedIn ? handleDragOver : undefined}
            onDragLeave={isLoaded && isSignedIn ? handleDragLeave : undefined}
            onDrop={isLoaded && isSignedIn ? handleDrop : undefined}
          >
            <Upload className="h-12 w-12 mx-auto mb-4 text-gray-400" />
            <p className="text-lg font-medium text-gray-900 mb-2">
              {!isLoaded ? 'Loading authentication...' : 'Drop files here or click to browse'}
            </p>
            <p className="text-sm text-gray-500 mb-4">
              Supported formats: JPEG, PNG, GIF, PDF (max {(maxFileSize / 1024 / 1024).toFixed(1)}MB)
            </p>
            <Button
              onClick={() => isLoaded && isSignedIn && fileInputRef.current?.click()}
              variant="outline"
              className="mb-2"
              disabled={!isLoaded || !isSignedIn}
            >
              {!isLoaded ? 'Loading...' : isSignedIn ? 'Choose Files' : 'Please Sign In'}
            </Button>
            <input
              ref={fileInputRef}
              type="file"
              multiple
              accept={acceptedTypes.join(',')}
              onChange={(e) => handleFileSelect(e.target.files)}
              className="hidden"
            />
          </div>

          {/* Upload Progress */}
          {uploads.length > 0 && (
            <div className="space-y-3">
              <h4 className="font-medium text-gray-900">Upload Progress</h4>
              {uploads.map((upload, index) => (
                <div
                  key={index}
                  className="flex items-center justify-between p-3 border rounded-lg"
                >
                  <div className="flex items-center gap-3 flex-1">
                    {getFileIcon(upload.file.type)}
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900 truncate">
                        {upload.file.name}
                      </p>
                      <div className="flex items-center gap-2 mt-1">
                        <Progress value={upload.progress} className="flex-1 h-2" />
                        <Badge className={getStatusColor(upload.status)}>
                          {upload.status}
                        </Badge>
                      </div>
                      {upload.error && (
                        <p className="text-xs text-red-600 mt-1">{upload.error}</p>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {getStatusIcon(upload.status)}
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => removeUpload(upload.file)}
                      className="h-8 w-8 p-0"
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { 
  ShoppingCart, 
  Plus, 
  Minus, 
  CheckCircle, 
  AlertCircle,
  Receipt,
  Calendar,
  MapPin,
  DollarSign,
  Wallet
} from 'lucide-react';
import { format } from 'date-fns';
import { toast } from 'sonner';
import { useAuth } from '@clerk/nextjs';

interface ReceiptItem {
  id: string;
  item_name: string;
  item_price: number;
  item_quantity: number;
  item_category?: string;
  item_brand?: string;
  item_unit?: string;
  confidence_score: number;
  added_to_shopping_list: boolean;
  added_to_spending: boolean;
  spend_entry_id?: string;
  user_confirmed: boolean;
  user_modified: boolean;
  attachment: {
    id: string;
    file_name: string;
    receipt_store?: string;
    receipt_date?: string;
  };
}

interface ReceiptItemsDisplayProps {
  attachmentId: string;
  className?: string;
}

export function ReceiptItemsDisplay({ attachmentId, className }: ReceiptItemsDisplayProps) {
  const { getToken } = useAuth();
  const [items, setItems] = useState<ReceiptItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedItems, setSelectedItems] = useState<Set<string>>(new Set());
  const [addingToShoppingList, setAddingToShoppingList] = useState(false);
  const [addingToSpending, setAddingToSpending] = useState(false);
  const [showSpendingOptions, setShowSpendingOptions] = useState(false);
  const [spendingOptions, setSpendingOptions] = useState({
    create_single_entry: false,
    payment_method: 'card' as 'cash' | 'card' | 'bank_transfer' | 'other'
  });

  // Fetch receipt items
  React.useEffect(() => {
    const fetchItems = async () => {
      try {
        const token = await getToken();
        const response = await fetch(`/api/receipt-items?attachment_id=${attachmentId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        const data = await response.json();
        
        if (data.success) {
          setItems(data.receipt_items || []);
        } else {
          toast.error('Failed to load receipt items');
        }
      } catch (error) {
        console.error('‚ùå Error fetching receipt items:', error);
        toast.error('Failed to load receipt items');
      } finally {
        setLoading(false);
      }
    };

    fetchItems();
  }, [attachmentId, getToken]);

  const handleSelectItem = (itemId: string, checked: boolean) => {
    const newSelected = new Set(selectedItems);
    if (checked) {
      newSelected.add(itemId);
    } else {
      newSelected.delete(itemId);
    }
    setSelectedItems(newSelected);
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedItems(new Set(items.map(item => item.id)));
    } else {
      setSelectedItems(new Set());
    }
  };

  const handleAddToShoppingList = async () => {
    if (selectedItems.size === 0) {
      toast.error('Please select items to add to shopping list');
      return;
    }

    setAddingToShoppingList(true);
    try {
      const token = await getToken();
      const response = await fetch('/api/receipt-items', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          item_ids: Array.from(selectedItems)
        })
      });

      const data = await response.json();
      
      if (data.success) {
        toast.success(data.message);
        
        // Update items to mark as added
        setItems(prev => prev.map(item => 
          selectedItems.has(item.id)
            ? { ...item, added_to_shopping_list: true }
            : item
        ));
        
        setSelectedItems(new Set());
      } else {
        toast.error(data.error || 'Failed to add items to shopping list');
      }
    } catch (error) {
      console.error('‚ùå Error adding to shopping list:', error);
      toast.error('Failed to add items to shopping list');
    } finally {
      setAddingToShoppingList(false);
    }
  };

  const handleAddToSpending = async () => {
    if (selectedItems.size === 0) {
      toast.error('Please select items to add to spending');
      return;
    }

    setAddingToSpending(true);
    try {
      const token = await getToken();
      const response = await fetch('/api/finance/receipt-to-spending', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          receipt_item_ids: Array.from(selectedItems),
          ...spendingOptions
        })
      });

      const data = await response.json();
      
      if (data.success) {
        toast.success(data.message);
        
        // Update items to mark as added to spending
        setItems(prev => prev.map(item => 
          selectedItems.has(item.id)
            ? { ...item, added_to_spending: true }
            : item
        ));
        
        setSelectedItems(new Set());
        setShowSpendingOptions(false);
      } else {
        toast.error(data.error || 'Failed to add items to spending');
      }
    } catch (error) {
      console.error('‚ùå Error adding to spending:', error);
      toast.error('Failed to add items to spending');
    } finally {
      setAddingToSpending(false);
    }
  };

  const handleUpdateItem = async (itemId: string, updates: Partial<ReceiptItem>) => {
    try {
      const token = await getToken();
      const response = await fetch('/api/receipt-items', {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          item_id: itemId,
          updates
        })
      });

      const data = await response.json();
      
      if (data.success) {
        setItems(prev => prev.map(item => 
          item.id === itemId ? { ...item, ...updates, user_modified: true } : item
        ));
        toast.success('Item updated successfully');
      } else {
        toast.error(data.error || 'Failed to update item');
      }
    } catch (error) {
      console.error('‚ùå Error updating item:', error);
      toast.error('Failed to update item');
    }
  };

  const getConfidenceColor = (score: number) => {
    if (score >= 0.8) return 'bg-green-100 text-green-800';
    if (score >= 0.6) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  };

  const getCategoryColor = (category?: string) => {
    const colors: Record<string, string> = {
      'dairy': 'bg-blue-100 text-blue-800',
      'produce': 'bg-green-100 text-green-800',
      'meat': 'bg-red-100 text-red-800',
      'bakery': 'bg-yellow-100 text-yellow-800',
      'pantry': 'bg-orange-100 text-orange-800',
      'beverages': 'bg-cyan-100 text-cyan-800',
      'snacks': 'bg-purple-100 text-purple-800',
      'household': 'bg-gray-100 text-gray-800',
      'health': 'bg-pink-100 text-pink-800'
    };
    
    return colors[category || 'other'] || 'bg-gray-100 text-gray-800';
  };

  if (loading) {
    return (
      <Card className={className}>
        <CardContent className="p-6">
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <span className="ml-2 text-gray-600">Loading receipt items...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (items.length === 0) {
    return (
      <Card className={className}>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Receipt className="h-5 w-5" />
            Receipt Items
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <AlertCircle className="h-12 w-12 mx-auto mb-4 text-gray-400" />
            <p className="text-gray-600">No items found in this receipt</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const allSelected = items.length > 0 && selectedItems.size === items.length;
  const someSelected = selectedItems.size > 0 && selectedItems.size < items.length;

  return (
    <Card className={className}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Receipt className="h-5 w-5" />
            Receipt Items ({items.length})
          </CardTitle>
          
          {items.length > 0 && (
            <div className="flex items-center gap-2">
              <Badge variant="outline" className="flex items-center gap-1">
                <Calendar className="h-3 w-3" />
                {items[0].attachment.receipt_date && 
                  format(new Date(items[0].attachment.receipt_date), 'MMM dd, yyyy')
                }
              </Badge>
              {items[0].attachment.receipt_store && (
                <Badge variant="outline" className="flex items-center gap-1">
                  <MapPin className="h-3 w-3" />
                  {items[0].attachment.receipt_store}
                </Badge>
              )}
            </div>
          )}
        </div>
      </CardHeader>
      
      <CardContent className="space-y-4">
        {/* Select All Controls */}
        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
          <div className="flex items-center space-x-2">
            <Checkbox
              checked={allSelected}
              onCheckedChange={handleSelectAll}
              ref={(el) => {
                if (el) el.indeterminate = someSelected;
              }}
            />
            <span className="text-sm font-medium text-gray-900">
              Select All ({selectedItems.size}/{items.length})
            </span>
          </div>
          
          {selectedItems.size > 0 && (
            <div className="flex items-center gap-2">
              <Button
                onClick={handleAddToShoppingList}
                disabled={addingToShoppingList}
                className="bg-green-600 hover:bg-green-700"
              >
                <ShoppingCart className="h-4 w-4 mr-2" />
                {addingToShoppingList ? 'Adding...' : `Add ${selectedItems.size} to Shopping List`}
              </Button>
              
              <Button
                onClick={() => setShowSpendingOptions(!showSpendingOptions)}
                disabled={addingToSpending}
                variant="outline"
                className="bg-blue-50 border-blue-200 text-blue-700 hover:bg-blue-100"
              >
                <DollarSign className="h-4 w-4 mr-2" />
                {addingToSpending ? 'Adding...' : `Add ${selectedItems.size} to Spending`}
              </Button>
            </div>
          )}
        </div>

        {/* Spending Options */}
        {showSpendingOptions && selectedItems.size > 0 && (
          <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg space-y-4">
            <div className="flex items-center gap-2">
              <Wallet className="h-5 w-5 text-blue-600" />
              <h3 className="font-medium text-blue-900">Spending Options</h3>
            </div>
            
            <div className="space-y-3">
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="single-entry"
                  checked={spendingOptions.create_single_entry}
                  onCheckedChange={(checked) => 
                    setSpendingOptions(prev => ({ ...prev, create_single_entry: checked as boolean }))
                  }
                />
                <Label htmlFor="single-entry" className="text-sm">
                  Create single spend entry for all items (recommended for receipt totals)
                </Label>
              </div>
              
              <div className="space-y-2">
                <Label htmlFor="payment-method" className="text-sm font-medium">
                  Payment Method
                </Label>
                <select
                  id="payment-method"
                  value={spendingOptions.payment_method}
                  onChange={(e) => 
                    setSpendingOptions(prev => ({ 
                      ...prev, 
                      payment_method: e.target.value as 'cash' | 'card' | 'bank_transfer' | 'other'
                    }))
                  }
                  className="w-full p-2 border border-gray-300 rounded-md text-sm"
                >
                  <option value="card">Card</option>
                  <option value="cash">Cash</option>
                  <option value="bank_transfer">Bank Transfer</option>
                  <option value="other">Other</option>
                </select>
              </div>
            </div>
            
            <div className="flex justify-end gap-2">
              <Button
                variant="outline"
                onClick={() => setShowSpendingOptions(false)}
                className="text-sm"
              >
                Cancel
              </Button>
              <Button
                onClick={handleAddToSpending}
                disabled={addingToSpending}
                className="bg-blue-600 hover:bg-blue-700 text-sm"
              >
                <DollarSign className="h-4 w-4 mr-2" />
                {addingToSpending ? 'Adding...' : 'Add to Spending'}
              </Button>
            </div>
          </div>
        )}

        {/* Items List */}
        <div className="space-y-2">
          {items.map((item) => (
            <div
              key={item.id}
              className={`flex items-center justify-between p-3 border rounded-lg ${
                item.added_to_shopping_list ? 'bg-green-50 border-green-200' : 'bg-white'
              }`}
            >
              <div className="flex items-center space-x-3 flex-1">
                <Checkbox
                  checked={selectedItems.has(item.id)}
                  onCheckedChange={(checked) => handleSelectItem(item.id, checked as boolean)}
                  disabled={item.added_to_shopping_list}
                />
                
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-medium text-gray-900 truncate">
                      {item.item_name}
                    </span>
                    {item.item_brand && (
                      <Badge variant="outline" className="text-xs">
                        {item.item_brand}
                      </Badge>
                    )}
                    {item.item_category && (
                      <Badge className={`text-xs ${getCategoryColor(item.item_category)}`}>
                        {item.item_category}
                      </Badge>
                    )}
                  </div>
                  
                  <div className="flex items-center gap-2 text-sm text-gray-600">
                    <span className="font-medium">${item.item_price.toFixed(2)}</span>
                    {item.item_quantity > 1 && (
                      <span>√ó {item.item_quantity}</span>
                    )}
                    {item.item_unit && (
                      <span>per {item.item_unit}</span>
                    )}
                    <Badge className={`text-xs ${getConfidenceColor(item.confidence_score)}`}>
                      {(item.confidence_score * 100).toFixed(0)}% confidence
                    </Badge>
                  </div>
                </div>
              </div>
              
              <div className="flex items-center gap-2">
                {item.added_to_shopping_list && (
                  <CheckCircle className="h-5 w-5 text-green-600" title="Added to shopping list" />
                )}
                {item.added_to_spending && (
                  <CheckCircle className="h-5 w-5 text-blue-600" title="Added to spending" />
                )}
                {item.user_modified && (
                  <Badge variant="outline" className="text-xs">
                    Modified
                  </Badge>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Summary */}
        <div className="flex justify-between items-center pt-4 border-t">
          <div className="text-sm text-gray-600">
            Total: ${items.reduce((sum, item) => sum + (item.item_price * item.item_quantity), 0).toFixed(2)}
          </div>
          <div className="flex items-center gap-4 text-sm text-gray-600">
            <span className="flex items-center gap-1">
              <ShoppingCart className="h-4 w-4 text-green-600" />
              {items.filter(item => item.added_to_shopping_list).length} shopping
            </span>
            <span className="flex items-center gap-1">
              <DollarSign className="h-4 w-4 text-blue-600" />
              {items.filter(item => item.added_to_spending).length} spending
            </span>
            <span>of {items.length} items</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { 
  Calendar,
  Link,
  Copy,
  RefreshCw,
  Shield,
  Globe,
  Clock,
  ExternalLink,
  CheckCircle,
  AlertCircle,
  Info
} from 'lucide-react';
import { toast } from 'sonner';
import { format } from 'date-fns';
import { useAuth } from '@clerk/nextjs';

interface SyncSettings {
  household_id: string;
  household_name: string;
  public_sync_enabled: boolean;
  ics_token: string;
  token_expires_at: string;
  sync_urls: {
    public: string;
    private: string;
  };
  instructions: {
    google_calendar: string;
    apple_calendar: string;
    outlook: string;
    general: string;
  };
}

interface CalendarSyncManagerProps {
  householdId: string;
  className?: string;
}

export function CalendarSyncManager({ householdId, className }: CalendarSyncManagerProps) {
  const { getToken } = useAuth();
  const [syncSettings, setSyncSettings] = useState<SyncSettings | null>(null);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [regenerating, setRegenerating] = useState(false);
  const [copiedUrl, setCopiedUrl] = useState<string | null>(null);

  useEffect(() => {
    fetchSyncSettings();
  }, [householdId]);

  const fetchSyncSettings = async () => {
    try {
      setLoading(true);
      const token = await getToken();
      const response = await fetch(`/api/calendars/${householdId}/sync`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      
      if (data.success) {
        setSyncSettings(data.sync_settings);
      } else {
        toast.error('Failed to load sync settings');
      }
    } catch (error) {
      console.error('‚ùå Error fetching sync settings:', error);
      toast.error('Failed to load sync settings');
    } finally {
      setLoading(false);
    }
  };

  const handleTogglePublicSync = async (enabled: boolean) => {
    try {
      setUpdating(true);
      const token = await getToken();
      const response = await fetch(`/api/calendars/${householdId}/sync`, {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ enable_public_sync: enabled })
      });

      const data = await response.json();
      
      if (data.success) {
        setSyncSettings(prev => prev ? { ...prev, public_sync_enabled: enabled } : null);
        toast.success(enabled ? 'Public sync enabled' : 'Public sync disabled');
      } else {
        toast.error(data.error || 'Failed to update sync settings');
      }
    } catch (error) {
      console.error('‚ùå Error updating sync settings:', error);
      toast.error('Failed to update sync settings');
    } finally {
      setUpdating(false);
    }
  };

  const handleRegenerateToken = async () => {
    if (!confirm('Are you sure you want to regenerate the sync token? This will invalidate existing calendar subscriptions.')) {
      return;
    }

    try {
      setRegenerating(true);
      const token = await getToken();
      const response = await fetch(`/api/calendars/${householdId}/sync`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();
      
      if (data.success) {
        toast.success('Sync token regenerated successfully');
        fetchSyncSettings(); // Refresh settings
      } else {
        toast.error(data.error || 'Failed to regenerate token');
      }
    } catch (error) {
      console.error('‚ùå Error regenerating token:', error);
      toast.error('Failed to regenerate token');
    } finally {
      setRegenerating(false);
    }
  };

  const copyToClipboard = async (text: string, label: string) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedUrl(label);
      toast.success(`${label} copied to clipboard`);
      setTimeout(() => setCopiedUrl(null), 2000);
    } catch (error) {
      toast.error('Failed to copy to clipboard');
    }
  };

  const isTokenExpired = (expiresAt: string) => {
    return new Date(expiresAt) < new Date();
  };

  if (loading) {
    return (
      <Card className={className}>
        <CardContent className="p-6">
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <span className="ml-2 text-gray-600">Loading sync settings...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!syncSettings) {
    return (
      <Card className={className}>
        <CardContent className="p-6">
          <div className="text-center">
            <AlertCircle className="h-12 w-12 mx-auto mb-4 text-red-500" />
            <p className="text-red-600">Failed to load sync settings</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className={className}>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            Calendar Sync Settings
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Public Sync Toggle */}
          <div className="flex items-center justify-between p-4 border rounded-lg">
            <div className="flex items-center gap-3">
              <Globe className="h-5 w-5 text-blue-600" />
              <div>
                <Label htmlFor="public-sync" className="text-base font-medium">
                  Enable Public Calendar Sync
                </Label>
                <p className="text-sm text-gray-600">
                  Allow external calendar apps to subscribe to your household calendar
                </p>
              </div>
            </div>
            <Switch
              id="public-sync"
              checked={syncSettings.public_sync_enabled}
              onCheckedChange={handleTogglePublicSync}
              disabled={updating}
            />
          </div>

          {syncSettings.public_sync_enabled && (
            <>
              {/* Sync URL */}
              <div className="space-y-3">
                <div className="flex items-center gap-2">
                  <Link className="h-4 w-4" />
                  <Label className="text-sm font-medium">Public Calendar URL</Label>
                  <Badge variant="secondary" className="bg-green-100 text-green-800">
                    <CheckCircle className="h-3 w-3 mr-1" />
                    Active
                  </Badge>
                </div>
                
                <div className="flex gap-2">
                  <Input
                    value={syncSettings.sync_urls.public}
                    readOnly
                    className="flex-1 font-mono text-sm"
                  />
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => copyToClipboard(syncSettings.sync_urls.public, 'Sync URL')}
                    className="shrink-0"
                  >
                    {copiedUrl === 'Sync URL' ? (
                      <CheckCircle className="h-4 w-4 text-green-600" />
                    ) : (
                      <Copy className="h-4 w-4" />
                    )}
                  </Button>
                </div>
              </div>

              {/* Token Status */}
              <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center gap-2">
                  <Shield className="h-4 w-4 text-gray-600" />
                  <span className="text-sm font-medium">Sync Token</span>
                  {isTokenExpired(syncSettings.token_expires_at) ? (
                    <Badge variant="destructive">
                      <AlertCircle className="h-3 w-3 mr-1" />
                      Expired
                    </Badge>
                  ) : (
                    <Badge variant="secondary" className="bg-green-100 text-green-800">
                      <CheckCircle className="h-3 w-3 mr-1" />
                      Valid
                    </Badge>
                  )}
                </div>
                
                <div className="flex items-center gap-2">
                  <div className="text-sm text-gray-600">
                    <Clock className="h-3 w-3 inline mr-1" />
                    Expires: {format(new Date(syncSettings.token_expires_at), 'MMM dd, yyyy')}
                  </div>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={handleRegenerateToken}
                    disabled={regenerating}
                  >
                    {regenerating ? (
                      <RefreshCw className="h-4 w-4 animate-spin" />
                    ) : (
                      <RefreshCw className="h-4 w-4" />
                    )}
                    Regenerate
                  </Button>
                </div>
              </div>

              {/* Setup Instructions */}
              <div className="space-y-4">
                <h4 className="font-medium text-gray-900">How to Subscribe</h4>
                
                <div className="grid gap-4">
                  {/* Google Calendar */}
                  <div className="p-4 border rounded-lg">
                    <div className="flex items-center gap-2 mb-2">
                      <div className="w-6 h-6 bg-blue-600 rounded flex items-center justify-center">
                        <span className="text-white text-xs font-bold">G</span>
                      </div>
                      <span className="font-medium">Google Calendar</span>
                    </div>
                    <p className="text-sm text-gray-600 mb-2">
                      1. Open Google Calendar<br/>
                      2. Click the "+" next to "Other calendars"<br/>
                      3. Choose "From URL"<br/>
                      4. Paste the sync URL above
                    </p>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => copyToClipboard(syncSettings.sync_urls.public, 'Google Calendar URL')}
                    >
                      {copiedUrl === 'Google Calendar URL' ? (
                        <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                      ) : (
                        <Copy className="h-4 w-4 mr-2" />
                      )}
                      Copy URL
                    </Button>
                  </div>

                  {/* Apple Calendar */}
                  <div className="p-4 border rounded-lg">
                    <div className="flex items-center gap-2 mb-2">
                      <div className="w-6 h-6 bg-gray-900 rounded flex items-center justify-center">
                        <span className="text-white text-xs">üçé</span>
                      </div>
                      <span className="font-medium">Apple Calendar</span>
                    </div>
                    <p className="text-sm text-gray-600 mb-2">
                      1. Open Calendar app<br/>
                      2. Go to File ‚Üí New Calendar Subscription<br/>
                      3. Paste the sync URL above<br/>
                      4. Click Subscribe
                    </p>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => copyToClipboard(syncSettings.sync_urls.public, 'Apple Calendar URL')}
                    >
                      {copiedUrl === 'Apple Calendar URL' ? (
                        <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                      ) : (
                        <Copy className="h-4 w-4 mr-2" />
                      )}
                      Copy URL
                    </Button>
                  </div>

                  {/* Outlook */}
                  <div className="p-4 border rounded-lg">
                    <div className="flex items-center gap-2 mb-2">
                      <div className="w-6 h-6 bg-blue-500 rounded flex items-center justify-center">
                        <span className="text-white text-xs font-bold">O</span>
                      </div>
                      <span className="font-medium">Microsoft Outlook</span>
                    </div>
                    <p className="text-sm text-gray-600 mb-2">
                      1. Open Outlook<br/>
                      2. Go to File ‚Üí Account Settings ‚Üí Account Settings<br/>
                      3. Click "Internet Calendars" tab<br/>
                      4. Click "New" and paste the sync URL
                    </p>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => copyToClipboard(syncSettings.sync_urls.public, 'Outlook URL')}
                    >
                      {copiedUrl === 'Outlook URL' ? (
                        <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                      ) : (
                        <Copy className="h-4 w-4 mr-2" />
                      )}
                      Copy URL
                    </Button>
                  </div>
                </div>
              </div>

              {/* Security Notice */}
              <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg">
                <div className="flex items-start gap-2">
                  <Info className="h-5 w-5 text-blue-600 mt-0.5" />
                  <div className="text-sm text-blue-800">
                    <p className="font-medium mb-1">Security Notice</p>
                    <p>
                      Only events marked as "Public" will be included in the sync. 
                      Private events remain private and are not shared through this URL.
                      Keep your sync URL secure and regenerate it if compromised.
                    </p>
                  </div>
                </div>
              </div>
            </>
          )}

          {!syncSettings.public_sync_enabled && (
            <div className="text-center py-8">
              <Calendar className="h-12 w-12 mx-auto mb-4 text-gray-400" />
              <p className="text-gray-600 mb-2">Public calendar sync is disabled</p>
              <p className="text-sm text-gray-500">
                Enable public sync to generate a URL for subscribing to your calendar in external apps.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React from 'react';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { X, Calendar, Clock, Tag, AlertCircle } from 'lucide-react';
import { useFormState } from '@/hooks/useFormValidation';
import { createCalendarEventInputSchema } from '@/lib/validation/schemas';

interface CreateEventModalProps {
  isOpen: boolean;
  onClose: () => void;
  onEventCreated: () => void;
}

function CreateEventModal({ isOpen, onClose, onEventCreated }: CreateEventModalProps) {
  const {
    values: formData,
    setValue,
    reset,
    validate,
    errors,
  } = useFormState({
    title: '',
    description: '',
    start_time: '',
    end_time: '',
    event_type: 'general',
    priority: 'medium',
  }, createCalendarEventInputSchema);

  const [isSubmitting, setIsSubmitting] = React.useState(false);

  const eventTypes = [
    { value: 'general', label: 'General', color: 'bg-gray-100 text-gray-800' },
    { value: 'work', label: 'Work', color: 'bg-blue-100 text-blue-800' },
    { value: 'personal', label: 'Personal', color: 'bg-green-100 text-green-800' },
    { value: 'family', label: 'Family', color: 'bg-purple-100 text-purple-800' },
    { value: 'health', label: 'Health', color: 'bg-red-100 text-red-800' },
    { value: 'social', label: 'Social', color: 'bg-yellow-100 text-yellow-800' }
  ];

  const priorities = [
    { value: 'low', label: 'Low', color: 'bg-green-100 text-green-800' },
    { value: 'medium', label: 'Medium', color: 'bg-yellow-100 text-yellow-800' },
    { value: 'high', label: 'High', color: 'bg-red-100 text-red-800' }
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const isValid = validate();
      if (!isValid) {
        setIsSubmitting(false);
        toast.error('Please fix the highlighted fields');
        return;
      }

      const response = await fetch('/api/calendar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          toast.success('Event created successfully!');
          onEventCreated();
          onClose();
          reset();
        } else {
          toast.error(`Failed to create event: ${result.error || 'Unknown error'}`);
        }
      } else {
        const error = await response.json();
        toast.error(`Failed to create event: ${error.error || error.message || 'Unknown error'}`);
      }
    } catch (error) {
      toast.error('Failed to create event. Please try again.');
      console.error('Error creating event:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setValue(name as keyof typeof formData, value);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5 text-blue-500" />
            Create New Event
          </CardTitle>
          <Button
            variant="ghost"
            size="sm"
            onClick={onClose}
            className="h-8 w-8 p-0"
          >
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Event Title */}
            <div>
              <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-2">
                Event Title *
              </label>
              <input
                type="text"
                id="title"
                name="title"
                required
                value={formData.title}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter event title"
              />
            </div>

            {/* Description */}
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                rows={3}
                value={formData.description}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Event description (optional)"
              />
            </div>

            {/* Date and Time */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="start_time" className="block text-sm font-medium text-gray-700 mb-2">
                  Start Time *
                </label>
                <input
                  type="datetime-local"
                  id="start_time"
                  name="start_time"
                  required
                  value={formData.start_time}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <div>
                <label htmlFor="end_time" className="block text-sm font-medium text-gray-700 mb-2">
                  End Time *
                </label>
                <input
                  type="datetime-local"
                  id="end_time"
                  name="end_time"
                  required
                  value={formData.end_time}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            {/* Event Type and Priority */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="event_type" className="block text-sm font-medium text-gray-700 mb-2">
                  Event Type
                </label>
                <select
                  id="event_type"
                  name="event_type"
                  value={formData.event_type}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  {eventTypes.map((type) => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label htmlFor="priority" className="block text-sm font-medium text-gray-700 mb-2">
                  Priority
                </label>
                <select
                  id="priority"
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  {priorities.map((priority) => (
                    <option key={priority.value} value={priority.value}>
                      {priority.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Selected Type and Priority Display */}
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <Tag className="h-4 w-4 text-gray-500" />
                <span className="text-sm text-gray-600">Type:</span>
                <Badge className={eventTypes.find(t => t.value === formData.event_type)?.color}>
                  {eventTypes.find(t => t.value === formData.event_type)?.label}
                </Badge>
              </div>
              <div className="flex items-center gap-2">
                <AlertCircle className="h-4 w-4 text-gray-500" />
                <span className="text-sm text-gray-600">Priority:</span>
                <Badge className={priorities.find(p => p.value === formData.priority)?.color}>
                  {priorities.find(p => p.value === formData.priority)?.label}
                </Badge>
              </div>
            </div>

            {/* Submit Buttons */}
            <div className="flex justify-end space-x-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={onClose}
                disabled={isSubmitting}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={isSubmitting}
                className="bg-blue-600 hover:bg-blue-700"
              >
                {isSubmitting ? (
                  <>
                    <Clock className="h-4 w-4 mr-2 animate-spin" />
                    Creating...
                  </>
                ) : (
                  <>
                    <Calendar className="h-4 w-4 mr-2" />
                    Create Event
                  </>
                )}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

export default CreateEventModal;

