'use client'

import Link from 'next/link'
import { useAuth } from '@clerk/nextjs'
import { useUserData } from '@/hooks/useUserData'

interface NavBarMobileMenuProps {
  isMobileMenuOpen: boolean;
  setIsMobileMenuOpen: (open: boolean) => void;
}

export default function NavBarMobileMenu({ isMobileMenuOpen, setIsMobileMenuOpen }: NavBarMobileMenuProps) {
  const { isLoaded, isSignedIn } = useAuth()
  const { userData } = useUserData()

  const navLinks = [
    { href: "/dashboard", label: "Dashboard" },
    { href: "/meal-planner", label: "Meal Planner" },
    { href: "/planner", label: "Planner" },
    { href: "/shopping-lists", label: "Shopping Lists" },
    { href: "/chores", label: "Chores" },
    { href: "/rewards", label: "Rewards" },
    { href: "/leaderboard", label: "Leaderboard" },
    { href: "/calendar", label: "Calendar" },
    { href: "/notifications", label: "Notifications" },
    { href: "/reminders", label: "Reminders" },
    { href: "/digest-preferences", label: "Digest Settings" },
  ];

  if (!isLoaded || !isSignedIn) {
    return null;
  }

  return (
    <>
      {/* Mobile menu button */}
      <div className="lg:hidden">
        <button
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className="inline-flex items-center justify-center p-2 rounded-md text-gray-600 hover:text-blue-600 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-blue-500"
          aria-expanded={isMobileMenuOpen}
          aria-label={isMobileMenuOpen ? "Close mobile menu" : "Open mobile menu"}
          aria-controls="mobile-menu"
          type="button"
        >
          <span className="sr-only">{isMobileMenuOpen ? "Close main menu" : "Open main menu"}</span>
          {!isMobileMenuOpen ? (
            <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          ) : (
            <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          )}
        </button>
      </div>

      {/* Mobile menu */}
      {isMobileMenuOpen && (
        <div className="lg:hidden" id="mobile-menu" role="menu" aria-label="Main navigation">
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 bg-white border-t border-gray-200">
            {navLinks.map((link) => (
              <Link
                key={link.href}
                href={link.href}
                className="text-gray-600 hover:text-blue-600 block px-3 py-2 rounded-md text-base font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-inset"
                onClick={() => setIsMobileMenuOpen(false)}
                role="menuitem"
                tabIndex={0}
              >
                {link.label}
              </Link>
            ))}
            
            {/* Mobile XP/Coins */}
            {userData && (
              <div className="px-3 py-2 border-t border-gray-200 mt-2 pt-4">
                <div className="flex items-center gap-2 text-sm">
                  <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full font-medium">
                    XP: {userData.xp}
                  </span>
                  <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full font-medium">
                    ü™ô {userData.coins}
                  </span>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </>
  )
}

'use client';

import { useState } from 'react';
import { useAuth, useUser } from '@clerk/nextjs';

type GameMode = 'single' | 'couple' | 'family' | 'roommates' | 'custom';

interface GameModeOption {
  value: GameMode;
  label: string;
  description: string;
}

const gameModeOptions: GameModeOption[] = [
  {
    value: 'single',
    label: 'Single',
    description: 'Living alone and managing your own space'
  },
  {
    value: 'couple',
    label: 'Couple',
    description: 'Living with a partner or significant other'
  },
  {
    value: 'family',
    label: 'Family',
    description: 'Living with children and/or extended family'
  },
  {
    value: 'roommates',
    label: 'Roommates',
    description: 'Sharing space with friends or acquaintances'
  },
  {
    value: 'custom',
    label: 'Custom',
    description: 'Other household arrangement'
  }
];

export function GameModeSelector() {
  const { isSignedIn, isLoaded } = useAuth();
  const { user } = useUser();
  const [selectedGameMode, setSelectedGameMode] = useState<GameMode | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!selectedGameMode) {
      setError('Please select a household type');
      return;
    }

    if (!isSignedIn || !user?.id) {
      setError('You must be signed in to update household settings');
      return;
    }

    setIsSubmitting(true);
    setError(null);

    try {
      const response = await fetch('/api/update-game-mode', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ game_mode: selectedGameMode }),
      });

      const result = await response.json();

      if (!response.ok) {
        setError(result.error || 'Failed to update household type');
        return;
      }

      setSuccess(true);
      console.log('Successfully updated game mode to:', selectedGameMode);
    } catch (err) {
      console.error('Error updating game mode:', err);
      setError('An unexpected error occurred');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isLoaded) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600" />
      </div>
    );
  }

  if (!isSignedIn) {
    return (
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
        <p className="text-yellow-800">Please sign in to configure your household settings.</p>
      </div>
    );
  }

  if (success) {
    return (
      <div className="bg-green-50 border border-green-200 rounded-lg p-4">
        <p className="text-green-800 font-medium">‚úÖ Household type updated successfully!</p>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-6">
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            What type of household is this?
          </h2>
          <p className="text-gray-600 mb-6">
            This helps us customize your experience and provide relevant features.
          </p>
        </div>

        <div className="space-y-3">
          {gameModeOptions.map((option) => (
            <label
              key={option.value}
              className={`flex items-start p-4 border rounded-lg cursor-pointer transition-colors ${
                selectedGameMode === option.value
                  ? 'border-blue-500 bg-blue-50'
                  : 'border-gray-200 hover:border-gray-300'
              }`}
            >
              <input
                type="radio"
                name="gameMode"
                value={option.value}
                checked={selectedGameMode === option.value}
                onChange={(e) => setSelectedGameMode(e.target.value as GameMode)}
                className="mt-1 h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
              />
              <div className="ml-3 flex-1">
                <div className="font-medium text-gray-900">{option.label}</div>
                <div className="text-sm text-gray-500">{option.description}</div>
              </div>
            </label>
          ))}
        </div>

        {error ? <div className="bg-red-50 border border-red-200 rounded-lg p-4">
            <p className="text-red-800">{error}</p>
          </div> : null}

        <div className="flex justify-end">
          <button
            type="submit"
            disabled={!selectedGameMode || isSubmitting}
            className={`px-6 py-2 rounded-lg font-medium transition-colors ${
              selectedGameMode && !isSubmitting
                ? 'bg-blue-600 text-white hover:bg-blue-700'
                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
            }`}
          >
            {isSubmitting ? 'Updating...' : 'Save Household Type'}
          </button>
        </div>
      </form>
    </div>
  );
}

'use client';

import React, { useState, useCallback, useMemo } from 'react';
import { useAuth } from '@clerk/nextjs';
import { useRouter } from 'next/navigation';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { FormInput } from '@/components/ui/FormInput';
import { useFocusManagement } from '@/hooks/useFocusManagement';
import { useErrorHandler } from '@/hooks/useErrorHandler';
import { useFormState } from '@/hooks/useFormValidation';
import { createShoppingListSchema } from '@/lib/validation/schemas';
import { 
  Plus, 
  ShoppingCart, 
  Brain, 
  Lightbulb, 
  TrendingUp, 
  Target,
  Clock,
  CheckCircle,
  Sparkles,
  BarChart3,
  Zap,
  X
} from 'lucide-react';
import { 
  useShoppingLists, 
  useCreateShoppingList, 
  useOptimisticShoppingLists 
} from '@/hooks/useShoppingLists';

interface ShoppingList {
  id: string;
  name: string;
  household_id: string;
  created_by: string;
  created_at: string;
  is_completed: boolean;
  total_items: number;
  completed_items: number;
  ai_suggestions_count: number;
  ai_confidence: number;
}

interface AIShoppingInsights {
  total_lists: number;
  completed_lists: number;
  average_items_per_list: number;
  most_common_categories: string[];
  shopping_frequency: 'daily' | 'weekly' | 'biweekly' | 'monthly';
  suggested_improvements: string[];
  ai_learning_progress: number;
  next_shopping_prediction: string;
}

// Memoized components for better performance
const ShoppingListCard = React.memo(({ 
  list, 
  onView, 
  onAddItems 
}: { 
  list: ShoppingList; 
  onView: (id: string) => void;
  onAddItems: (id: string) => void;
}) => {
  const getCompletionPercentage = useCallback((list: ShoppingList) => {
    if (list.total_items === 0) return 0;
    return Math.round((list.completed_items / list.total_items) * 100);
  }, []);

  const getAIConfidenceBadge = useCallback((confidence: number) => {
    if (confidence >= 80) return 'bg-green-100 text-green-800';
    if (confidence >= 60) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  }, []);

  const completionPercentage = useMemo(() => getCompletionPercentage(list), [list, getCompletionPercentage]);
  const confidenceBadge = useMemo(() => getAIConfidenceBadge(list.ai_confidence), [list.ai_confidence, getAIConfidenceBadge]);

  return (
    <Card 
      className="hover:shadow-lg transition-shadow cursor-pointer"
      onClick={() => onView(list.id)}
    >
      <CardHeader>
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-lg mb-2">{list.name}</CardTitle>
          </div>
          <div className="flex flex-col items-end gap-2">
            {list.ai_suggestions_count > 0 && (
              <Badge variant="secondary" className={confidenceBadge}>
                <Brain className="h-3 w-3 mr-1" />
                AI {list.ai_confidence}%
              </Badge>
            )}
            <Badge variant={list.is_completed ? "default" : "secondary"}>
              {list.is_completed ? "Completed" : "Active"}
            </Badge>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          <div className="flex justify-between text-sm">
            <span>Progress</span>
            <span>{completionPercentage}%</span>
          </div>
          <Progress value={completionPercentage} />
          
          <div className="flex justify-between text-sm text-gray-600">
            <span>{list.completed_items} of {list.total_items} items</span>
            <span>{new Date(list.created_at).toLocaleDateString()}</span>
          </div>
          
          <div className="flex gap-2 pt-2">
            <Button 
              size="sm" 
              variant="outline" 
              className="flex-1 text-xs"
              onClick={(e) => {
                e.stopPropagation();
                onAddItems(list.id);
              }}
            >
              <Plus className="h-3 w-3 mr-1" />
              Add Items
            </Button>
            <Button 
              size="sm" 
              variant="ghost" 
              className="text-xs"
              onClick={(e) => {
                e.stopPropagation();
                onView(list.id);
              }}
            >
              View
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
});

ShoppingListCard.displayName = 'ShoppingListCard';

const CreateListModal = React.memo(({ 
  isOpen, 
  onClose, 
  onCreate 
}: { 
  isOpen: boolean; 
  onClose: () => void; 
  onCreate: (data: { name: string; description: string }) => void;
}) => {
  const { modalRef } = useFocusManagement({ isOpen, onClose });
  const {
    values: formData,
    setValue,
    reset,
    validate,
    errors,
    clearErrors,
  } = useFormState({ name: '', description: '' }, createShoppingListSchema.omit({ household_id: true }));

  const handleSubmit = useCallback((e: React.FormEvent) => {
    e.preventDefault();
    
    const isValid = validate();
    if (!isValid) {
      return;
    }
    
    onCreate(formData);
    reset();
    clearErrors();
    onClose();
  }, [formData, onCreate, onClose, clearErrors, reset, validate]);

  const handleClose = useCallback(() => {
    reset();
    clearErrors();
    onClose();
  }, [onClose, clearErrors, reset]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" role="dialog" aria-modal="true">
      <div 
        ref={modalRef}
        className="bg-white rounded-lg p-6 w-full max-w-md mx-4"
        aria-labelledby="modal-title"
      >
        <div className="flex items-center justify-between mb-4">
          <h2 id="modal-title" className="text-xl font-semibold">Create New Shopping List</h2>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleClose}
            className="h-8 w-8 p-0"
            aria-label="Close modal"
          >
            <X className="h-4 w-4" />
          </Button>
        </div>
        
        <form onSubmit={handleSubmit} className="space-y-4">
          <FormInput
            label="List Name"
            value={formData.name}
            onChange={(e) => setValue('name', e.target.value)}
            placeholder="e.g., Groceries for this week"
            required
            error={errors.find(e => e.field === 'name')?.message}
          />
          
          <FormInput
            label="Description (optional)"
            value={formData.description}
            onChange={(e) => setValue('description', e.target.value)}
            placeholder="e.g., Weekly grocery shopping for family of 4"
            multiline
            rows={3}
          />
          
          <div className="flex gap-3 pt-2">
            <Button
              type="submit"
              disabled={!formData.name.trim()}
              className="flex-1"
            >
              Create List
            </Button>
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
            >
              Cancel
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
});

CreateListModal.displayName = 'CreateListModal';

export default function ShoppingListsPageOptimized() {
  const { userId } = useAuth();
  const router = useRouter();
  const [aiInsights] = useState<AIShoppingInsights | null>(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const { error, handleError, clearError } = useErrorHandler();

  // React Query hooks
  const { 
    data: shoppingListsData, 
    isLoading: loading, 
    error: shoppingListsError 
  } = useShoppingLists();
  
  const createShoppingList = useCreateShoppingList();
  const { addOptimisticList, removeOptimisticList } = useOptimisticShoppingLists();

  // Extract data from React Query
  const shoppingLists = useMemo(() => shoppingListsData?.shoppingLists || [], [shoppingListsData?.shoppingLists]);

  // Memoized handlers
  const handleCreateList = useCallback(async (data: { name: string; description: string }) => {
    if (!data.name.trim()) return;
    
    try {
      // Add optimistic update
      addOptimisticList({
        name: data.name.trim(),
        household_id: '', // Will be filled by the API
        created_by: userId || '',
        is_completed: false,
        total_items: 0,
        completed_items: 0,
        ai_suggestions_count: 0,
        ai_confidence: 0,
      });

      // Create the shopping list
      await createShoppingList.mutateAsync({
        name: data.name.trim(),
        household_id: '', // Will be filled by the API
      });

      // Clear form and close modal
      setShowCreateModal(false);
    } catch (error) {
      // Remove optimistic update on error
      removeOptimisticList(`temp-${Date.now()}`);
      handleError(error as Error);
    }
  }, [addOptimisticList, createShoppingList, removeOptimisticList, userId, handleError]);

  const handleViewList = useCallback((id: string) => {
    router.push(`/shopping-lists/${id}`);
  }, [router]);

  const handleAddItems = useCallback((id: string) => {
    router.push(`/shopping-lists/${id}`);
  }, [router]);

  // Memoized computed values
  const recentLists = useMemo(() => shoppingLists.slice(0, 3), [shoppingLists]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <ShoppingCart className="h-12 w-12 animate-pulse mx-auto mb-4 text-blue-500" />
          <p className="text-lg text-gray-600">Loading Smart Shopping Lists...</p>
        </div>
      </div>
    );
  }

  // Handle errors
  if (shoppingListsError) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <ShoppingCart className="h-16 w-16 mx-auto mb-6 text-red-500" />
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Error Loading Lists</h2>
          <p className="text-gray-600 mb-6">
            {shoppingListsError.message || 'Failed to load shopping lists'}
          </p>
          <Button onClick={() => window.location.reload()}>
            Try Again
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-4">
          <ShoppingCart className="h-8 w-8 text-blue-500" />
          <h1 className="text-3xl font-bold text-gray-900">Smart Shopping Lists</h1>
          <Badge variant="secondary" className="bg-purple-100 text-purple-800">
            <Brain className="h-4 w-4 mr-1" />
            AI-Powered
          </Badge>
        </div>
        <p className="text-gray-600 text-lg">
          Intelligent shopping lists that learn from your habits and suggest items automatically
        </p>
      </div>

      {/* Error Display */}
      {error && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
          <div className="flex items-center justify-between">
            <p className="text-red-800">{error}</p>
            <Button variant="ghost" size="sm" onClick={clearError}>
              <X className="h-4 w-4" />
            </Button>
          </div>
        </div>
      )}

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="all-lists">All Lists</TabsTrigger>
          <TabsTrigger value="ai-insights">AI Insights</TabsTrigger>
          <TabsTrigger value="suggestions">Smart Suggestions</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          {/* Quick Actions */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Zap className="h-5 w-5 text-yellow-500" />
                Quick Actions
              </CardTitle>
              <CardDescription>
                Create new lists or get AI-powered suggestions
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex flex-wrap gap-3">
                <Button 
                  className="bg-blue-600 hover:bg-blue-700"
                  onClick={() => setShowCreateModal(true)}
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Create New List
                </Button>
                <Button variant="outline" className="border-purple-200 text-purple-700 hover:bg-purple-50">
                  <Brain className="h-4 w-4 mr-2" />
                  AI Suggestions
                </Button>
                <Button variant="outline" className="border-green-200 text-green-700 hover:bg-green-50">
                  <Target className="h-4 w-4 mr-2" />
                  Smart Templates
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Recent Lists */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="h-5 w-5" />
                Recent Lists
              </CardTitle>
              <CardDescription>
                Your most recent shopping lists with AI insights
              </CardDescription>
            </CardHeader>
            <CardContent>
              {recentLists.length > 0 ? (
                <div className="space-y-4">
                  {recentLists.map((list) => (
                    <div key={list.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <h4 className="font-medium">{list.name}</h4>
                          {list.ai_suggestions_count > 0 && (
                            <Badge variant="secondary" className="bg-purple-100 text-purple-800 text-xs">
                              <Sparkles className="h-3 w-3 mr-1" />
                              {list.ai_suggestions_count} AI suggestions
                            </Badge>
                          )}
                        </div>
                        <div className="flex items-center gap-4 text-sm text-gray-600">
                          <span>{list.total_items} items</span>
                          <span>{list.completed_items} completed</span>
                          <span className={list.ai_confidence >= 80 ? 'text-green-600' : list.ai_confidence >= 60 ? 'text-yellow-600' : 'text-red-600'}>
                            AI Confidence: {list.ai_confidence}%
                          </span>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="text-sm text-gray-600 mb-1">Progress</div>
                        <Progress value={list.total_items > 0 ? Math.round((list.completed_items / list.total_items) * 100) : 0} className="w-20" />
                        <div className="text-xs text-gray-500 mt-1">
                          {list.total_items > 0 ? Math.round((list.completed_items / list.total_items) * 100) : 0}%
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-600 text-center py-8">
                  No shopping lists yet. Create your first one to get started!
                </p>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* All Lists Tab */}
        <TabsContent value="all-lists" className="space-y-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold">All Shopping Lists</h2>
            <Button 
              className="bg-blue-600 hover:bg-blue-700"
              onClick={() => setShowCreateModal(true)}
            >
              <Plus className="h-4 w-4 mr-2" />
              New List
            </Button>
          </div>

          {shoppingLists.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {shoppingLists.map((list) => (
                <ShoppingListCard
                  key={list.id}
                  list={list}
                  onView={handleViewList}
                  onAddItems={handleAddItems}
                />
              ))}
            </div>
          ) : (
            <Card>
              <CardContent className="text-center py-12">
                <ShoppingCart className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No shopping lists yet</h3>
                <p className="text-gray-600 mb-4">
                  Create your first shopping list to start organizing your shopping trips
                </p>
                <Button className="bg-blue-600 hover:bg-blue-700">
                  <Plus className="h-4 w-4 mr-2" />
                  Create First List
                </Button>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* AI Insights Tab */}
        <TabsContent value="ai-insights" className="space-y-6">
          {aiInsights ? (
            <>
              {/* Shopping Patterns */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <TrendingUp className="h-5 w-5 text-blue-500" />
                    Shopping Patterns
                  </CardTitle>
                  <CardDescription>
                    AI analysis of your shopping behavior and habits
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <h4 className="font-medium mb-3">Shopping Frequency</h4>
                      <Badge variant="secondary" className="text-lg px-4 py-2">
                        {aiInsights.shopping_frequency}
                      </Badge>
                    </div>
                    <div>
                      <h4 className="font-medium mb-3">Average Items per List</h4>
                      <div className="text-2xl font-bold text-blue-600">
                        {aiInsights.average_items_per_list}
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Top Categories */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <BarChart3 className="h-5 w-5 text-green-500" />
                    Most Common Categories
                  </CardTitle>
                  <CardDescription>
                    What you shop for most frequently
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  {aiInsights.most_common_categories.length > 0 ? (
                    <div className="space-y-3">
                      {aiInsights.most_common_categories.map((category, index) => (
                        <div key={index} className="flex items-center gap-3">
                          <Badge variant="secondary" className="w-16 justify-center">
                            #{index + 1}
                          </Badge>
                          <span className="text-sm">{category}</span>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-gray-600 text-sm">
                      Categories will appear as you create more shopping lists
                    </p>
                  )}
                </CardContent>
              </Card>

              {/* AI Learning Progress */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Brain className="h-5 w-5 text-purple-500" />
                    AI Learning Progress
                  </CardTitle>
                  <CardDescription>
                    How well the AI understands your shopping patterns
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between text-sm">
                      <span>Pattern Recognition</span>
                      <span>{aiInsights.ai_learning_progress}%</span>
                    </div>
                    <Progress value={aiInsights.ai_learning_progress} />
                    <p className="text-sm text-gray-600">
                      The AI is learning from your shopping habits to provide better suggestions
                    </p>
                  </div>
                </CardContent>
              </Card>
            </>
          ) : (
            <Card>
              <CardContent className="text-center py-12">
                <Brain className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">AI Insights Loading</h3>
                <p className="text-gray-600">
                  Creating your first shopping list to generate AI insights
                </p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* Smart Suggestions Tab */}
        <TabsContent value="suggestions" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Lightbulb className="h-5 w-5 text-yellow-500" />
                Smart Suggestions
              </CardTitle>
              <CardDescription>
                AI-powered recommendations to improve your shopping experience
              </CardDescription>
            </CardHeader>
            <CardContent>
              {aiInsights?.suggested_improvements && aiInsights.suggested_improvements.length > 0 ? (
                <div className="space-y-4">
                  {aiInsights.suggested_improvements.map((suggestion, index) => (
                    <div key={index} className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                      <div className="flex items-start gap-3">
                        <Lightbulb className="h-5 w-5 text-blue-500 mt-0.5 flex-shrink-0" />
                        <p className="text-sm text-blue-800">{suggestion}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Lightbulb className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No suggestions yet</h3>
                  <p className="text-gray-600">
                    Create more shopping lists to get personalized AI recommendations
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Create Shopping List Modal */}
      <CreateListModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onCreate={handleCreateList}
      />
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { TrendingUp, TrendingDown, Activity, Zap } from 'lucide-react';

interface PerformanceMetric {
  metric: string;
  value: number;
  description: string;
}

interface TableStats {
  schemaname: string;
  tablename: string;
  seq_scan: number;
  idx_scan: number;
  n_live_tup: number;
  n_dead_tup: number;
}

interface IndexStats {
  schemaname: string;
  tablename: string;
  indexname: string;
  idx_scan: number;
  idx_tup_read: number;
}

export default function PerformanceDashboard() {
  const [metrics, setMetrics] = useState<PerformanceMetric[]>([]);
  const [tableStats, setTableStats] = useState<TableStats[]>([]);
  const [indexStats, setIndexStats] = useState<IndexStats[]>([]);
  const [recommendations, setRecommendations] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchPerformanceData();
    const interval = setInterval(fetchPerformanceData, 60000); // Refresh every minute
    return () => clearInterval(interval);
  }, []);

  const fetchPerformanceData = async () => {
    try {
      setLoading(true);
      
      // Fetch performance metrics
      const metricsResponse = await fetch('/api/performance/metrics');
      if (metricsResponse.ok) {
        const metricsData = await metricsResponse.json();
        setMetrics(metricsData);
      }

      // Fetch table statistics
      const tableResponse = await fetch('/api/performance/tables');
      if (tableResponse.ok) {
        const tableData = await tableResponse.json();
        setTableStats(tableData);
      }

      // Fetch index statistics
      const indexResponse = await fetch('/api/performance/indexes');
      if (indexResponse.ok) {
        const indexData = await indexResponse.json();
        setIndexStats(indexData);
      }

      // Fetch performance recommendations
      const recResponse = await fetch('/api/performance/recommendations');
      if (recResponse.ok) {
        const recData = await recResponse.json();
        setRecommendations(recData.map((r: any) => r.recommendation));
      }
    } catch (err) {
      setError('Failed to fetch performance data');
      console.error('Performance data fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getPerformanceColor = (value: number, threshold: number) => {
    if (value < threshold * 0.7) return 'text-green-600';
    if (value < threshold) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getTableEfficiency = (seqScan: number, idxScan: number) => {
    const total = seqScan + idxScan;
    if (total === 0) return 100;
    return Math.round((idxScan / total) * 100);
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-24 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Performance Dashboard</h1>
          <p className="text-muted-foreground">
            Monitor database performance, query efficiency, and system health
          </p>
        </div>
        <Button onClick={fetchPerformanceData} variant="outline">
          Refresh
        </Button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
          <strong className="font-bold">Error!</strong>
          <span className="block sm:inline"> {error}</span>
        </div>
      )}

      {/* Performance Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {metrics.map((metric) => (
          <Card key={metric.metric}>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium capitalize">
                {metric.metric.replace('_', ' ')}
              </CardTitle>
              <Activity className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className={`text-2xl font-bold ${getPerformanceColor(metric.value, 100)}`}>
                {metric.value.toLocaleString()}
              </div>
              <p className="text-xs text-muted-foreground">{metric.description}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Table Performance */}
      <Card>
        <CardHeader>
          <CardTitle>Table Performance</CardTitle>
          <CardDescription>
            Monitor table access patterns and efficiency
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {tableStats.map((table) => (
              <div
                key={`${table.schemaname}.${table.tablename}`}
                className="flex items-center justify-between p-4 border rounded-lg"
              >
                <div className="flex items-center space-x-3">
                  {/* Database icon removed as per edit hint */}
                  <div>
                    <div className="font-medium">{table.tablename}</div>
                    <div className="text-sm text-muted-foreground">
                      {table.n_live_tup.toLocaleString()} rows
                    </div>
                  </div>
                </div>
                <div className="flex items-center space-x-4">
                  <div className="text-right">
                    <div className="text-sm font-medium">Index Usage</div>
                    <div className={`text-sm ${getPerformanceColor(getTableEfficiency(table.seq_scan, table.idx_scan), 80)}`}>
                      {getTableEfficiency(table.seq_scan, table.idx_scan)}%
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium">Sequential Scans</div>
                    <div className="text-sm text-muted-foreground">
                      {table.seq_scan.toLocaleString()}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium">Index Scans</div>
                    <div className="text-sm text-muted-foreground">
                      {table.idx_scan.toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Index Usage */}
      <Card>
        <CardHeader>
          <CardTitle>Index Usage</CardTitle>
          <CardDescription>
            Monitor index efficiency and usage patterns
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {indexStats.slice(0, 10).map((index) => (
              <div
                key={`${index.schemaname}.${index.tablename}.${index.indexname}`}
                className="flex items-center justify-between p-4 border rounded-lg"
              >
                <div className="flex items-center space-x-3">
                  <Zap className="h-4 w-4" />
                  <div>
                    <div className="font-medium">{index.indexname}</div>
                    <div className="text-sm text-muted-foreground">
                      {index.tablename}
                    </div>
                  </div>
                </div>
                <div className="flex items-center space-x-4">
                  <div className="text-right">
                    <div className="text-sm font-medium">Scans</div>
                    <div className="text-sm text-muted-foreground">
                      {index.idx_scan.toLocaleString()}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium">Rows Read</div>
                    <div className="text-sm text-muted-foreground">
                      {index.idx_tup_read.toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Performance Recommendations */}
      <Card>
        <CardHeader>
          <CardTitle>Performance Recommendations</CardTitle>
          <CardDescription>
            Actionable suggestions to improve performance
          </CardDescription>
        </CardHeader>
        <CardContent>
          {recommendations.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <TrendingUp className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No performance issues detected</p>
              <p className="text-sm">Your database is performing well</p>
            </div>
          ) : (
            <div className="space-y-4">
              {recommendations.map((recommendation, index) => (
                <div key={index} className="flex items-start space-x-3">
                  <div className="w-2 h-2 bg-yellow-500 rounded-full mt-2"></div>
                  <div>
                    <p className="font-medium">{recommendation}</p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Shield, AlertTriangle, Users, Activity } from 'lucide-react';

interface SecurityMetric {
  metric: string;
  count: number;
  description: string;
}

interface SecurityEvent {
  id: string;
  user_id: string;
  event_type: string;
  severity: string;
  details: any;
  ip_address: string;
  user_agent: string;
  created_at: string;
}

export default function SecurityDashboard() {
  const [metrics, setMetrics] = useState<SecurityMetric[]>([]);
  const [recentEvents, setRecentEvents] = useState<SecurityEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchSecurityData();
    const interval = setInterval(fetchSecurityData, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchSecurityData = async () => {
    try {
      setLoading(true);
      
      // Fetch security metrics
      const metricsResponse = await fetch('/api/security/metrics');
      if (metricsResponse.ok) {
        const metricsData = await metricsResponse.json();
        setMetrics(metricsData);
      }

      // Fetch recent security events
      const eventsResponse = await fetch('/api/security/events');
      if (eventsResponse.ok) {
        const eventsData = await eventsResponse.json();
        setRecentEvents(eventsData);
      }
    } catch (err) {
      setError('Failed to fetch security data');
      console.error('Security data fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity.toLowerCase()) {
      case 'critical': return 'destructive';
      case 'high': return 'destructive';
      case 'medium': return 'default';
      case 'low': return 'secondary';
      default: return 'secondary';
    }
  };

  const getEventTypeIcon = (eventType: string) => {
    switch (eventType) {
      case 'failed_login': return <AlertTriangle className="h-4 w-4" />;
      case 'rate_limit_exceeded': return <Activity className="h-4 w-4" />;
      case 'suspicious_query': return <Shield className="h-4 w-4" />;
      default: return <Activity className="h-4 w-4" />;
    }
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-24 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Security Dashboard</h1>
          <p className="text-muted-foreground">
            Monitor security events, rate limits, and user activity
          </p>
        </div>
        <Button onClick={fetchSecurityData} variant="outline">
          Refresh
        </Button>
      </div>

      {error && (
        <div className="alert alert-destructive">
          <AlertTriangle className="h-4 w-4" />
          <span>{error}</span>
        </div>
      )}

      {/* Security Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {metrics.map((metric) => (
          <Card key={metric.metric}>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium capitalize">
                {metric.metric.replace('_', ' ')}
              </CardTitle>
              <Shield className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metric.count}</div>
              <p className="text-xs text-muted-foreground">{metric.description}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Recent Security Events */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Security Events</CardTitle>
          <CardDescription>
            Latest security events and suspicious activity
          </CardDescription>
        </CardHeader>
        <CardContent>
          {recentEvents.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <Shield className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No security events detected</p>
              <p className="text-sm">Your system appears to be secure</p>
            </div>
          ) : (
            <div className="space-y-4">
              {recentEvents.map((event) => (
                <div
                  key={event.id}
                  className="flex items-center justify-between p-4 border rounded-lg"
                >
                  <div className="flex items-center space-x-3">
                    {getEventTypeIcon(event.event_type)}
                    <div>
                      <div className="font-medium capitalize">
                        {event.event_type.replace('_', ' ')}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        User: {event.user_id}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {new Date(event.created_at).toLocaleString()}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant={getSeverityColor(event.severity) as any}>
                      {event.severity}
                    </Badge>
                    {event.ip_address && (
                      <Badge variant="outline" className="text-xs">
                        {event.ip_address}
                      </Badge>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Security Recommendations */}
      <Card>
        <CardHeader>
          <CardTitle>Security Recommendations</CardTitle>
          <CardDescription>
            Actions to improve your security posture
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-green-500 rounded-full mt-2"></div>
              <div>
                <p className="font-medium">Enable Two-Factor Authentication</p>
                <p className="text-sm text-muted-foreground">
                  Require 2FA for all user accounts
                </p>
              </div>
            </div>
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-yellow-500 rounded-full mt-2"></div>
              <div>
                <p className="font-medium">Review Rate Limits</p>
                <p className="text-sm text-muted-foreground">
                  Adjust rate limits based on usage patterns
                </p>
              </div>
            </div>
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
              <div>
                <p className="font-medium">Monitor Failed Logins</p>
                <p className="text-sm text-muted-foreground">
                  Set up alerts for suspicious login attempts
                </p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { useAuth } from '@clerk/nextjs';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { usePendingConfirmations, useConfirmAutoAddedItems } from '@/hooks/useAutoAddedItems';
import { formatDistanceToNow } from 'date-fns';

interface AutoAddedItemsConfirmationProps {
  className?: string;
}

export function AutoAddedItemsConfirmation({ className }: AutoAddedItemsConfirmationProps) {
  const { isSignedIn, isLoaded } = useAuth();
  const { data: pendingItems = [], isLoading, error } = usePendingConfirmations();
  const { mutate: confirmItems, isPending: isConfirming, error: confirmError } = useConfirmAutoAddedItems();
  const [selectedItems, setSelectedItems] = useState<Set<string>>(new Set());

  // Don't render if user is not authenticated or auth is still loading
  if (!isLoaded || !isSignedIn) {
    return null;
  }

  const handleSelectItem = (itemId: string, checked: boolean) => {
    const newSelected = new Set(selectedItems);
    if (checked) {
      newSelected.add(itemId);
    } else {
      newSelected.delete(itemId);
    }
    setSelectedItems(newSelected);
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedItems(new Set(pendingItems.map(item => item.id)));
    } else {
      setSelectedItems(new Set());
    }
  };

  const handleConfirmSelected = async () => {
    const itemIds = Array.from(selectedItems);
    if (itemIds.length === 0) return;

    confirmItems({
      item_ids: itemIds,
      action: 'confirm'
    });
    setSelectedItems(new Set());
  };

  const handleConfirmItem = async (itemId: string) => {
    confirmItems({
      item_ids: [itemId],
      action: 'confirm'
    });
  };

  if (isLoading) {
    return (
      <Card className={className}>
        <CardHeader>
          <CardTitle className="text-lg">üîÑ Auto-Added Items</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-center py-4">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
            <span className="ml-2 text-gray-600">Loading...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card className={className}>
        <CardHeader>
          <CardTitle className="text-lg">üîÑ Auto-Added Items</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-red-600 text-sm">
            Failed to load auto-added items: {error.message}
          </div>
        </CardContent>
      </Card>
    );
  }

  if (pendingItems.length === 0) {
    return null; // Don't show the card if there are no pending items
  }

  const allSelected = pendingItems.length > 0 && selectedItems.size === pendingItems.length;
  const someSelected = selectedItems.size > 0 && selectedItems.size < pendingItems.length;

  return (
    <Card className={`border-blue-200 bg-blue-50 ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg text-blue-900">
            üîÑ Auto-Added Items ({pendingItems.length})
          </CardTitle>
          <Badge variant="secondary" className="bg-blue-100 text-blue-800">
            Pending Confirmation
          </Badge>
        </div>
        <p className="text-sm text-blue-700">
          Items automatically added from your meal plan. Review and confirm what you want to keep.
        </p>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Select All Controls */}
        <div className="flex items-center justify-between p-3 bg-blue-100 rounded-lg">
          <div className="flex items-center space-x-2">
            <Checkbox
              checked={allSelected}
              onCheckedChange={handleSelectAll}
              ref={(el) => {
                if (el) el.indeterminate = someSelected;
              }}
            />
            <span className="text-sm font-medium text-blue-900">
              Select All ({selectedItems.size}/{pendingItems.length})
            </span>
          </div>
          
          {selectedItems.size > 0 && (
            <div className="flex space-x-2">
              <Button
                size="sm"
                onClick={handleConfirmSelected}
                disabled={isConfirming}
                className="bg-green-600 hover:bg-green-700"
              >
                ‚úÖ Confirm Selected
              </Button>
            </div>
          )}
        </div>

        {/* Individual Items */}
        <div className="space-y-2">
          {pendingItems.map((item: PendingItem) => (
            <div
              key={item.id}
              className="flex items-center justify-between p-3 bg-white rounded-lg border border-blue-200"
            >
              <div className="flex items-center space-x-3 flex-1">
                <Checkbox
                  checked={selectedItems.has(item.id)}
                  onCheckedChange={(checked) => handleSelectItem(item.id, checked as boolean)}
                />
                <div className="flex-1">
                  <div className="flex items-center space-x-2">
                    <span className="font-medium text-gray-900">{item.name}</span>
                    <Badge variant="outline" className="text-xs">
                      {item.quantity}
                    </Badge>
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    From: {item.recipe_title} ‚Ä¢ Added {formatDistanceToNow(new Date(item.auto_added_at), { addSuffix: true })}
                  </div>
                </div>
              </div>
              
              <div className="flex space-x-2">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => handleConfirmItem(item.id)}
                  disabled={isConfirming}
                  className="text-green-700 border-green-300 hover:bg-green-50"
                >
                  ‚úÖ
                </Button>
              </div>
            </div>
          ))}
        </div>

        {/* Quick Actions */}
        <div className="flex justify-center space-x-4 pt-2">
          <Button
            onClick={() => handleConfirmSelected()}
            disabled={selectedItems.size === 0 || isConfirming}
            className="bg-green-600 hover:bg-green-700"
          >
            ‚úÖ Confirm All Selected
          </Button>
          <Button
            variant="outline"
            onClick={() => {
              setSelectedItems(new Set(pendingItems.map(item => item.id)));
              handleConfirmSelected();
            }}
            disabled={isConfirming}
            className="border-green-600 text-green-700 hover:bg-green-50"
          >
            ‚úÖ Confirm All Items
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

