import React from 'react';
import { cn } from '@/lib/utils';

interface CheckboxProps extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'onChange'> {
  label?: string;
  description?: string;
  error?: string;
  size?: 'sm' | 'md' | 'lg';
  onCheckedChange?: (checked: boolean) => void;
  onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
}

export function Checkbox({ 
  className = '', 
  label,
  description,
  error,
  size = 'md',
  id,
  onCheckedChange,
  onChange,
  ...props 
}: CheckboxProps) {
  const checkboxId = id || `checkbox-${Math.random().toString(36).substr(2, 9)}`;
  
  const sizeClasses = {
    sm: 'h-3 w-3',
    md: 'h-4 w-4',
    lg: 'h-5 w-5'
  };
  
  const labelSizeClasses = {
    sm: 'text-xs',
    md: 'text-sm',
    lg: 'text-base'
  };
  
  const classes = cn(
    'rounded border border-input bg-background text-primary shadow-sm transition-colors',
    'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2',
    'disabled:cursor-not-allowed disabled:opacity-50',
    'data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground',
    sizeClasses[size],
    className
  );

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    // Call the standard onChange if provided
    if (onChange) {
      onChange(event);
    }
    
    // Call the onCheckedChange if provided
    if (onCheckedChange) {
      onCheckedChange(event.target.checked);
    }
  };
  
  return (
    <div className="flex items-start space-x-2">
      <div className="flex items-center h-5">
        <input
          type="checkbox"
          id={checkboxId}
          className={classes}
          onChange={handleChange}
          {...props}
        />
      </div>
      {(label || description) && (
        <div className="flex flex-col space-y-1">
          {label && (
            <label 
              htmlFor={checkboxId}
              className={cn(
                'font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70',
                labelSizeClasses[size]
              )}
            >
              {label}
            </label>
          )}
          {description && (
            <p className={cn(
              'text-muted-foreground',
              size === 'sm' ? 'text-xs' : 'text-sm'
            )}>
              {description}
            </p>
          )}
          {error && (
            <p className="text-sm text-destructive">
              {error}
            </p>
          )}
        </div>
      )}
    </div>
  );
}

// Export a simple checkbox without label for cases where you just need the input
export function CheckboxInput({ 
  className = '', 
  size = 'md',
  onCheckedChange,
  onChange,
  ...props 
}: Omit<CheckboxProps, 'label' | 'description' | 'error'>) {
  const sizeClasses = {
    sm: 'h-3 w-3',
    md: 'h-4 w-4',
    lg: 'h-5 w-5'
  };
  
  const classes = cn(
    'rounded border border-input bg-background text-primary shadow-sm transition-colors',
    'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2',
    'disabled:cursor-not-allowed disabled:opacity-50',
    'data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground',
    sizeClasses[size],
    className
  );

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    // Call the standard onChange if provided
    if (onChange) {
      onChange(event);
    }
    
    // Call the onCheckedChange if provided
    if (onCheckedChange) {
      onCheckedChange(event.target.checked);
    }
  };
  
  return (
    <input
      type="checkbox"
      className={classes}
      onChange={handleChange}
      {...props}
    />
  );
}

import { clerkMiddleware } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";
import { getUserHouseholdId, getUserOnboardingStatus } from "@/lib/api/database";

export default clerkMiddleware(async (auth, req) => {
  const url = new URL(req.url);
  
  // Create response to add security headers
  const response = NextResponse.next();
  
  // Add comprehensive security headers
  response.headers.set('X-Frame-Options', 'DENY');
  response.headers.set('X-Content-Type-Options', 'nosniff');
  response.headers.set('Referrer-Policy', 'strict-origin-when-cross-origin');
  response.headers.set('X-XSS-Protection', '1; mode=block');
  response.headers.set('Permissions-Policy', 'camera=(), microphone=(), geolocation=()');
  
  // Content Security Policy - Re-enabled with proper Clerk domains
  response.headers.set('Content-Security-Policy',
    "default-src 'self'; " +
    "script-src 'self' 'unsafe-inline' 'unsafe-eval' https://*.clerk.com https://*.clerk.accounts.dev https://va.vercel-scripts.com; " +
    "worker-src 'self' blob:; " +
    "style-src 'self' 'unsafe-inline' https://fonts.googleapis.com; " +
    "img-src 'self' data: https: blob:; " +
    "font-src 'self' https://fonts.gstatic.com data:; " +
    "connect-src 'self' https://*.clerk.com https://*.clerk.accounts.dev https://*.supabase.co https://*.vercel.com https://va.vercel-analytics.com; " +
    "frame-src 'self' https://*.clerk.com https://*.clerk.accounts.dev; " +
    "object-src 'none'; " +
    "base-uri 'self'; " +
    "form-action 'self'"
  );
  
  // Add HSTS header for all requests (will be ignored on HTTP)
  response.headers.set('Strict-Transport-Security', 'max-age=31536000; includeSubDomains; preload');
  
  // Get user authentication info - this is crucial for Clerk to work
  const { userId } = await auth();
  
  // Skip onboarding check for specific routes
  const staticAssetPattern = /\.(?:svg|png|jpg|jpeg|gif|webp|ico|css|js|txt|xml|json|map|woff|woff2|ttf|otf)$/i;
  const skipOnboardingCheck = 
    url.pathname.startsWith('/dev/') ||
    url.pathname === '/onboarding' ||
    url.pathname.startsWith('/onboarding/') ||
    url.pathname.startsWith('/sign-in') ||
    url.pathname.startsWith('/sign-up') ||
    url.pathname.startsWith('/_next/') ||
    url.pathname === '/favicon.ico' ||
    staticAssetPattern.test(url.pathname) ||
    url.pathname === '/' ||
    url.pathname.startsWith('/user-button') ||
    url.pathname.startsWith('/fonts/');

  // Protected routes that require authentication
  const protectedRoutes = [
    '/dashboard',
    '/shopping-lists',
    '/chores',
    '/bills',
    '/meal-planner',
    '/recipes',
    '/rewards',
    '/calendar',
    '/inbox'
  ];

  // Check if current path is protected
  const isProtectedRoute = protectedRoutes.some(route => 
    url.pathname === route || url.pathname.startsWith(route + '/')
  );

  // Skip API routes from redirect logic - let them handle auth themselves
  if (url.pathname.startsWith('/api/')) {
    return response;
  }

  // Redirect to sign-in if accessing protected route without authentication
  if (isProtectedRoute && !userId) {
    const signInUrl = new URL('/sign-in', req.url);
    signInUrl.searchParams.set('redirect_url', req.url);
    return NextResponse.redirect(signInUrl);
  }
  
  // Check onboarding status for signed-in users on app routes (but NOT API routes)
  if (userId && !skipOnboardingCheck) {
    try {
      const [hasHousehold, hasOnboarded] = await Promise.all([
        getUserHouseholdId(userId),
        getUserOnboardingStatus(userId)
      ]);

      if ((!hasHousehold || !hasOnboarded) && !url.pathname.startsWith('/onboarding')) {
        const onboardingUrl = new URL('/onboarding', req.url);
        onboardingUrl.searchParams.set('redirect', url.pathname);
        return NextResponse.redirect(onboardingUrl);
      }
    } catch (error) {
      console.error('Error in onboarding check middleware:', error);
    }
  }
  
  return response;
});

export const config = {
  matcher: [
    /*
     * Match all request paths except for the ones starting with:
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public folder
     */
    "/((?!_next/static|_next/image|favicon.ico|.*\\.(?:svg|png|jpg|jpeg|gif|webp)$).*)",
    "/",
    "/(api|trpc)(.*)"
  ],
};

import { describe, it, expect, vi, beforeEach } from 'vitest'

// Mock the Notification API
const mockNotification = {
  permission: 'default' as NotificationPermission,
  requestPermission: vi.fn(() => Promise.resolve('granted' as NotificationPermission))
}

// Mock navigator and window objects for push notifications
const mockNavigator = {
  serviceWorker: {
    ready: Promise.resolve({
      pushManager: {
        subscribe: vi.fn(() => Promise.resolve({
          endpoint: 'https://fcm.googleapis.com/fcm/send/test-endpoint',
          keys: {
            p256dh: 'test-p256dh-key',
            auth: 'test-auth-key'
          }
        }))
      }
    }),
    addEventListener: vi.fn(),
    getRegistration: vi.fn(() => Promise.resolve({
      pushManager: {
        getSubscription: vi.fn(() => Promise.resolve(null))
      }
    }))
  }
}

// Mock global objects
Object.defineProperty(globalThis, 'Notification', {
  value: mockNotification,
  writable: true
})

Object.defineProperty(globalThis, 'navigator', {
  value: mockNavigator,
  writable: true
})

describe('Push Notifications', () => {
  beforeEach(() => {
    vi.clearAllMocks()
  })

  describe('Notification Permission', () => {
    it('should check notification permission status', () => {
      expect(mockNotification.permission).toBe('default')
    })

