      })

      expect(subscription.endpoint).toBe('https://fcm.googleapis.com/fcm/send/test-endpoint')
      expect(subscription.keys.p256dh).toBe('test-p256dh-key')
      expect(subscription.keys.auth).toBe('test-auth-key')
