      // Validate subscription structure
      expect(subscriptionData.endpoint).toMatch(/^https:\/\//)
      expect(subscriptionData.keys.p256dh).toBeTruthy()
      expect(subscriptionData.keys.auth).toBeTruthy()
    })
  })

